INSERT INTO `posts` (`id`, `title`, `description`, `contents`, `thumbnail`, `user_id`, `category_id`, `status`, `view`, `rate`, `created_at`, `updated_at`) VALUES
(1, 'Giải ngố về Java - ngòi nổ cho cuộc chiến 9 tỷ USD giữa Google và Oracle', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Năm 2010, Oracle đ&atilde; khởi động một cuộc chiến ph&aacute;p l&yacute; khổng lồ nhắm v&agrave;o Google. Trong diễn tiến mới nhất, v&agrave;o tuần n&agrave;y Oracle y&ecirc;u cầu Google phải bồi thường khoản tiền l&ecirc;n tới 9 tỷ USD...</span></p>\r\n', '<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">D&ugrave; v&ocirc; t&igrave;nh hay hữu &yacute; th&igrave; Sun cũng đ&atilde; giăng sẵn một c&aacute;i bẫy khi tuy&ecirc;n bố Java l&agrave; ng&ocirc;n ngữ mở v&agrave; miễn ph&iacute;. Google bước ch&acirc;n v&agrave;o c&aacute;i bẫy đ&oacute; v&agrave; hưởng trọn tr&aacute;i ngọt từ Android. Đến khi Sun về tay Oracle th&igrave; c&aacute;i bẫy tai qu&aacute;i n&agrave;y mới hiện h&igrave;nh.</span></h2>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://genk.vn/kham-pha/giai-ngo-ve-api-vi-sao-noi-api-co-y-nghia-song-con-voi-ca-the-gioi-dien-toan-20160403144419703.chn\" title=\"Giải ngố về API: Vì sao nói API có ý nghĩa sống còn với cả thế giới điện toán?\">Giải ngố về API: V&igrave; sao n&oacute;i API c&oacute; &yacute; nghĩa sống c&ograve;n với cả thế giới điện to&aacute;n?</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://genk.vn/internet/chatbot-la-gi-vi-sao-noi-chatbot-se-giup-microsoft-danh-bai-google-va-facebook-20160402115226123.chn\" title=\"Giải ngố về thứ vũ khí đáng sợ sẽ giúp Microsoft đánh bại cả Google và Facebook\">Giải ngố về thứ vũ kh&iacute; đ&aacute;ng sợ sẽ gi&uacute;p Microsoft đ&aacute;nh bại cả Google v&agrave; Facebook</a></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Năm 2010, Oracle đ&atilde; khởi động một cuộc chiến ph&aacute;p l&yacute; khổng lồ nhắm v&agrave;o Google. Trong diễn tiến mới nhất, v&agrave;o tuần n&agrave;y Oracle y&ecirc;u cầu Google phải bồi thường khoản tiền l&ecirc;n tới 9 tỷ USD.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trọng t&acirc;m của vụ kiện n&agrave;y l&agrave; những c&aacute;o buộc của Oracle rằng Google đ&atilde; sử dụng tr&aacute;i ph&eacute;p nhiều phần của c&ocirc;ng nghệ Java tr&ecirc;n hệ điều h&agrave;nh Android m&agrave; kh&ocirc;ng trả ph&iacute; bản quyền cho Oracle.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Về phần m&igrave;nh, Google khẳng định c&aacute;ch h&atilde;ng n&agrave;y sử dụng Java nằm trong giới hạn &ldquo;sử dụng c&ocirc;ng bằng&rdquo; (&ldquo;fair use&rdquo;, kh&aacute;i niệm ph&aacute;p l&yacute; cho ph&eacute;p sử dụng c&oacute; giới hạn c&aacute;c nội dung tr&iacute; tuệ c&oacute; bản quyền m&agrave; kh&ocirc;ng cần phải xin ph&eacute;p chủ sở hữu). Ngo&agrave;i ra, Google cũng cho rằng Oracle kh&ocirc;ng thể đăng k&yacute; quyền sở hữu c&aacute;c th&agrave;nh phần Java được h&atilde;ng n&agrave;y sử dụng cho Android.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu kết th&uacute;c với phần thắng nghi&ecirc;ng về Oracle, vụ kiện n&agrave;y chắc chắn sẽ l&agrave;m chấn động thế giới phần mềm: x&eacute;t về mặt ph&aacute;p l&yacute;, qu&aacute; tr&igrave;nh x&acirc;y dựng c&aacute;c phần mềm c&oacute; khả năng tương th&iacute;ch với nhau sẽ trở n&ecirc;n kh&oacute; khăn hơn rất nhiều.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://genk.mediacdn.vn/k:2016/android-java-copy-1463158855232/giai-ngo-ve-ava-ngoi-no-cho-cuoc-chien-9-ty-usd-giua-google-va-oracle.jpg\" target=\"_blank\" title=\"\"><img alt=\"\" id=\"img_804e2840-192c-11e6-b3d1-11e3aadf807b\" src=\"https://genk.mediacdn.vn/k:2016/android-java-copy-1463158855232/giai-ngo-ve-ava-ngoi-no-cho-cuoc-chien-9-ty-usd-giua-google-va-oracle.jpg\" /></a></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Vậy, Java l&agrave; g&igrave;? Tại sao Google lại sử dụng Java? V&agrave; Oracle d&ugrave;ng căn cứ n&agrave;o để đ&ograve;i Google phải trả những khoản ph&iacute; khổng lồ?</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Sơ lược về Java</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">N&oacute;i ngắn gọn, Java l&agrave; một trong những ng&ocirc;n ngữ lập tr&igrave;nh phổ biến nhất thế giới. Amazon, Google, Netflix, PayPal v&agrave; v&ocirc; số c&aacute;c tập đo&agrave;n kh&aacute;c sử dụng Java để x&acirc;y dựng hệ thống web v&agrave; phần mềm m&aacute;y chủ.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Java ra đời v&agrave;o năm 1991 tại Sun Microsystems, một trong những g&atilde; khổng lồ đầu ti&ecirc;n của Thung lũng Silicon. Dự &aacute;n Java được khởi động bởi huyền thoại c&ocirc;ng nghệ James Gosling với cơ sở l&agrave; C++ (một ng&ocirc;n ngữ vẫn c&ograve;n rất phổ biến v&agrave;o thời điểm đ&oacute;), nhưng với tầm nh&igrave;n hướng về ph&iacute;a tương lai.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Điểm đặc biệt của Java l&agrave; khả năng chạy tr&ecirc;n bất cứ một nền tảng n&agrave;o, từ Windows đến Mac, Linux v&agrave; sau n&agrave;y l&agrave; cả c&aacute;c thiết bị di động. Để c&oacute; được thế mạnh n&agrave;y, Java phải chạy tr&ecirc;n một lớp m&atilde; trung gian (bytecode) thay v&igrave; chạy trực tiếp tr&ecirc;n hệ điều h&agrave;nh, kết quả l&agrave; hiệu năng ứng dụng thường k&eacute;m hơn so với c&aacute;c ng&ocirc;n ngữ kh&aacute;c.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://genk.mediacdn.vn/k:2016/james-gosling-2008-1-copy-1463158855242/giai-ngo-ve-ava-ngoi-no-cho-cuoc-chien-9-ty-usd-giua-google-va-oracle.jpg\" target=\"_blank\" title=\"Cha đẻ của Java, James Gosling.\"><img alt=\"Cha đẻ của Java, James Gosling.\" id=\"img_7f559770-192c-11e6-b3d1-11e3aadf807b\" src=\"https://genk.mediacdn.vn/k:2016/james-gosling-2008-1-copy-1463158855242/giai-ngo-ve-ava-ngoi-no-cho-cuoc-chien-9-ty-usd-giua-google-va-oracle.jpg\" /></a></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cha đẻ của Java, James Gosling.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Giới lập tr&igrave;nh vi&ecirc;n trẻ tuổi tại Thung lũng Silicon thường ch&ecirc; bai Java l&agrave; qu&aacute; gi&agrave; cỗi v&agrave; lỗi thời, đặc biệt l&agrave; trong thời đại di động, nơi hiệu năng c&oacute; &yacute; nghĩa v&ocirc; c&ugrave;ng quan trọng với người d&ugrave;ng. Nhưng ng&ocirc;n ngữ n&agrave;y lại c&oacute; nhiều đặc t&iacute;nh được c&aacute;c tập đo&agrave;n lớn đặc biệt ưa th&iacute;ch.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;<em>Java. Kh&ocirc;ng th&uacute; vị nhưng rất dễ dự đo&aacute;n. Ng&ocirc;n ngữ để c&aacute;c đội ngũ to lớn c&oacute; thể x&acirc;y dựng những thứ to lớn cho c&aacute;c tổ chức to lớn. Người ta ch&ecirc; bai, nhưng Java đơn giản l&agrave; hoạt động tốt&rdquo;</em>, t&aacute;c giả Paul Ford khẳng định trong b&agrave;i viết &ldquo;M&atilde; nguồn l&agrave; g&igrave;&rdquo; đăng tải tr&ecirc;n tạp ch&iacute; Bloomberg Businessweek.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&iacute;nh nhờ độ ổn định v&agrave; t&iacute;nh tương th&iacute;ch cao n&ecirc;n Java rất được l&ograve;ng c&aacute;c doanh nghiệp lớn. Sự đảm bảo n&agrave;y khiến cho cộng đồng coder Java l&uacute;c n&agrave;o cũng đ&ocirc;ng đảo về số lượng.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đến năm 2009, Oracle mua lại Sun với gi&aacute; 7,4 tỷ USD. Chỉ một năm sau đ&oacute;, Oracle mang Google ra t&ograve;a.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Cuộc trỗi dậy mạnh mẽ của Android</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Năm 2005, Google mua lại một startup c&oacute; t&ecirc;n gọi Android với gi&aacute; 50 triệu USD. Đội ngũ Android được Google trưng dựng để ph&aacute;t triển một hệ điều h&agrave;nh di động ho&agrave;n to&agrave;n mới.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiểu r&otilde; rằng cuộc c&aacute;ch mạng smartphone sẽ sớm b&ugrave;ng nổ, đội ngũ Google/Android phải gấp r&uacute;t ho&agrave;n th&agrave;nh hệ điều h&agrave;nh của m&igrave;nh. Để l&agrave;m được điều n&agrave;y, họ buộc phải x&acirc;y dựng Android tr&ecirc;n nền tảng l&agrave; c&aacute;c c&ocirc;ng nghệ đ&atilde; phổ biến v&agrave; được cung cấp miễn ph&iacute; từ trước, v&iacute; dụ như l&otilde;i (kernel) Linux v&agrave; ng&ocirc;n ngữ lập tr&igrave;nh Java của Sun.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://genk.mediacdn.vn/k:2016/google-android-for-tablets-1463118352567/giai-ngo-ve-ava-ngoi-no-cho-cuoc-chien-9-ty-usd-giua-google-va-oracle.jpg\" target=\"_blank\" title=\"Andy Rubin, cha đẻ Android.\"><img alt=\"Andy Rubin, cha đẻ Android.\" id=\"img_2b0b4fb0-18ce-11e6-b3d1-11e3aadf807b\" src=\"https://genk.mediacdn.vn/k:2016/google-android-for-tablets-1463118352567/giai-ngo-ve-ava-ngoi-no-cho-cuoc-chien-9-ty-usd-giua-google-va-oracle.jpg\" /></a></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Andy Rubin, cha đẻ Android.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Quyết định n&agrave;y của Google c&oacute; 2 &yacute; nghĩa quan trọng: Đầu ti&ecirc;n, rất nhiều lập tr&igrave;nh vi&ecirc;n đ&atilde; th&agrave;nh tạo Java từ trước, do đ&oacute; họ sẽ kh&ocirc;ng phải học lại một ng&ocirc;n ngữ mới nếu tham gia v&agrave;o cuộc c&aacute;ch mạng di động của Google. Thứ 2, v&igrave; đ&atilde; phổ biến từ trước n&ecirc;n Java đ&atilde; c&oacute; sẵn khả năng hỗ trợ rất nhiều c&ocirc;ng nghệ cốt l&otilde;i cần thiết để x&acirc;y dựng một hệ điều h&agrave;nh mới. Google chẳng c&oacute; l&yacute; do g&igrave; để l&agrave;m lại những thứ đ&atilde; c&oacute; từ trước.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong phi&ecirc;n xử vừa diễn ra v&agrave;i ng&agrave;y trước, cựu CEO Google v&agrave; cũng l&agrave; chủ tịch Alphabet hiện tại, Eric Schmidt khẳng định rằng Google đ&atilde; từng cố trả khoản ph&iacute; 40 triệu USD để được sử dụng Java tr&ecirc;n Android. Cuộc thương thuyết n&agrave;y thất bại, v&agrave; kết quả l&agrave; Google kh&ocirc;ng sử dụng logo Java ch&iacute;nh thức cho Android hay c&aacute;c c&ocirc;ng nghệ kh&aacute;c của h&atilde;ng.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Song, Java l&agrave; một ng&ocirc;n ngữ mở. Do đ&oacute;, Google vẫn tiếp tục d&ugrave;ng Java tr&ecirc;n Android, sao ch&eacute;p lại bất cứ phần n&agrave;o h&atilde;ng n&agrave;y được ph&eacute;p sao ch&eacute;p. Ngay ch&iacute;nh cả Sun Microsystems c&oacute; lẽ cũng đ&atilde; kh&ocirc;ng qu&aacute; kh&oacute; chịu với h&agrave;nh vi n&agrave;y của Google. X&eacute;t cho c&ugrave;ng, h&agrave;ng trăm ngh&igrave;n (nếu kh&ocirc;ng muốn n&oacute;i l&agrave; h&agrave;ng triệu) coder tr&ecirc;n thế giới vẫn sử dụng Java m&agrave; chẳng cần phải trả một đồng ph&iacute; n&agrave;o cho Sun trước đ&acirc;y hay Oracle b&acirc;y giờ.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://genk.mediacdn.vn/k:2016/larry-ellison-1463159169410/giai-ngo-ve-ava-ngoi-no-cho-cuoc-chien-9-ty-usd-giua-google-va-oracle.jpg\" target=\"_blank\" title=\"\r\nNhà sáng lập Oracle, Larry Ellison là người rất hiếu chiến.\r\n\"><img alt=\"\r\nNhà sáng lập Oracle, Larry Ellison là người rất hiếu chiến.\r\n\" id=\"img_37af31f0-192d-11e6-b3d1-11e3aadf807b\" src=\"https://genk.mediacdn.vn/k:2016/larry-ellison-1463159169410/giai-ngo-ve-ava-ngoi-no-cho-cuoc-chien-9-ty-usd-giua-google-va-oracle.jpg\" /></a></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nh&agrave; s&aacute;ng lập Oracle, Larry Ellison l&agrave; người rất hiếu chiến.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhưng Oracle về bản chất l&agrave; một g&atilde; khổng lồ cực kỳ hung h&atilde;n. Khi đ&atilde; nh&igrave;n thấy kẽ hở trong h&agrave;nh động của Google, Oracle đ&atilde; nhanh ch&oacute;ng khởi kiện.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Nhập nhằng về mặt ph&aacute;p l&yacute;</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Oracle kh&ocirc;ng hề khởi kiện Google v&igrave; sử dụng ng&ocirc;n ngữ lập tr&igrave;nh Java. H&atilde;ng n&agrave;y chỉ bất b&igrave;nh v&igrave; Google sử dụng tr&aacute;i ph&eacute;p&nbsp;<em>một phần</em>&nbsp;của c&ocirc;ng nghệ Java n&oacute;i chung. N&oacute;i c&aacute;ch kh&aacute;c, d&ugrave; v&ocirc; t&igrave;nh hay hữu &yacute; th&igrave; Sun cũng đ&atilde; giăng sẵn một c&aacute;i bẫy khi tuy&ecirc;n bố Java l&agrave; ng&ocirc;n ngữ mở v&agrave; miễn ph&iacute;. Google bước ch&acirc;n v&agrave;o c&aacute;i bẫy đ&oacute; v&agrave; hưởng trọn tr&aacute;i ngọt từ Android. Đến khi Sun về tay Oracle th&igrave; c&aacute;i bẫy mới hiện h&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cụ thể hơn, Oracle cho rằng Google đ&atilde; vi phạm quyền sở hữu tr&iacute; tuệ của h&atilde;ng n&agrave;y khi copy lại c&aacute;c API c&oacute; bản quyền tr&ecirc;n Java. Bạn c&oacute; thể&nbsp;<a href=\"https://genk.vn/kham-pha/giai-ngo-ve-api-vi-sao-noi-api-co-y-nghia-song-con-voi-ca-the-gioi-dien-toan-20160403144419703.chn\" target=\"_blank\" title=\"tìm hiểu sâu về API tại đây\">t&igrave;m hiểu s&acirc;u về API tại đ&acirc;y</a>, nhưng n&oacute;i n&ocirc;m na th&igrave; API l&agrave; c&aacute;c khớp nối để mỗi phần mềm c&oacute; thể giao tiếp, tận dụng lại t&iacute;nh năng của một phần mềm kh&aacute;c. Oracle kh&ocirc;ng khởi kiện v&igrave; Google đ&atilde; copy lại&nbsp;<em>nội dung</em>&nbsp;m&atilde; nguồn của Oracle, m&agrave; bởi Google đ&atilde; sao ch&eacute;p lại&nbsp;<em>cấu tr&uacute;c</em>&nbsp;tương t&aacute;c m&agrave; Oracle đ&atilde; quy định cho c&aacute;c thư viện cốt l&otilde;i của Java.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://genk.mediacdn.vn/k:2016/15180124055-4c1d340bab-o-copy-1463158855222/giai-ngo-ve-ava-ngoi-no-cho-cuoc-chien-9-ty-usd-giua-google-va-oracle.jpg\" title=\"Bao nhiêu kẹo ngọt nay sắp trở thành \"><img alt=\"Bao nhiêu kẹo ngọt nay sắp trở thành trái đắng.\" id=\"img_828c3160-192c-11e6-b3d1-11e3aadf807b\" src=\"https://genk.mediacdn.vn/k:2016/15180124055-4c1d340bab-o-copy-1463158855222/giai-ngo-ve-ava-ngoi-no-cho-cuoc-chien-9-ty-usd-giua-google-va-oracle.jpg\" /></a></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bao nhi&ecirc;u kẹo ngọt nay sắp trở th&agrave;nh &quot;tr&aacute;i đắng&quot;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Phần lớn ng&agrave;nh c&ocirc;ng nghiệp phần mềm đều bất đồng s&acirc;u sắc với quan điểm của Oracle. Theo họ, API l&agrave; sản phẩm tr&iacute; tuệ&nbsp;<em>kh&ocirc;ng thể được đăng k&yacute; bản quyền</em>. Nhưng Oracle kh&ocirc;ng hẳn l&agrave; v&ocirc; l&yacute;, v&agrave; một t&ograve;a ph&uacute;c thẩm đ&atilde; từng tuy&ecirc;n phần thắng về ph&iacute;a h&atilde;ng n&agrave;y. Mới gần đ&acirc;y nhất, cha đẻ của Android l&agrave; Andy Rubin đ&atilde; bị lộ một email với nội dung rằng&nbsp;<a href=\"https://genk.vn/cha-de-android-vua-de-lo-mot-email-co-the-khien-google-mat-9-ty-usd-20160513124855102.chn\" target=\"_blank\" title=\"các API có thể được đăng ký bản quyền\">c&aacute;c API Java c&oacute; thể được đăng k&yacute; bản quyền</a>, v&agrave; Google c&oacute; thể đ&atilde; cố t&igrave;nh vi phạm quyền sở hữu của Oracle khi sử dụng lại c&aacute;c API của Java.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu Oracle thắng kiện, thế giới phần mềm n&oacute;i ri&ecirc;ng v&agrave; to&agrave;n bộ ng&agrave;nh c&ocirc;ng nghệ cao n&oacute;i chung sẽ trở n&ecirc;n hỗn loạn. API l&agrave; khớp nối của tất cả c&aacute;c phần mềm, n&ecirc;n nếu API l&agrave; cơ sở để khởi kiện th&igrave; chắc chắn chẳng c&oacute; một c&ocirc;ng ty phần mềm n&agrave;o kh&ocirc;ng thể trở th&agrave;nh nguy&ecirc;n đơn hay bị đơn cả.</span></p>\r\n', 'giai-ngo-ve-ava-ngoi-no-cho-cuoc-chien-9-ty-usd-giua-google-va-oracle.jpg', 1, 1, 1, 1190, NULL, '2020-04-10 15:14:36', '2020-07-17 05:45:53'),
(2, 'Học lập trình PHP nên bắt đầu từ đâu', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Lập tr&igrave;nh l&agrave; c&ocirc;ng việc tạo ra ứng dụng, chương tr&igrave;nh thực thi tr&ecirc;n m&aacute;y t&iacute;nh, thiết bị để phục vụ cho cuộc sống, l&agrave;m...cho cuộc sống tiện nghi, tốt đẹp v&agrave; c&oacute; nhiều niềm vui hơn.</span></p>\r\n', '<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">1. Đ&Aacute;NH GI&Aacute; CHUNG VỀ NG&Ocirc;N NGỮ PHP&nbsp;</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/lap%20trinh%20php%20co%20ban.png\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;Ng&ocirc;n ngữ PHP ( Personal Home Page ) l&agrave; một ng&ocirc;n ngữ lập tr&igrave;nh script ( một nh&aacute;nh của ng&ocirc;n ngữ lập tr&igrave;nh ) hay một loại m&atilde; lệnh chủ yếu được d&ugrave;ng để ph&aacute;t triển c&aacute;c ứng dụng viết cho&nbsp;m&aacute;y chủ, m&atilde; nguồn mở ( ho&agrave;n to&agrave;n miễn ph&iacute; cho việc thực hiện website của m&igrave;nh ). PHP c&oacute; thể chạy được tr&ecirc;n nhiều nền tảng hệ điều h&agrave;nh kh&aacute;c nhau như Windows, Mac OS v&agrave; Linux.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;PHP hoạt động với tốc độ nhanh v&agrave; hiệu quả. Một server b&igrave;nh thường c&oacute; thể đ&aacute;p ứng được h&agrave;ng triệu lượt truy cập/ng&agrave;y.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">PHP hỗ trợ kết nối đến h&agrave;ng triệu hệ quản trị cơ sở dữ liệu kh&aacute;c nhau.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;PHP cung cấp một hệ thống dữ liệu phong ph&uacute;. Từ đầu PHP được x&acirc;y dựng v&agrave; ph&aacute;t triển c&aacute;c ứng dụng tr&ecirc;n web n&ecirc;n n&oacute; cũng cung cấp rất nhiều h&agrave;m x&acirc;y dựng sẵn gi&uacute;p dễ d&agrave;ng thực hiện c&aacute;c c&ocirc;ng việc : Gửi, nhận email, l&agrave;m việc vưới Cookie,...</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">PHP c&oacute; thể sử dụng được nhiều hệ điều h&agrave;nh. Thậm ch&iacute;, c&aacute;c lập tr&igrave;nh vi&ecirc;n c&oacute; thể đem m&atilde; PHP n&agrave;y chạy tr&ecirc;n c&aacute;c hệ điều h&agrave;nh kh&aacute;c m&agrave; kh&ocirc;ng phải sửa đổi lại m&atilde;.&nbsp;</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;PHP sử dụng m&atilde; nguồn mở mang lại sự linh hoạt cho c&aacute;c lập tr&igrave;nh vi&ecirc;n trong qu&aacute; tr&igrave;nh chỉnh sửa, s&aacute;ng tạo ri&ecirc;ng của m&igrave;nh.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;PHP c&oacute; cộng đồng ph&aacute;t triển mạnh mẽ. Được ph&aacute;t triển v&agrave; tối ưu li&ecirc;n tục từ c&aacute;c nh&agrave; ph&aacute;t triển tr&ecirc;n to&agrave;n thế giới khiến cho n&oacute; v&ocirc; th&acirc;n thiện với mọi người v&agrave; ng&agrave;y c&agrave;ng ho&agrave;n thiện hơn.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">PHP nhanh ch&oacute;ng trở th&agrave;nh ng&ocirc;n ngữ lập tr&igrave;nh Web top đầu được nhiều lập tr&igrave;nh vi&ecirc;n sử dụng, v&agrave; đ&acirc;y l&agrave; ng&ocirc;n ngữ c&oacute; đặc t&iacute;nh dễ học v&agrave; ph&ugrave; hợp với người bắt đầu.</span></li>\r\n</ul>\r\n\r\n<h2><br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">2. KIẾN THỨC PHP CẦN QUAN T&Acirc;M ĐỂ BẮT ĐẦU HỌC LẬP TR&Igrave;NH PHP&nbsp;</span></h2>\r\n\r\n<p><br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><em>T&igrave;m hiểu những kiến thức cơ bản trước khi đi s&acirc;u v&agrave;o nghi&ecirc;n cứu n&acirc;ng cao hơn . Khi đ&atilde; c&oacute; kiến thức cơ sở về PHP l&agrave; nền tảng&nbsp; để bạn c&oacute; thể&nbsp; thể tiếp thu kiến thức mới dễ d&agrave;ng v&agrave; nhanh hơn</em>.</span></p>\r\n\r\n<h3><br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">1. T&igrave;m hiểu HTML/CSS</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://techmaster.vn/media/fileman/Uploads/HTML/HTMLCSSJS.jpg\" /></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;HTML v&agrave; CSS l&agrave; kiến thức cơ sở để bạn theo học ng&ocirc;n ngữ PHP một c&aacute;ch dễ d&agrave;ng.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;HTML v&agrave; CSS l&agrave; nền tảng của Web . HTML ( HyperText Markup Language ) l&agrave; n&ocirc;n ngữ được sử dụng để n&oacute;i cho tr&igrave;nh duyệt web của bạn biết đ&acirc;u l&agrave; c&aacute;c phần của 1 trang web. V&igrave; vậy, bằng c&aacute;ch sử dụng HTML bạn c&oacute; thể x&aacute;c định được ti&ecirc;u đề, đoạn văn bản, li&ecirc;n kết, h&igrave;nh ảnh, v&agrave; nhiều hơn nữa, gi&uacute;p tr&igrave;nh duyệt của bạn biết c&aacute;ch l&agrave;m thế n&agrave;o để cấu tr&uacute;c trang web m&agrave; bạn đang xem. Css ( Cascading Style Sheets ) l&agrave; ng&ocirc;n ngữ cung cấp h&igrave;nh thức v&agrave; định dạng cho c&aacute;c trang web. N&oacute;i c&aacute;ch kh&aacute;c CSS l&agrave; c&aacute;i m&agrave; bạn sử dụng để l&agrave;m cho c&aacute;c trang web tr&ocirc;ng đẹp hơn với c&aacute;c font chứ ưa th&iacute;ch, m&agrave;u sắc phong ph&uacute;, h&igrave;nh nền tuyệt đẹp v&agrave; thậm ch&iacute; c&aacute;c ảnh động v&agrave; c&aacute;c hiệu ứng 3D.</span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">2. C&ocirc;ng cụ&nbsp;</span></h3>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;XAMPP l&agrave; một c&ocirc;ng cụ tuyệt vời gi&uacute;p bạn tạo ra một m&aacute;y chủ ( Web - server) ngay tr&ecirc;n m&aacute;y t&iacute;nh c&aacute; nh&acirc;n. N&oacute; được t&iacute;ch hợp sẵn Apache, PHP, MySQL, FPT, Mail Server v&agrave; c&aacute;c c&ocirc;ng cụ kh&aacute;c như phpMyAdmi.&nbsp;</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu bạn chưa am hiểu về Website th&igrave; kh&ocirc;ng n&ecirc;n mất tiền thu&ecirc; hosting hay t&ecirc;n miền. XAMPP ho&agrave;n to&agrave;n c&oacute; thể gi&uacute;p bạn trong việc học PHP.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Link tải XAMPP phi&ecirc;n bản mới nhất: https://www.apachefriends.org/download.html</span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">3. Những kiến thức cơ bản</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://techmaster.vn/media/fileman/Uploads/PHP/php.png\" /></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;C&aacute;c c&aacute;ch sử dụng c&uacute; ph&aacute;p bắt đầu <!--?php và kết thúc?--> đ&uacute;ng thời điểm.&nbsp;</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;C&aacute;ch ghi ch&uacute; trong một tập tin PHP.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;C&aacute;ch sử dụng c&acirc;u lệnh echo v&agrave; print.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;C&aacute;ch thiết lập một gi&aacute; trị ( variable)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;C&aacute;ch sử dụng mảng, to&aacute;n tử , c&acirc;u điều kiện.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;Kiến thức v&ograve;ng lặp : for, while.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;H&agrave;m ( function): Viết h&agrave;m gi&uacute;p tiết kiệm thời gian v&agrave; l&agrave;m đoạn code dễ đọc hơn. C&oacute; rất nhiều h&agrave;m trong một website. Khi bạn đ&atilde; c&oacute; kiến thức cơ bản về PHP th&igrave; c&oacute; thể học h&agrave;m dễ d&agrave;ng.&nbsp;</span></li>\r\n</ul>\r\n\r\n<blockquote>\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Lập tr&igrave;nh web được coi l&agrave; h&agrave;nh trang v&agrave; sự lựa chọn tốt cho tương lai của c&aacute;c bạn đang theo định hướng chuy&ecirc;n ng&agrave;nh phần mềm. Với cơ hội việc l&agrave;m rộng lớn, mức lương hấp dẫn cơ hội việc l&agrave;m với mức lương&nbsp;<br />\r\nhấp dẫn v&agrave; cơ hội thăng tiến.&nbsp; &nbsp;</span></p>\r\n</blockquote>\r\n', 'php.png', 1, 2, 1, 167, 3.5, '2020-04-10 15:18:35', '2020-07-04 03:12:15'),
(3, 'Tại sao chúng ta nên sử dụng Angular', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Việc ph&aacute;t triển Web đ&atilde; c&oacute; bước thay đổi đ&aacute;ng kể trong v&agrave;i năm qua. Với phi&ecirc;n bản ECMAScript (ES) 2015 - ch&uacute;ng ta quen thuộc với c&aacute;i t&ecirc;n ES6, với những class hay arrow function.</span></p>\r\n', '<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Mở đầu</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu đ&atilde; từng x&acirc;y dựng một v&agrave;i ứng dụng&nbsp;<strong>Web</strong>, chắc hẳn bạn đ&atilde; từng nghe đến c&aacute;i t&ecirc;n&nbsp;<strong>Angular</strong>, một&nbsp;<strong>Frameworks Javascript</strong>&nbsp;gi&uacute;p ch&uacute;ng ta x&acirc;y dựng ứng dụng&nbsp;<strong>Web</strong>&nbsp;đầy đủ t&iacute;nh năng từ ph&iacute;a&nbsp;<strong>Client</strong>.&nbsp;<strong>Angular</strong>lần đầu được ph&aacute;t h&agrave;nh bởi g&atilde; khổng lồ&nbsp;<strong>Google</strong>&nbsp;v&agrave;o năm 2010 với phi&ecirc;n bản&nbsp;<strong>AngularJS</strong>, sau đ&oacute; đ&atilde; c&oacute; chỗ đứng kh&aacute; vững chắc trong một thời gian d&agrave;i, phi&ecirc;n bản&nbsp;<strong>Angular 2</strong>&nbsp;ph&aacute;t h&agrave;nh năm 2016 mang đến một bước chuyển m&igrave;nh vượt bậc, một c&ocirc;ng cụ thực sự mạnh mẽ cho việc ph&aacute;t triển ứng dụng&nbsp;<strong>Web</strong>&nbsp;tr&ecirc;n cả nền tảng&nbsp;<strong>Mobile</strong>&nbsp;v&agrave;&nbsp;<strong>Desktop</strong>. Hiện nay ch&uacute;ng ta đang c&oacute; phi&ecirc;n bản&nbsp;<strong>Angular 6</strong>.<br />\r\nHiện giữa v&ocirc; v&agrave;n c&aacute;c thư viện v&agrave;&nbsp;<strong>frameworks Javscript</strong>,&nbsp;<strong>Angular</strong>&nbsp;c&oacute; g&igrave; nổi bật v&agrave; tại sao ch&uacute;ng ta n&ecirc;n chọn&nbsp;<strong>Angular</strong>&nbsp;cho ứng dụng tiếp theo của m&igrave;nh ?<br />\r\nB&agrave;i viết n&agrave;y xin đưa ra 6 l&yacute; do khiến ch&uacute;ng ta sẽ y&ecirc;u&nbsp;<strong>Angular</strong></span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">1. Angular gi&uacute;p n&acirc;ng cao năng suất của c&aacute;c lập tr&igrave;nh vi&ecirc;n.</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Việc ph&aacute;t triển&nbsp;<strong>Web</strong>&nbsp;đ&atilde; c&oacute; bước thay đổi đ&aacute;ng kể trong v&agrave;i năm qua. Với phi&ecirc;n bản&nbsp;<strong>ECMAScript (ES) 2015</strong>&nbsp;- ch&uacute;ng ta quen thuộc với c&aacute;i t&ecirc;n ES6, với những&nbsp;<strong>class</strong>&nbsp;hay&nbsp;<strong>arrow function</strong>.&nbsp;<strong>Angular 2+</strong>&nbsp;ứng dụng những t&iacute;nh năng mới n&agrave;y gi&uacute;p việc&nbsp;<strong>code</strong>&nbsp;với&nbsp;<strong>Angular</strong>&nbsp;trở n&ecirc;n r&otilde; r&agrave;ng v&agrave; dễ học hơn rất nhiều.<br />\r\nTh&ecirc;m v&agrave;o đ&oacute;, với việc ứng dụng&nbsp;<strong>Typescript</strong>&nbsp;- một ng&ocirc;n ngữ - hay l&agrave; một bản&nbsp;<strong>n&acirc;ng cấp</strong>&nbsp;đ&aacute;ng gi&aacute; của&nbsp;<strong>Javascript</strong>,&nbsp;<strong>Angular</strong>&nbsp;kết hợp với&nbsp;<strong>Typescript</strong>, ch&uacute;ng ta c&oacute; một c&ocirc;ng cụ tuyệt vời gi&uacute;p xử l&yacute; c&aacute;c vấn đề hạn chế của&nbsp;<strong>JS</strong>&nbsp;như kiểm tra kiểu dữ liệu,&nbsp;<strong>refactor code</strong>&nbsp;an to&agrave;n hơn,... từ đ&oacute; cũng hỗ trợ tốt hơn cho việc&nbsp;<strong>Debug</strong>&nbsp;cũng như gi&uacute;p c&aacute;c&nbsp;<strong>Dev</strong>&nbsp;thực sự hiểu r&otilde; m&atilde; nguồn của họ hơn.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">2. Cấu tr&uacute;c ph&aacute;t triển r&otilde; r&agrave;ng.</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Điều quan trọng của một&nbsp;<strong>Frameworks</strong>&nbsp;đối với lập tr&igrave;nh vi&ecirc;n đ&oacute; l&agrave; cấu tr&uacute;c ph&aacute;t triển ứng dụng của n&oacute;, v&agrave;&nbsp;<strong>Angular</strong>&nbsp;mang đến một kiến tr&uacute;c rất r&otilde; r&agrave;ng, dựa tr&ecirc;n ba yếu tố ch&iacute;nh:&nbsp;<strong>class</strong>, c&aacute;c&nbsp;<strong>dependency</strong>&nbsp;được th&ecirc;m v&agrave;o v&agrave; m&ocirc; h&igrave;nh MVVM (model-view-view/model).<br />\r\n<strong>Angular</strong>&nbsp;sử dụng&nbsp;<strong>class</strong>&nbsp;trong ES6 với một loạt c&aacute;c thuộc t&iacute;nh để x&acirc;y dựng to&agrave;n bộ c&aacute;c cấu tr&uacute;c chủ chốt, giả sử bạn muốn tạo một&nbsp;<strong>Angular component</strong>&nbsp;- Tạo một&nbsp;<strong>class</strong>&nbsp;v&agrave; th&ecirc;m v&agrave;o c&aacute;c thuộc t&iacute;nh cần thiết. Hay bạn muốn tạo một&nbsp;<strong>Angular module</strong>&nbsp;- H&atilde;y tạo một&nbsp;<strong>class</strong>&nbsp;v&agrave; th&ecirc;m v&agrave;o đ&oacute; c&aacute;c thuộc t&iacute;nh cần thiết. Về cơ bản sẽ l&agrave; như vậy,&nbsp;<strong>Angular</strong>&nbsp;cung cấp một cấu tr&uacute;c r&otilde; r&agrave;ng để x&acirc;y dựng từng t&iacute;nh năng cho ứng dụng của bạn.<br />\r\nC&aacute;c&nbsp;<strong>dependency</strong>&nbsp;mạnh mẽ được sử dụng trong ứng dụng khi cần thiết, v&agrave; khi cần t&iacute;ch hợp bất k&igrave;&nbsp;<strong>dependency</strong>&nbsp;n&agrave;o, như&nbsp;<strong>HTTP</strong>&nbsp;hay&nbsp;<strong>Router</strong>, ch&uacute;ng ta chỉ cần th&ecirc;m n&oacute; v&agrave;o b&ecirc;n trong&nbsp;<strong>constructor</strong>&nbsp;của&nbsp;<strong>class</strong>.<br />\r\nM&ocirc; h&igrave;nh&nbsp;<strong>MVVM</strong>&nbsp;cũng gi&uacute;p&nbsp;<strong>Angular</strong>&nbsp;chiếm lợi thế trong x&acirc;y dựng ứng dụng&nbsp;<strong>client-side</strong>, thường ta sẽ c&oacute; 3 điều cần quan t&acirc;m ch&iacute;nh: đ&oacute; l&agrave;&nbsp;<strong>giao diện người d&ugrave;ng</strong>, m&atilde; nguồn điều khiển giao diện v&agrave; m&ocirc; h&igrave;nh dữ liệu (<strong>data</strong>) cho giao diện. Angular với&nbsp;<strong>MVVM</strong>&nbsp;ph&acirc;n biệt ho&agrave;n to&agrave;n r&otilde; r&agrave;ng c&aacute;c yếu tố tr&ecirc;n nhờ m&ocirc; h&igrave;nh&nbsp;<strong>MVVM</strong>:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Phần giao diện (<strong>view</strong>) được định nghĩa trong một&nbsp;<strong>template</strong>&nbsp;bao h&agrave;m&nbsp;<strong>HTML</strong>&nbsp;d&agrave;nh cho một&nbsp;<strong>component</strong>&nbsp;nhất định.&nbsp;<strong>Template</strong>&nbsp;c&oacute; thể l&agrave; to&agrave;n bộ&nbsp;<strong>Layout</strong>&nbsp;hoặc bất cứ mảnh gh&eacute;p n&agrave;o trong&nbsp;<strong>Layout</strong>&nbsp;đ&oacute;.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Model</strong>&nbsp;được định nghĩa như l&agrave; c&aacute;c thuộc t&iacute;nh của&nbsp;<strong>component class</strong>. C&oacute; thể hiểu l&agrave; dữ liệu, dựa v&agrave;o đ&oacute; để phần&nbsp;<strong>View</strong>&nbsp;sử dụng để thực thi.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>view/model</strong>&nbsp;l&agrave;&nbsp;<strong>class</strong>&nbsp;quản l&yacute; cả&nbsp;<strong>view</strong>&nbsp;cũng như&nbsp;<strong>model</strong>. L&agrave; phần&nbsp;<strong>code</strong>&nbsp;sẽ xử l&yacute; việc truy xuất dữ liệu, đồng thời thực thi c&aacute;c tương t&aacute;c của người d&ugrave;ng tr&ecirc;n&nbsp;<strong>view</strong>.<br />\r\n	Với việc ứng dụng c&aacute;c điểm t&iacute;ch cực của c&aacute;c th&agrave;nh phần tr&ecirc;n,&nbsp;<strong>Angular</strong>&nbsp;khiến việc ph&aacute;t triển ứng dụng trở n&ecirc;n dễ d&agrave;ng v&agrave; hiệu quả hơn.</span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">3. Extensive binding</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Rất nhiều ứng dụng&nbsp;<strong>Web</strong>&nbsp;l&agrave;m việc với dữ liệu (<strong>data</strong>).&nbsp;<strong>App</strong>&nbsp;sẽ truy xuất dữ liệu từ&nbsp;<strong>Server</strong>&nbsp;v&agrave; hiển thị dữ liệu đ&oacute; tới người d&ugrave;ng tr&ecirc;n&nbsp;<strong>view</strong>, sử dụng&nbsp;<strong>template</strong>. V&agrave; c&aacute;c tương t&aacute;c của người d&ugrave;ng sẽ được khiến dữ liệu thay đổi, được&nbsp;<strong>view</strong>&nbsp;ghi nhận v&agrave; lưu lại tr&ecirc;n&nbsp;<strong>server</strong>.&nbsp;<strong>Data Binding</strong>&nbsp;trong Angular gi&uacute;p bạn thực thi tiến tr&igrave;nh tr&ecirc;n rất dễ d&agrave;ng. Đơn thuần từ việc r&agrave;ng buộc th&agrave;nh phần&nbsp;<strong>HTML</strong>&nbsp;trong&nbsp;<strong>template</strong>&nbsp;với c&aacute;c thuộc t&iacute;nh trong&nbsp;<strong>class</strong>&nbsp;v&agrave; dữ liệu sẽ tự động xuất hiện tr&ecirc;n m&agrave;n h&igrave;nh. Với c&aacute;c tương t&aacute;c của người d&ugrave;ng đ&ograve;i hỏi thay đổi dữ liệu,&nbsp;<strong>Angular</strong>&nbsp;sử dụng phương ph&aacute;p&nbsp;<strong>two-way binding</strong>. Bất k&igrave; thay đổi dữ liệu đến từ&nbsp;<strong>view</strong>&nbsp;sẽ tự động cập nhật thuộc t&iacute;nh &quot;<strong>model</strong>&quot; b&ecirc;n trong&nbsp;<strong>class</strong>.<br />\r\nTh&ecirc;m v&agrave;o đ&oacute;,&nbsp;<strong>Angular</strong>&nbsp;cũng hỗ trợ&nbsp;<strong>property binding</strong>&nbsp;- cho ph&eacute;p ch&uacute;ng ta điều khiển&nbsp;<strong>DOM</strong>&nbsp;bằng c&aacute;ch r&agrave;ng buộc thuộc t&iacute;nh&nbsp;<strong>HTML</strong>&nbsp;với thuộc t&iacute;nh của&nbsp;<strong>component class</strong>,&nbsp;<strong>data</strong>&nbsp;sẽ tự động xuất hiện b&ecirc;n trong&nbsp;<strong>view</strong>. V&iacute; dụ, ch&uacute;ng ta r&agrave;ng buộc thuộc t&iacute;nh&nbsp;<strong>hidden</strong>&nbsp;đối với một thẻ&nbsp;<strong>img</strong>&nbsp;với thuộc t&iacute;nh&nbsp;<strong>hideImg</strong>&nbsp;b&ecirc;n trong&nbsp;<strong>class</strong>. Khi thuộc t&iacute;nh&nbsp;<strong>hideImg</strong>&nbsp;nhận gi&aacute; trụ&nbsp;<strong>true</strong>,&nbsp;<strong>img</strong>&nbsp;sẽ tự động&nbsp;<strong>hidden</strong>&nbsp;v&agrave; ngược lại khi&nbsp;<strong>hideImg</strong>&nbsp;nhận gi&aacute; trị&nbsp;<strong>false</strong>, thẻ&nbsp;<strong>img</strong>&nbsp;sẽ tự động hiển thị tới người d&ugrave;ng.<br />\r\nCuối c&ugrave;ng,&nbsp;<strong>Angular</strong>&nbsp;hỗ trợ&nbsp;<strong>event binding</strong>, c&oacute; nghĩa l&agrave; ch&uacute;ng ta c&oacute; thể xử l&iacute; bất k&igrave;&nbsp;<strong>event</strong>&nbsp;n&agrave;o từ ph&iacute;a&nbsp;<strong>view</strong>, như&nbsp;<strong>HTML event</strong>. Về cơ bản ch&uacute;ng ta sẽ gắt&nbsp;<strong>event</strong>&nbsp;với một&nbsp;<strong>method</strong>&nbsp;b&ecirc;n trong&nbsp;<strong>class</strong>. Mỗi khi&nbsp;<strong>event</strong>&nbsp;xuất hiện,&nbsp;<strong>method</strong>&nbsp;tương ứng sẽ được thực thi.<br />\r\n<strong>Extensive binding</strong>&nbsp;gi&uacute;p qu&aacute; tr&igrave;nh hiển thị dữ liệu, điều khiển&nbsp;<strong>DOM</strong>, thực thi c&aacute;c&nbsp;<strong>event</strong>&nbsp;một c&aacute;ch trơn tru v&agrave; dễ d&agrave;ng.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">4. Hỗ trợ đầy đủ t&iacute;nh năng điều hướng (<strong>routing</strong>)</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đa số c&aacute;c ứng dụng&nbsp;<strong>Web</strong>&nbsp;kh&ocirc;ng chỉ c&oacute; 1&nbsp;<strong>view</strong>&nbsp;hay một&nbsp;<strong>page</strong>&nbsp;duy nhất, m&agrave; sẽ cung cấp nhiều&nbsp;<strong>view</strong>&nbsp;kh&aacute;c nhau tương ứng với với c&aacute;c chức năng ch&iacute;nh. V&iacute; dụ như một trang&nbsp;<strong>web</strong>&nbsp;với c&aacute;c trang giới thiệu, trang nội dung, trang chi tiết, trang đăng nhập, đăng k&yacute;,... Ch&uacute;ng ta sẽ cần hiển thị đ&uacute;ng&nbsp;<strong>view</strong>&nbsp;v&agrave;o đ&uacute;ng thời điểm. Đ&oacute; l&agrave; mục đ&iacute;ch của điều hướng (<strong>routing</strong>). V&agrave;&nbsp;<strong>Angular</strong>&nbsp;cung cấp đầy đủ t&iacute;nh năng cho việc n&agrave;y, ch&uacute;ng ta định nghĩa c&aacute;c đường dẫn (<strong>route</strong>) cho mỗi&nbsp;<strong>page view</strong>&nbsp;của ứng dụng. V&agrave; ch&uacute;ng ta sẽ k&iacute;ch hoạt&nbsp;<strong>route</strong>&nbsp;dựa tr&ecirc;n tương t&aacute;c của người d&ugrave;ng (<strong>user</strong>). Ch&uacute;ng ta c&oacute; thể truyền th&ecirc;m dữ liệu v&agrave;o c&aacute;c&nbsp;<strong>route</strong>, gi&uacute;p&nbsp;<strong>view</strong>&nbsp;hiển thị nội dung một c&aacute;ch&nbsp;<strong>dynamic</strong>, c&oacute; thể bảo vệ&nbsp;<strong>route</strong>&nbsp;để người d&ugrave;ng chỉ c&oacute; thể truy cập sau khi đ&atilde; đăng nhập hoặc c&oacute; quyền truy cập, c&oacute; thể ngăn chặn việc người d&ugrave;ng ngay lập tức rời một trang khi c&aacute;c thao t&aacute;c c&ograve;n dang dở cho đến khi họ thực sự x&aacute;c nhận việc rời đi hoặc lưu lại tiến tr&igrave;nh sử dụng,...<strong>Angular</strong>&nbsp;đồng thời cũng hỗ trợ&nbsp;<strong>child-route</strong>&nbsp;cho việc điều hướng b&ecirc;n trong một&nbsp;<strong>route</strong>. Việc điều hướng giữa c&aacute;c&nbsp;<strong>view</strong>&nbsp;b&ecirc;n trong ứng dụng&nbsp;<strong>Angular</strong>&nbsp;thực sự rất linh hoạt v&agrave; mạnh mẽ.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">5.&nbsp;<strong>Angular</strong>&nbsp;gi&uacute;p giảm tối đa k&iacute;ch thước v&agrave; tăng tối đa hiệu suất của ứng dụng.</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">K&iacute;ch thước v&agrave; hiệu năng c&oacute; mối li&ecirc;n quan mật thiết khi ch&uacute;ng ta l&agrave;m việc tr&ecirc;n nền tảng&nbsp;<strong>Web</strong>. Một&nbsp;<strong>component</strong>&nbsp;nhỏ hơn sẽ gi&uacute;p n&acirc;ng cao hiệu suất khởi động - giảm cả thời gian&nbsp;<strong>download</strong>&nbsp;cũng như thời gian cũng như thời gian&nbsp;<strong>compile</strong>&nbsp;tr&ecirc;n tr&igrave;nh duyệt. Giảm k&iacute;ch thước&nbsp;<strong>component</strong>&nbsp;v&agrave; gi&uacute;p tăng hiệu suất l&agrave; một ưu điểm cũng như mục ti&ecirc;u m&agrave;&nbsp;<strong>Angular</strong>&nbsp;mong muốn mang đến cho c&aacute;c lập tr&igrave;nh vi&ecirc;n.<br />\r\nGiảm k&iacute;ch thước ứng dụng c&oacute; thể thực hiện bằng nhiều c&aacute;ch. Đầu ti&ecirc;n ch&uacute;ng ta c&oacute; thể giảm tối đa k&iacute;ch thước của từng&nbsp;<strong>component</strong>&nbsp;tới mức tối thiểu c&oacute; thể. Tiếp theo c&aacute;c&nbsp;<strong>component</strong>&nbsp;sẽ được sắp xếp b&ecirc;n trongtrong&nbsp;<strong>Angular Module</strong>&nbsp;bằng 1 c&aacute;ch đề cho c&aacute;c nh&oacute;m logic c&oacute; li&ecirc;n quan đến nhau sẽ được&nbsp;<strong>download</strong>&nbsp;c&ugrave;ng với nhau. V&agrave; bước thứ ba,&nbsp;<strong>lazy loading</strong>&nbsp;b&ecirc;n trong c&aacute;c&nbsp;<strong>route</strong>&nbsp;sẽ chỉ&nbsp;<strong>downloaad</strong>&nbsp;những&nbsp;<strong>module</strong>&nbsp;cần thiết cho việc hiển thị nội dung cần thiết tới người d&ugrave;ng, v&agrave; sẽ kh&ocirc;ng bao giờ&nbsp;<strong>download</strong>&nbsp;những nội dung kh&ocirc;ng cần thiết.<br />\r\nCh&uacute;ng ta c&oacute; một tr&igrave;nh bi&ecirc;n dịch t&ecirc;n l&agrave;&nbsp;<strong>AOT</strong>, tr&igrave;nh bi&ecirc;n dịch n&agrave;y sẽ chạy một lần trong thời gian&nbsp;<strong>build</strong>&nbsp;ứng dụng. Tr&igrave;nh duyệt sau đ&oacute; sẽ&nbsp;<strong>download</strong>&nbsp;phi&ecirc;n bản chưa được bi&ecirc;n dịch của ứng dụng v&agrave;&nbsp;<strong>render</strong>&nbsp;ứng dụng tới người d&ugrave;ng ngay lập tức m&agrave; kh&ocirc;ng cần bi&ecirc;n dịch n&oacute; lần đầu trong tr&igrave;nh duyệt. Th&ecirc;m nữa l&agrave; sẽ koong cần&nbsp;<strong>download</strong>&nbsp;tr&igrave;nh bi&ecirc;n dịch&nbsp;<strong>Angular</strong>, gi&uacute;p l&agrave;m giảm đ&aacute;ng kể k&iacute;ch thước (<strong>size</strong>) của ứng dụng cần tải về.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">6. Document v&agrave; cộng đồng (community)</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Document cho Angular 2+ -&nbsp;<a href=\"https://viblo.asia/p/angular.io\" target=\"_blank\">angular.io</a>&nbsp;- rất đầy đủ v&agrave; chi tiết, bao h&agrave;m giới thiệu cơ bản gi&uacute;p bạn l&agrave;m quen nhanh ch&oacute;ng với&nbsp;<strong>Angular</strong>, giới thiệu chi tiết, từ cơ bản đến n&acirc;ng cao c&aacute;c&nbsp;<strong>API</strong>&nbsp;của&nbsp;<strong>Angular</strong>, cũng như c&oacute; hẳn một&nbsp;<strong>Tutorial Basic</strong>&nbsp;được x&acirc;y dựng n&ecirc;n bởi&nbsp;<strong>Angular team</strong>, cung cấp cho bạn nhanh ch&oacute;ng nắm bắt c&aacute;c thuộc t&iacute;nh cơ bản của&nbsp;<strong>Framework</strong>.<br />\r\n<strong>Angular</strong>&nbsp;cũng c&oacute; một cộng đồng sử dụng rất lớn, đồng thời được ph&aacute;t triển bởi g&atilde; khổng lồ&nbsp;<strong>Google</strong>, khiến&nbsp;<strong>Angular</strong>&nbsp;kh&ocirc;ng ngừng trưởng th&agrave;nh với c&aacute;c phi&ecirc;n bản lu&ocirc;n được cập nhật, hiện ch&uacute;ng ta đ&atilde; c&oacute; phi&ecirc;n bản&nbsp;<strong>Angular 6+</strong>&nbsp;mới cam kết&nbsp;<strong>LTS</strong>&nbsp;từ&nbsp;<strong>Google</strong>.<br />\r\n<strong>Angular</strong>&nbsp;kh&ocirc;ng thật sự dễ để l&agrave;m quen, nhưng rất đ&aacute;ng để &aacute;p dụng cho ứng dụng của bạn, tr&ecirc;n đ&acirc;y l&agrave; những l&iacute; do ch&uacute;ng ta n&ecirc;n sử dụng&nbsp;<strong>Angular</strong>, được tham khảo v&agrave; dịch từ b&agrave;i viết&nbsp;<a href=\"https://www.pluralsight.com/blog/software-development/angular-2\" target=\"_blank\">Why learn Angular</a>, hi vọng sẽ hữu &iacute;ch.</span></p>\r\n', 'angular.png', 2, 4, 1, 12394, 1.5, '2020-04-10 15:20:37', '2020-07-15 16:49:58');
INSERT INTO `posts` (`id`, `title`, `description`, `contents`, `thumbnail`, `user_id`, `category_id`, `status`, `view`, `rate`, `created_at`, `updated_at`) VALUES
(4, 'Con đường Sự nghiệp Lập trình JAVA', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiện nay, c&oacute; rất nhiều cơ hội tốt cho Lập tr&igrave;nh vi&ecirc;n Java. Dự b&aacute;o tỷ lệ tăng trưởng việc l&agrave;m l&agrave; 19% từ năm 2019 - 2024.</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tại Việt Nam, thị trường dự b&aacute;o c&ograve;n thiếu khoảng 250.000 lập tr&igrave;nh vi&ecirc;n cho đến năm 2020 v&agrave; vẫn tiếp tục tăng.</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Java c&oacute; mặt ở khắp mọi nơi</strong>: Tr&ecirc;n tất cả c&aacute;c nền tảng v&agrave; thiết bị v&agrave; ở tất cả c&aacute;c quốc gia tr&ecirc;n thế giới. Java&nbsp;cho ph&eacute;p c&aacute;c lập tr&igrave;nh vi&ecirc;n&nbsp;tạo ra&nbsp;c&aacute;c chương tr&igrave;nh hoạt động ở&nbsp;bất cứ nơi n&agrave;o. Theo thời gian, tầm quan trọng v&agrave; mức độ phổ biến của Java đang tăng l&ecirc;n v&igrave; n&oacute; c&oacute; khả năng đổi mới v&agrave; biến đổi đ&aacute;ng kinh ngạc khi bối cảnh c&ocirc;ng nghệ thay đổi.</span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Java vẫn l&agrave; nền tảng phổ biến nhất</strong>:&nbsp;Cho d&ugrave; bạn muốn sử dụng Java&nbsp;để ph&aacute;t triển c&aacute;c ứng dụng cho thiết bị IOT, ứng dụng di động hay ứng dụng web...</span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Java rất chặt chẽ</strong>: V&igrave; t&iacute;nh chặt chẽ của Java, bạn c&oacute; thể dễ nắm bắt v&agrave; dự đo&aacute;n&nbsp;c&aacute;ch Java hoạt động. Java l&agrave;&nbsp;dễ sử dụng, dễ viết, bi&ecirc;n dịch, gỡ lỗi so với c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh kh&aacute;c. Java cũng cho ph&eacute;p bạn tạo m&atilde; c&oacute; thể t&aacute;i sử dụng v&agrave; tạo c&aacute;c chương tr&igrave;nh c&oacute; t&iacute;nh&nbsp;m&ocirc;-đun.</span></li>\r\n</ul>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">V&igrave; lẽ đ&oacute;,&nbsp;<strong>Sự nghiệp Lập tr&igrave;nh Java</strong>&nbsp;được nhiều người quan t&acirc;m v&agrave; m&igrave;nh biết bạn cũng thế.</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Gi&aacute;o dục, tr&igrave;nh độ cần thiết của Lập tr&igrave;nh vi&ecirc;n Java l&agrave; g&igrave;?</strong></span></h2>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Giáo dục, trình độ cần thiết của Lập trình viên Java là gì?\" src=\"https://niithanoi.edu.vn/pic/News/images/tin-tuc-cong-nghe-va-lap-trinh/giao-duc-kien-thuc-de-tro-thanh-lap-trinh-vien-java.jpg\" style=\"height:314px; width:600px\" /><br />\r\n<br />\r\n<em>Gi&aacute;o dục, tr&igrave;nh độ cần thiết của Lập tr&igrave;nh vi&ecirc;n Java</em></span></p>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">Để được xem x&eacute;t cho vị tr&iacute; tuyển dụng Lập tr&igrave;nh vi&ecirc;n Java, bạn cần phải c&oacute; bằng cử nh&acirc;n Khoa học m&aacute;y t&iacute;nh, hệ thống th&ocirc;ng tin, to&aacute;n học hoặc kiến thức tương đương.<br />\r\n<br />\r\n<br />\r\nT&ugrave;y theo một số vị tr&iacute; / c&ocirc;ng ty cũng sẽ cầu cấp độ cao hơn.<br />\r\n<br />\r\n<br />\r\nNhưng với một số kiến thức cơ bản về lập tr&igrave;nh Java, việc t&igrave;m một c&ocirc;ng việc ở cấp độ thấp tại một c&ocirc;ng ty sẽ tương đối dễ d&agrave;ng.<br />\r\n<br />\r\n<br />\r\nHiện nay, nhiều nh&agrave; tuyển dụng c&oacute; thể chấp nhận ứng vi&ecirc;n kh&ocirc;ng c&oacute; tr&igrave;nh độ gi&aacute;o dục ch&iacute;nh quy trong nếu bạn c&oacute; thể chứng minh kinh nghiệm v&agrave; kiến ​​thức về Java của m&igrave;nh.<br />\r\n<br />\r\n<br />\r\nTuy nhi&ecirc;n, bạn c&agrave;ng c&oacute; nhiều kiến ​​thức v&agrave; kinh nghiệm, bạn c&agrave;ng c&oacute;&nbsp;<u>lợi thế đ&agrave;m ph&aacute;n lương</u>&nbsp;với nh&agrave; tuyển dụng.</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Con đường sự nghiệp Lập tr&igrave;nh Java</strong></span></h2>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Con đường sự nghiệp Lập trình viên Java\" src=\"https://niithanoi.edu.vn/pic/News/images/tin-tuc-cong-nghe-va-lap-trinh/con-duong-su-nghiep-lap-trinh-vien-java.jpg\" style=\"height:314px; width:600px\" /><br />\r\n<br />\r\n<em>Con đường sự nghiệp Lập tr&igrave;nh vi&ecirc;n Java</em></span><br />\r\n&nbsp;</p>\r\n\r\n<p><br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Java</strong>&nbsp;l&agrave; một ng&ocirc;n ngữ lập tr&igrave;nh chung số 1. Dưới đ&acirc;y l&agrave; những l&yacute; do:</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<ol>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Java l&agrave; nền tảng độc lập. Bất kể bạn viết chương tr&igrave;nh Java ở đ&acirc;u, n&oacute; sẽ được thực thi tr&ecirc;n bất kỳ hệ điều h&agrave;nh (HĐH) n&agrave;o với bất kỳ bộ xử l&yacute; n&agrave;o.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Java l&agrave; m&atilde; nguồn mở</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Java hỗ trợ c&aacute;c ứng dụng web động.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c t&iacute;nh năng Java kh&aacute;c nhau như Java Applet, JSP, JavaBeans, JDBC cho cơ sở dữ liệu, v.v ... mang lại cho Java một lợi thế so với c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh kh&aacute;c.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Java l&agrave; ng&ocirc;n ngữ an to&agrave;n v&agrave; mạnh mẽ nhất. Xử l&yacute; lỗi trong Java mạnh hơn nhiều so với C / C ++. Java t&igrave;m thấy hầu hết c&aacute;c lỗi tại thời gian bi&ecirc;n dịch.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Java c&oacute; sẵn bộ thu gom r&aacute;c.</span></li>\r\n</ol>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">Java l&agrave; ng&ocirc;n ngữ lập tr&igrave;nh hướng đối tượng (OOP), thể hiện r&otilde; nhất t&iacute;nh hướng đối tượng. M&atilde; / chương tr&igrave;nh Java được thực thi bởi M&aacute;y ảo Java (JVM).<br />\r\n<br />\r\n<br />\r\nTr&igrave;nh bi&ecirc;n dịch Java chuyển đổi m&atilde; nguồn Java th&agrave;nh bytecode v&agrave; sau đ&oacute; với bytecode, tr&igrave;nh bi&ecirc;n dịch Just - In - Time sau đ&oacute; được chuyển đổi th&agrave;nh m&atilde; m&aacute;y.&nbsp;Sau đ&oacute;, m&atilde; m&aacute;y sẽ được thực thi.&nbsp;(Ch&iacute;nh l&agrave; cơ chế n&agrave;y khiến Java độc lập với nền tảng).<br />\r\n<br />\r\n<br />\r\nĐ&acirc;y l&agrave; những phẩm chất của Java mang lại lợi thế cho c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh kh&aacute;c.<br />\r\n<br />\r\n<br />\r\nV&igrave; thế, nếu bạn học lập tr&igrave;nh Java, bạn sẽ c&oacute; nhiều cơ hội hơn tr&ecirc;n con đường sự nghiệp của m&igrave;nh.<br />\r\n<br />\r\n<br />\r\nVề sự nghiệp trong Java, sẽ tốt hơn nếu bạn c&oacute; nhiều kiến ​​thức kh&ocirc;ng chỉ về Java m&agrave; c&ograve;n về c&aacute;c c&ocirc;ng nghệ kh&aacute;c, v&iacute; dụ:</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Android</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Java UI</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">...</span></li>\r\n</ul>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">Java l&agrave; ng&ocirc;n ngữ lập tr&igrave;nh chung, do đ&oacute;, để thực sự ứng dụng Java v&agrave;o để l&agrave;m việc thực tế, ch&uacute;ng ta cần học th&ecirc;m những ứng dụng của n&oacute;.<br />\r\n<br />\r\n<br />\r\nV&agrave; lợi &iacute;ch khi c&oacute; thể l&agrave;m việc với nhiều c&ocirc;ng nghệ đ&oacute; ch&iacute;nh l&agrave; mức lương cao hơn, vị tr&iacute; cao hơn.</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Một Lập tr&igrave;nh vi&ecirc;n Java n&ecirc;n học những g&igrave;?</strong></span></h3>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">L&agrave; một Lập tr&igrave;nh vi&ecirc;n Java, bạn hướng tới ph&aacute;t c&oacute; thể ph&aacute;t triển nhiều loại sản phẩm ứng dụng kh&aacute;c nhau n&ecirc;n biết c&agrave;ng nhiều c&agrave;ng tốt. Tuy nhi&ecirc;n, cũng c&oacute; một số kiến thức cốt l&otilde;i cần phải nắm thật chắc:</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Kh&aacute;i niệm cơ bản của Java</strong>: Lập tr&igrave;nh Hướng đối tượng, Xử l&yacute; ngoại lệ, Đa luồng</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Ng&ocirc;n ngữ ph&iacute;a m&aacute;y kh&aacute;ch</strong>: HTML, CSS v&agrave; Javascript</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Cơ sở dữ liệu</strong>: Bất kỳ hệ quản trị cơ sở dữ liệu n&agrave;o (SQL, MySQL, MongoDB...)</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Framework</strong>: JSP &amp; Servlet, Spring, Hibernate, Struts, JSF ...</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Kiến thức cơ bản về c&aacute;c c&ocirc;ng nghệ phần mềm trung gian như EJB, RMI l&agrave; đủ.</span></li>\r\n</ul>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c kỹ năng được đề cập ở tr&ecirc;n l&agrave; bắt buộc hoặc &iacute;t nhất một số kỹ năng cần thiết cho CV của bạn trước khi bạn ứng tuyển một vị tr&iacute; Lập tr&igrave;nh vi&ecirc;n Java n&agrave;o đ&oacute;.<br />\r\n<br />\r\n<br />\r\nNote: Nếu bạn muốn bắt đầu với vị tr&iacute; thấp hơn như học việc hoặc thực tập sinh th&igrave; kiến thức về Java cơ bản đ&atilde; tạm đủ.</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><tt><u><strong><a href=\"https://niithanoi.edu.vn/lap-trinh-java.html\">Tự học Lập tr&igrave;nh Java cơ bản</a></strong></u>&nbsp;ngay!</tt></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><tt>C&ograve;n nếu bạn muốn sự nghiệp Lập tr&igrave;nh vi&ecirc;n Java của m&igrave;nh vững chắc hơn, đầy đủ hơn th&igrave;&nbsp;<u><strong><a href=\"https://niithanoi.edu.vn/khoa-hoc-java-fullstack.html\">Kh&oacute;a học Java Fullstack</a></strong></u>&nbsp;sẽ gi&uacute;p bạn.</tt></span></p>\r\n\r\n<p><br />\r\n&nbsp;</p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>C&aacute;c vị tr&iacute; c&ocirc;ng việc Lập tr&igrave;nh Java bạn c&oacute; thể tham gia</strong></span></h3>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Java</strong>&nbsp;đ&atilde; bắt đầu một kỷ nguy&ecirc;n mới cho lĩnh vực CNTT, Lập tr&igrave;nh. Đ&atilde; c&oacute; một số sản phẩm kh&ocirc;ng thể tin được v&agrave; nhiều dịch vụ được sinh ra bằng c&aacute;ch sử dụng Java.<br />\r\n<br />\r\n<br />\r\nC&aacute;c nh&agrave; ph&aacute;t triển / lập tr&igrave;nh vi&ecirc;n Java c&oacute; một vai tr&ograve; to lớn trong việc thay đổi thị trường CNTT.<br />\r\n<br />\r\n<br />\r\nJava t&aacute;c động đến mọi thứ, từ t&iacute;nh di động (tức l&agrave; Android) đến phần mềm trung gian (tức l&agrave; Hadoop) v&agrave; do đ&oacute;, Java c&oacute; một tương lai ph&aacute;t triển d&agrave;i ở ph&iacute;a trước.<br />\r\n<br />\r\n<br />\r\nVề cơ bản, Java kh&ocirc;ng chỉ l&agrave; ng&ocirc;n ngữ lập tr&igrave;nh m&agrave; n&oacute; l&agrave; nền tảng tạo n&ecirc;n hệ sinh th&aacute;i Java rộng lớn.<br />\r\n<br />\r\n<br />\r\nĐiều quan trọng đối với ng&agrave;nh c&ocirc;ng nghệ th&ocirc;ng tin l&agrave; ph&aacute;t triển v&agrave; tạo ra nhiều ứng dụng dựa tr&ecirc;n web hoặc m&aacute;y chủ để n&acirc;ng cao năng lực cạnh tranh. Java đ&aacute;p ứng rất tốt cho c&aacute;c nhiệm vụ n&agrave;y.<br />\r\n<br />\r\n<br />\r\nL&agrave; một nghề nghiệp cho c&aacute;c lập tr&igrave;nh vi&ecirc;n Java, bất kỳ ai cũng c&oacute; thể xem x&eacute;t tham gia với c&aacute;c vai tr&ograve; sau:</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Lập tr&igrave;nh vi&ecirc;n Web&nbsp;<em>(Lưu &yacute;: Nếu bạn l&agrave; người mới t&igrave;m hiểu về Lập tr&igrave;nh Web. C&oacute; thể bạn n&ecirc;n&nbsp;xem qua&nbsp;<strong><a href=\"https://niithanoi.edu.vn/lo-trinh-hoc-lap-trinh-web.html\">Lộ tr&igrave;nh học Lập tr&igrave;nh Web</a></strong>&nbsp;trước khi quyết định xem học lập tr&igrave;nh&nbsp;ở đ&acirc;u.)</em></span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Lập tr&igrave;nh vi&ecirc;n Ứng dụng di động</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Lập tr&igrave;nh vi&ecirc;n EJB</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Lập tr&igrave;nh phần mềm</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Kiểm thử vi&ecirc;n</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">...</span></li>\r\n</ul>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">Hoặc</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Trở th&agrave;nh gi&aacute;o vi&ecirc;n dạy lập tr&igrave;nh Java</span></li>\r\n</ul>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c vị tr&iacute; được đề cập ở tr&ecirc;n l&agrave; một số c&ocirc;ng việc lập tr&igrave;nh Java m&agrave; bạn c&oacute; thể mong đợi khi học l&agrave;m chủ ng&ocirc;n ngữ Java.</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Lương Lập tr&igrave;nh vi&ecirc;n Java</strong></span></h2>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Lương Lập trình viên Java\" src=\"https://niithanoi.edu.vn/pic/News/images/tin-tuc-cong-nghe-va-lap-trinh/luong-lap-trinh-vien-java.jpg\" style=\"height:314px; width:600px\" /><br />\r\n<br />\r\n<em>Lương Lập tr&igrave;nh vi&ecirc;n Java</em></span></p>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">Bảng dưới đ&acirc;y cho thấy&nbsp;<strong>mức lương trung b&igrave;nh</strong>&nbsp;/ năm của&nbsp;<strong>c&aacute;c&nbsp;vị tr&iacute; kh&aacute;c nhau</strong>&nbsp;của Lập tr&igrave;nh vi&ecirc;n Java (Dữ liệu tại Mỹ)</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Hoặc bạn c&oacute; thể tham khảo&nbsp;<strong><a href=\"https://niithanoi.edu.vn/luong-lap-trinh-vien.html\">Lương lập tr&igrave;nh vi&ecirc;n Java Việt Nam</a></strong></span></li>\r\n</ul>\r\n\r\n<p><br />\r\n&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Vị tr&iacute;</strong></span></td>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">Lương TB / Năm</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">Programmer</span></td>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">$72,375</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">Programmer Analyst</span></td>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">$72,499</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">Java Developer</span></td>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">$103,056</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">Senior Java Developer</span></td>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">$120,504</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">Senior Programmer Analyst</span></td>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">$90,864</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">Application Developer</span></td>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">$84,968</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">System Programmer</span></td>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">$68,652</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">Computer Programmer</span></td>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">$57,393</span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">Web Developer</span></td>\r\n			<td><span style=\"font-family:Arial,Helvetica,sans-serif\">$71,246</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Bảng dữ liệu tham khảo thống k&ecirc; của&nbsp;<strong><a href=\"https://www.indeed.com/salaries/Java-Programmer-Salaries\" rel=\"nofolow\">Indeed</a></strong></em></span></p>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">Dữ liệu n&agrave;y cũng được sử dụng để tham khảo trong trường hợp bạn l&agrave;m việc tại c&aacute;c c&ocirc;ng ty của Mỹ hoặc Outsourcing cho c&aacute;c c&ocirc;ng ty Mỹ th&igrave; thu nhập c&oacute; thể tương đương hoặc gần bằng.</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Lời kết về Con đường sự nghiệp Lập tr&igrave;nh vi&ecirc;n Java</strong></span></h2>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&ocirc;ng thể phủ nhận thực tế rằng Java l&agrave; nh&acirc;n tố quan trọng nhất đ&atilde; thay đổi mạnh mẽ ng&agrave;nh CNTT sau C ++. V&agrave; l&agrave; một&nbsp;<strong>Lập tr&igrave;nh vi&ecirc;n Java</strong>, bạn n&ecirc;n tự h&agrave;o v&igrave; điều đ&oacute;.<br />\r\n<br />\r\n<br />\r\nJava đ&atilde; c&oacute; một h&agrave;nh tr&igrave;nh lừng lẫy kể từ khi được sinh ra v&agrave; l&agrave; trụ cột của nhiều sản phẩm v&agrave; dịch vụ mới khiến cả thế giới ngạc nhi&ecirc;n.<br />\r\n<br />\r\n<br />\r\nV&igrave; vậy, n&oacute; c&agrave;ng trở n&ecirc;n quan trọng v&agrave; cần thiết hơn.<br />\r\n<br />\r\n<br />\r\n<strong>Về tương lai của Java</strong>: Java vẫn đang được&nbsp;<strong><a href=\"https://www.oracle.com/index.html\">Oracle</a></strong>&nbsp;v&agrave; cộng đồng Java li&ecirc;n tục ph&aacute;t triển, n&oacute; được đan xen với lối suy nghĩ nhanh nhẹn v&agrave; tinh gọn sẽ cho ph&eacute;p cộng đồng Java tiếp tục đổi mới v&agrave; cung cấp c&aacute;c hệ thống, ứng dụng, phần mềm chất lượng đ&aacute;p ứng nhu cầu kinh doanh.</span><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">M&igrave;nh tin rằng c&aacute;c lập tr&igrave;nh vi&ecirc;n Java sẽ&nbsp;gi&uacute;p th&uacute;c đẩy sự ph&aacute;t triển của CNTT từ c&aacute;c hệ thống bị c&ocirc; lập sang c&aacute;c hệ thống c&oacute; thể&nbsp;hợp t&aacute;c.</span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Java được nh&uacute;ng v&agrave;o nhiều hệ thống CNTT quan trọng tr&ecirc;n thế giới v&agrave; c&oacute; vị tr&iacute; tốt để đ&oacute;ng g&oacute;p một phần v&agrave;o sự đổi mới trong tương lai.</span></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Phần tốt nhất về Java l&agrave;, hệ thống sinh th&aacute;i của Java l&agrave; tự duy tr&igrave;, từ t&iacute;nh di động đến phần mềm trung gian, n&oacute; t&aacute;c động đến mọi thứ v&agrave; cũng sẽ tiếp tục g&acirc;y ảnh hưởng lớn trong tương lai.</span></li>\r\n</ul>\r\n\r\n<p><br />\r\n<br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu bạn muốn đ&oacute;ng g&oacute;p phần n&agrave;o đ&oacute; cho nền CNTT hay chỉ đơn giản l&agrave; muốn c&oacute; một&nbsp;<strong>Sự nghiệp Lập tr&igrave;nh Java tươi s&aacute;ng</strong>&nbsp;th&igrave; h&atilde;y&nbsp;<strong>Học Lập tr&igrave;nh Java</strong>&nbsp;ngay h&ocirc;m nay!</span></p>\r\n', 'con-duong-su-nghiep-lap-trinh-vien-java.jpg', 1, 1, 1, 136, 3.625, '2020-04-10 15:21:54', '2020-07-08 06:12:27'),
(5, 'C sharp là gì? Vì sao C# lại trở thành ngôn ngữ lập trình phổ biến', '<p>C sharp viết l&agrave; C# - l&agrave; một ng&ocirc;n ngữ lập tr&igrave;nh hướng đối tượng ti&ecirc;n tiến được Anders Hejlsberg ph&aacute;t triển v&agrave;o năm 2000 tại Microsoft v&agrave; được xem l&agrave; đối thủ của Java (một ng&ocirc;n ngữ lập tr&igrave;nh tương đối giống C#).</p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://techvccloud.mediacdn.vn/zoom/650_406/2018/12/14/78058b8225-1544786826983377727560-crop-15447868320501842021413.jpg\" title=\"C sharp là gì? Vì sao C# là một trong những ngôn ngữ lập trình phổ biến nhất trên thế giới?\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>C sharp</strong>&nbsp;viết l&agrave;&nbsp;<strong>C#</strong>&nbsp;- l&agrave; một ng&ocirc;n ngữ lập tr&igrave;nh hướng đối tượng ti&ecirc;n tiến được Anders Hejlsberg ph&aacute;t triển v&agrave;o năm 2000 tại Microsoft v&agrave; được xem l&agrave; đối thủ của Java (một ng&ocirc;n ngữ lập tr&igrave;nh tương đối giống C#). Ng&ocirc;n ngữ n&agrave;y ra đời l&agrave; bởi Sun, (được&nbsp;<a href=\"https://tech.vccloud.vn/oracle-la-gi-20181115154434768.htm\" target=\"_blank\" title=\"Oracle\"><strong>Oracle</strong></a>&nbsp;mua lại sau n&agrave;y) kh&ocirc;ng muốn Microsoft c&oacute; bất cứ thay đổi g&igrave; đối với&nbsp;<a href=\"https://tech.vccloud.vn/so-bo-ve-kien-truc-cua-java-20180116193111008.htm\" target=\"_blank\" title=\"ngôn ngữ Java\"><strong>ng&ocirc;n ngữ Java</strong></a>, v&igrave; vậy Microsoft đ&atilde; quyết định tạo ra một ng&ocirc;n ngữ của ri&ecirc;ng họ.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>C#</strong>&nbsp;đ&atilde; ph&aacute;t triển nhanh ch&oacute;ng kể từ lần đầu ti&ecirc;n được viết ra, nhờ sự hậu thuẫn từ t&ecirc;n tuổi của Microsoft, ng&ocirc;n ngữ n&agrave;y đ&atilde; đạt được một số lượng người sử dụng kh&aacute; đ&ocirc;ng đảo. V&agrave; hiện nay, C# đ&atilde; trở th&agrave;nh một trong những ng&ocirc;n ngữ lập tr&igrave;nh phổ biến nhất tr&ecirc;n thế giới.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://tech.vccloud.vn/c-sharp-la-gi-20181214182955977.htm\" title=\"C sharp hay C# là gì?\">C sharp hay C# l&agrave; g&igrave;?</a></span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y l&agrave; ng&ocirc;n ngữ dạng chung, phổ th&ocirc;ng được thiết kế để ph&aacute;t triển ứng dụng tr&ecirc;n nền tảng Microsoft v&agrave; cần c&oacute; .NET framework tr&ecirc;n Windows để hoạt động. C# c&oacute; thể được xem l&agrave; một sự kết hợp giao thoa giữa C v&agrave; C++, tận dụng những yếu tố tốt nhất của 2 ng&ocirc;n ngữ n&agrave;y để tạo ra một ng&ocirc;n ngữ c&oacute; t&iacute;nh hiện đại hơn. Mặc d&ugrave; .NET framework cũng hỗ trợ một số ng&ocirc;n ngữ m&atilde; h&oacute;a kh&aacute;c, C# đ&atilde; nhanh ch&oacute;ng trở th&agrave;nh một trong những ng&ocirc;n ngữ phổ biến nhất.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&gt;&gt; Tham khảo th&ecirc;m:&nbsp;<a href=\"https://tech.vccloud.vn/net-framework-la-gi-cac-khai-niem-co-ban-ve-net-framework-20180912090957839.htm\" target=\"_blank\" title=\".NET framework là gì? Các khái niệm cơ bản về .NET framework\"><em><strong>.NET framework l&agrave; g&igrave;? C&aacute;c kh&aacute;i niệm cơ bản về .NET framework</strong></em></a></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://techvccloud.mediacdn.vn/2018/12/14/1515659537-81054-3-1-15447869163442092986844.png\" target=\"_blank\" title=\"\"><img alt=\"C sharp là gì? Vì sao C# là một trong những ngôn ngữ lập trình phổ biến nhất trên thế giới? - Ảnh 1.\" id=\"img_65143a60-ff93-11e8-ad12-cdea564e1fdb\" src=\"https://techvccloud.mediacdn.vn/2018/12/14/1515659537-81054-3-1-15447869163442092986844.png\" title=\"C sharp là gì? Vì sao C# là một trong những ngôn ngữ lập trình phổ biến nhất trên thế giới? - Ảnh 1.\" /></a></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C# c&oacute; thể sử dụng để tạo bất kỳ loại ứng dụng n&agrave;o nhưng ng&ocirc;n ngữ n&agrave;y đặc biệt c&oacute; ưu thế trong việc x&acirc;y dựng c&aacute;c ứng dụng v&agrave; tr&ograve; chơi tr&ecirc;n Windows. C# cũng c&oacute; thể th&iacute;ch hợp cho ph&aacute;t triển c&aacute;c ứng dụng web v&agrave; ng&agrave;y c&agrave;ng trở n&ecirc;n phổ biến trong ph&aacute;t triển tr&ecirc;n nền tảng di động. C&aacute;c c&ocirc;ng cụ đa nền tảng như Xamarin cho ph&eacute;p c&aacute;c ứng dụng được viết bằng C# cũng sử dụng được tr&ecirc;n hầu hết c&aacute;c thiết bị di động.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">C sharp trong ph&aacute;t triển Game</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C# được sử dụng rộng r&atilde;i cho tạo game tr&ecirc;n c&ocirc;ng cụ game Unity - c&ocirc;ng cụ game phổ biến nhất hiện nay. Hơn một phần ba số tr&ograve; chơi thuộc hang top game đều được tạo bởi Unity v&agrave; c&oacute; khoảng 770 triệu người chơi tr&ecirc;n thế giới đang sử dụng c&aacute;c game được tạo bằng c&ocirc;ng cụ Unity. Unity cũng được sử dụng cho c&ocirc;ng nghệ VR (c&ocirc;ng nghệ thực tế ảo), trong đ&oacute;, 90% tất cả Samsung Gear v&agrave; 53% tất cả c&aacute;c tr&ograve; chơi Oculus Rift VR đều được ph&aacute;t triển tr&ecirc;n Unity.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>C#</strong>&nbsp;l&agrave; một c&ocirc;ng cụ rất phổ biến để tạo c&aacute;c ứng dụng dạng n&agrave;y, v&agrave; do đ&oacute;, l&agrave; một lựa chọn tuyệt vời cho bất kỳ lập tr&igrave;nh vi&ecirc;n n&agrave;o muốn gia nhập ng&agrave;nh c&ocirc;ng nghiệp ph&aacute;t triển tr&ograve; chơi hay bất kỳ ai quan t&acirc;m đến c&ocirc;ng nghệ thực tế ảo.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://www.vccloud.vn/?utm_source=techblog&amp;utm_medium=banner696x100&amp;utm_campaign=homepage&amp;utm_content=register\" rel=\"nofollow\" target=\"_blank\"><img alt=\"C sharp là gì? Vì sao C# là một trong những ngôn ngữ lập trình phổ biến nhất trên thế giới? - Ảnh 2.\" id=\"img_85b57fe0-ff93-11e8-bb9b-1b89a8610342\" src=\"https://techvccloud.mediacdn.vn/2018/12/14/banner10-696x100px-15447869702251826953641.gif\" title=\"C sharp là gì? Vì sao C# là một trong những ngôn ngữ lập trình phổ biến nhất trên thế giới? - Ảnh 2.\" /></a></span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">C sharp tương đối dễ học nhưng cũng l&agrave; một ng&ocirc;n ngữ kh&aacute; phức tạp</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>C#</strong>&nbsp;c&oacute; nhiều t&iacute;nh năng tương đối dễ học. C# l&agrave; một ng&ocirc;n ngữ cấp cao, tương đối dễ đọc, trong đ&oacute; nhiều t&aacute;c vụ được xem l&agrave; phức tạp nhất đ&atilde; được đ&uacute;c kết lại, v&igrave; vậy lập tr&igrave;nh vi&ecirc;n kh&ocirc;ng cần phải qu&aacute; lo lắng khi t&igrave;m hiểu.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>V&iacute; dụ</strong>, việc quản l&yacute; bộ nhớ kh&ocirc;ng c&ograve;n l&agrave; tr&aacute;ch nhiệm của người d&ugrave;ng nữa v&agrave; sẽ được lược đồ thu gom r&aacute;c .NET xử l&yacute;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://techvccloud.mediacdn.vn/2018/12/14/codec-1544786947332727112705.jpeg\" target=\"_blank\" title=\"\"><img alt=\"C sharp là gì? Vì sao C# là một trong những ngôn ngữ lập trình phổ biến nhất trên thế giới? - Ảnh 3.\" id=\"img_778f1020-ff93-11e8-b75f-7d8f64d18748\" src=\"https://techvccloud.mediacdn.vn/2018/12/14/codec-1544786947332727112705.jpeg\" title=\"C sharp là gì? Vì sao C# là một trong những ngôn ngữ lập trình phổ biến nhất trên thế giới? - Ảnh 3.\" /></a></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C# &nbsp;cũng l&agrave; một ng&ocirc;n ngữ g&otilde; tĩnh, do đ&oacute;, code sẽ được kiểm tra trước khi chuyển th&agrave;nh ứng dụng. Điều n&agrave;y gi&uacute;p developer dễ d&agrave;ng hơn trong ph&aacute;t hiện lỗi, v&agrave; cũng đặc biệt hữu &iacute;ch cho người mới bắt đầu. Mặc d&ugrave; c&uacute; ph&aacute;p của C# nhất qu&aacute;n v&agrave; logic hơn C++, nhưng vẫn c&ograve;n rất nhiều điều ở C# cần t&igrave;m hiểu v&agrave; học hỏi. C# l&agrave; một ng&ocirc;n ngữ kh&aacute; phức tạp v&agrave; để th&agrave;nh thạo bạn c&oacute; thể sẽ mất nhiều thời gian hơn so với c&aacute;c ng&ocirc;n ngữ đơn giản như Python. Người d&ugrave;ng sẽ cần phải t&igrave;m hiểu về một lượng code đ&aacute;ng kể để tạo c&aacute;c chương tr&igrave;nh n&acirc;ng cao, v&agrave; điều n&agrave;y c&oacute; thể l&agrave; trở ngai cho một số người d&ugrave;ng mới.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&gt;&gt; T&igrave;m hiểu th&ecirc;m:&nbsp;<a href=\"https://tech.vccloud.vn/python-la-gi-tai-sao-lai-chon-python-20180403100334553.htm\" target=\"_blank\" title=\"Python là gì? Tại sao lại chọn Python?\"><em><strong>Python l&agrave; g&igrave;? Tại sao lại chọn Python?</strong></em></a></span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">So s&aacute;nh t&iacute;nh năng C v&agrave; C++ v&agrave; C#</span></h2>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C++</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C#</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Procedural (tu&acirc;n thủ thủ tục)</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỗ trợ hướng đối tượng</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỗ trợ hướng đối tượng</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cấp độ abstract thấp nhất</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cấp độ abstract thấp</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cấp độ abstract cao</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Quản l&yacute; bộ nhớ thủ c&ocirc;ng</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Quản l&yacute; bộ nhớ thủ c&ocirc;ng</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cơ chế thu gom r&aacute;c</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Dung lượng rất nhẹ, c&oacute; tr&igrave;nh bi&ecirc;n dịch</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Dung lượng nhẹ, c&oacute; tr&igrave;nh bi&ecirc;n dịch</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; thể th&ocirc;ng dịch sang bytecode, bi&ecirc;n dịch được bởi CLR, c&aacute;c nhị ph&acirc;n lớn hơn</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiệu suất nhanh, ưu việt</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tương đương với C</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiệu suất b&igrave;nh thường</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; thể code cho bất kỳ loại nền tảng n&agrave;o</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; thể code cho bất kỳ loại nền tảng n&agrave;o</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hướng tới hệ điều h&agrave;nh Windows</span></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cho ph&eacute;p code gần như tất cả ứng dụng, c&uacute; ph&aacute;p chuẩn</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cho ph&eacute;p code gần như tất cả ứng dụng, c&uacute; ph&aacute;p chuẩn</span></p>\r\n			</td>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiển thị cảnh b&aacute;o cho tr&igrave;nh bi&ecirc;n dịch để giảm lỗi nghi&ecirc;m trọng</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Theo BizFly Cloud tổng hợp</em></span></p>\r\n', 'Cshap.jpg', 1, 3, 1, 587, NULL, '2020-04-10 15:22:58', '2020-07-15 15:43:42');
INSERT INTO `posts` (`id`, `title`, `description`, `contents`, `thumbnail`, `user_id`, `category_id`, `status`, `view`, `rate`, `created_at`, `updated_at`) VALUES
(6, 'Angular 8.0 – Có gì mới? Cách nâng cấp ra sao?', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&uacute;ng ta đều biết Google vừa ra mắt phi&ecirc;n bản mới của Angular 8.0. C&aacute;c nh&agrave; ph&aacute;t triển Angular v&agrave; cộng đồng đ&atilde; đợi bản update n&agrave;y từ l&acirc;u. Angular 8 sẽ ph&aacute;t h&agrave;nh với c&aacute;c t&iacute;nh năng của IVY engine.</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">T&iacute;nh năng mới trong Angular 8</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỗ trợ TypeScript 3.4.x</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Angular 8 hỗ trợ bản TypeScript 3.4 hoặc cao hơn. Nếu bạn muốn d&ugrave;ng Angular 8 cho ứng dụng của m&igrave;nh th&igrave; cần upgrade TypeScript l&ecirc;n 3.4 hoặc cao hơn.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">IVY Rendering Engine</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">T&iacute;nh năng quan trọng v&agrave; được mong chờ nhất của Angular 8 l&agrave; IVY render engine. IVY l&agrave; tr&igrave;nh bi&ecirc;n dịch Angular hoạt động như một cơ chế render mới. Lợi &iacute;ch của IVY l&agrave; tạo ra c&aacute;c bundle nhỏ c&oacute; thể th&ecirc;m&nbsp;dễ d&agrave;ng. IVY cơ bản l&agrave; một cải tiến của Angular. Sau khi chuyển sang IVY th&igrave; ứng dụng đ&atilde; c&oacute; vẫn l&agrave;m việc như trước đ&acirc;y nhưng giảm k&iacute;ch thước bundle. Trong Angular 8, Google giới thiệu bản preview cho IVY. Mục ti&ecirc;u ch&iacute;nh của bản n&agrave;y l&agrave; nhận phản hồi từ c&aacute;c Developer li&ecirc;n quan tới Ivy. N&oacute; được khuyến nghị kh&ocirc;ng d&ugrave;ng cho m&ocirc;i trường production.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><em><img src=\"https://tedu.com.vn/uploaded/images/062019/A01_03_Ivy_Render_Engine.jpg\" style=\"height:334px; width:600px\" /></em></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Figure 1: K&iacute;ch thước Bundle của ứng dụng Hello World với Angular Ivy v&agrave; kh&ocirc;ng c&oacute; Ivy. (nguồn ngconf 2019 Keynote bởi Brad Green v&agrave; Igor Minar)</em></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tạo ngconf 2019, Brad Green, Techincal Director của Angular Team tạo Google n&oacute;i Ivy sẽ c&oacute; một cải tiến đ&aacute;ng kể li&ecirc;n quan đến k&iacute;ch thước bundle v&agrave; tương th&iacute;ch với t&iacute;nh năng&nbsp;Differnent Loading. Ch&uacute;ng ta sẽ c&oacute; c&aacute;c lợi &iacute;ch sau đ&acirc;y khi sử dụng Ivy:</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">N&oacute; sẽ gi&uacute;p bi&ecirc;n dịch nhanh hơn (dự kiến ph&aacute;t h&agrave;nh ở Angular 9)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhiều cải tiến ki&ecirc;n quan đến check type trong template v&agrave; bắt lỗi trong thời gian build v&agrave; gi&uacute;p&nbsp;người d&ugrave;ng giảm&nbsp;gặp lỗi ở giai đoạn runtime.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Bundle size nhỏ hơn so với hiện tại</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">N&oacute; cũng c&oacute; thể tương th&iacute;ch ngược với c&aacute;c ứng dụng đ&atilde; ph&aacute;t triển trước đ&acirc;y.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Code được tạo bởi Angular Complier sẽ dễ d&agrave;ng để đọc hiểu hơn.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn c&oacute; thể debug cả template, t&iacute;nh năng n&agrave;y chắc chắn sẽ l&agrave;m rất nhiều developer th&iacute;ch th&uacute;.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">V&agrave; nếu bạn muốn sử dụng Ivy trong ứng dụng mới của m&igrave;nh, bạn c&oacute; thể tạo mới project với option enable-ivy</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>ng new ivy-project --enable-ivy  </code></span></pre>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&acirc;u lệnh tr&ecirc;n tạo mới Angular CLI sẽ lưu cấu h&igrave;nh trong tsconfig.json file</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>&quot;angularCompilerOptions&quot;: {   \r\n&quot;enableIvy&quot;: true  \r\n} </code></span></pre>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cấu h&igrave;nh tr&ecirc;n c&oacute; thể th&ecirc;m bằng tay v&agrave;o bất cứ ứng dụng vũ n&agrave;o sau khi upgrade l&ecirc;n Angular 8. Một khuyến nghị l&agrave; nếu bạn muốn d&ugrave;ng Ivy trong ứng dụng, bạn c&oacute; thể chạy ứng dụng trong mode debug với mode AOT mode.</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>ng&nbsp;serve&nbsp;--aot&nbsp;</code></span></pre>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỗ trợ Bazel</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong Angular 8, Google giới thiệu một c&ocirc;ng cụ build mới l&agrave; Bazel. Thực tế n&oacute; kh&ocirc;ng phải l&agrave; c&ocirc;ng cụ mới ho&agrave;n to&agrave;n. Google sử dụng c&ocirc;ng cụ n&agrave;y nội bộ từ l&acirc;u v&agrave; hiện tại họ ph&aacute;t h&agrave;nh n&oacute; như l&agrave; một m&atilde; nguồn mở. Nhưng một thứ cần phải l&agrave;m r&otilde; l&agrave; Bazel chưa thực sự sẵn s&agrave;ng tr&ecirc;n Angular 8. N&oacute; được giới thiệu như l&agrave; một t&iacute;nh năng nhỏ trong Angular 8 v&agrave; được mong chờ l&agrave; sẽ c&oacute; bản ch&iacute;nh thức trong Angular CLI 9. Ch&uacute;ng ta c&oacute; thể c&oacute; c&aacute;c lợi &iacute;ch sau khi sử dụng tool n&agrave;y:</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">N&oacute; sẽ gi&uacute;p build nhanh hơn. N&oacute; sẽ tốn time hơn cho lần đầu build nhưng sẽ nhanh hơn nhiều nếu như build song song.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">D&ugrave;ng c&ocirc;ng cụ n&agrave;y ch&uacute;ng ta c&oacute; thể build ứng dụng như l&agrave; một incremental build (build chỉ những g&igrave; thay đổi)&nbsp;v&agrave; deploy chỉ với những g&igrave; thay đổi hơn l&agrave; to&agrave;n bộ ứng dụng.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&uacute;ng ta c&oacute; thể ẩn file Bazel đi</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&uacute;ng ta c&oacute; thể th&ecirc;m Bazel v&agrave;o ứng dụng sử dụng lệnh:</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>ng&nbsp;add&nbsp;@angular/bazel&nbsp;&nbsp;</code></span></pre>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">V&agrave; c&oacute; thể tạo ứng dụng với t&ugrave;y chọn Bazel:</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>npm&nbsp;install&nbsp;-g&nbsp;@angular/bazel&nbsp;&nbsp;\r\nng&nbsp;new&nbsp;my-app&nbsp;--colection=@angular/bazel&nbsp;&nbsp;</code></span></pre>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Differential Loading cho việc tối ưu h&oacute;a hiệu năng</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Một trong những t&iacute;nh năng rất hay trong Angular CLI 8. Bởi v&igrave; n&oacute; gi&uacute;p ch&uacute;ng ta chỉ ra tr&igrave;nh duyệt n&agrave;o sẽ được target v&agrave; CLI sẽ build ứng dụng với c&aacute;c bundle JS li&ecirc;n quan cần thiết. Tr&igrave;nh duyệt target mặc định trong tsconfig.json file giờ l&agrave; es2015. Nghĩa l&agrave; khi CLI build ứng dụng, n&oacute; sẽ build ứng dụng trong một tr&igrave;nh duyệt cũ hơn như l&agrave; IE9 sau đ&oacute; ch&uacute;ng ta cần chỉ ra n&oacute; trong file browserlist. File n&agrave;y t&ograve;n tại trong thư mục gốc của ứng dụng v&agrave; sử dụng</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>#&nbsp;For&nbsp;IE&nbsp;9-11&nbsp;support,&nbsp;please&nbsp;remove&nbsp;&#39;not&#39;&nbsp;from&nbsp;the&nbsp;last&nbsp;line&nbsp;of&nbsp;the&nbsp;file&nbsp;and&nbsp;adjust&nbsp;as&nbsp;needed&nbsp;&nbsp;\r\n&gt;&nbsp;0.5%&nbsp;&nbsp;\r\nlast&nbsp;2&nbsp;versions&nbsp;&nbsp;\r\nFirefox&nbsp;ESR&nbsp;&nbsp;\r\nnot&nbsp;dead&nbsp;&nbsp;\r\nnot&nbsp;IE&nbsp;9-11&nbsp;&nbsp;</code></span></pre>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu ch&uacute;ng ta bỏ từ kh&oacute;a not ở d&ograve;ng cuối đi th&igrave; build process sẽ gen ra c&aacute;c file cho cả 2 version.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img src=\"https://tedu.com.vn/uploaded/images/062019/A01_01_Differential_Bundling.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Chỉ c&oacute; một nhược điểm của qu&aacute; tr&igrave;nh n&agrave;y l&agrave; sẽ mất nhiều thời gian build hơn. C&aacute;c tr&igrave;nh duyệt kh&aacute;c nhau c&oacute; thể sẽ cần quyết định xem bundle n&agrave;o sẽ được load. Cho mục đ&iacute;ch n&agrave;y, một sript reference được th&ecirc;m v&agrave;o index.html:</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>&nbsp;--&nbsp;For&nbsp;ES6&nbsp;support&nbsp;browser&nbsp;or&nbsp;latest&nbsp;browser&nbsp;&nbsp;\r\n&nbsp;&nbsp;\r\n&nbsp;--&nbsp;For&nbsp;es5&nbsp;support&nbsp;browser&nbsp;or&nbsp;old&nbsp;browser&nbsp;&nbsp;</code></span></pre>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">H&igrave;nh ảnh tr&ecirc;n ch&uacute;ng ta c&oacute; thể thấy bundle size giảm đi đ&aacute;ng kể trong trường hợp ES2015 từ 9% đến 20%. Cuối c&ugrave;ng th&igrave; bundle size nghĩa l&agrave; tối ưu hiệu năng. V&igrave; thế Angular sẽ build th&ecirc;m c&aacute;c file bổ sung với polyfills v&agrave; ch&uacute;ng sẽ được th&ecirc;m v&agrave;o trong ứng dụng</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><img src=\"https://tedu.com.vn/uploaded/images/062019/A01_02_Differential_Browser.jpg\" /></span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Thay đổi Lazy Loading trong Route</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong Angular từ khi khởi đầu cho đến nay, cơ chế router lu&ocirc;n hỗ trợ kh&aacute;i niệm lazy loading. Đến Angular 7 n&oacute; đ&atilde; được ho&agrave;n thiện:</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>{&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;path:&nbsp;&#39;lazy&#39;,&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;loadChildren:&nbsp;()&nbsp;=&gt;&nbsp;&#39;./admin/admin.module#AdminModule&#39;&nbsp;&nbsp;\r\n}&nbsp;</code></span></pre>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Gi&aacute; trị trước dấu # chỉ ra đường dẫn đến module file m&agrave; module đ&oacute; tồn tại, c&ograve;n sau dấu # chỉ ra t&ecirc;n của class Module. Phong c&aacute;ch n&agrave;y vẫn l&agrave;m việc trong Angular 8, nhưng c&aacute;ch để viết lazy module được thay đổi. Chuỗi gi&aacute; trị của loadChidren bị deprecated. Giờ loadChildren được khai b&aacute;o như sau:</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>{&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;path:&nbsp;&#39;lazy&#39;,&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;loadChildren:&nbsp;()&nbsp;=&gt;&nbsp;import(&#39;./admin/admin.module&#39;).then(m&nbsp;=&gt;&nbsp;m.AdminModule)&nbsp;&nbsp;\r\n}&nbsp;&nbsp;</code></span></pre>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỗ trợ Web Worker</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Như ch&uacute;ng ta đ&atilde; biết JavaScript lu&ocirc;n thực thi đơn thread. N&oacute; l&agrave; điều tốt khi thực thi lượng lớn dữ liệu hoặc gọi li&ecirc;n tiếp nhau trong phương ph&aacute;p bất đồng bộ. Nhưng theo kịch bản ứng dụng thực tế, kh&aacute;i niệm n&agrave;y kh&ocirc;ng gi&uacute;p g&igrave; nhiều. Đ&oacute; l&agrave; tại sao ng&agrave;y n&agrave;y tất cả c&aacute;c tr&igrave;nh duyệt web hỗ trợ web workder process. Cơ bản l&agrave; web worker process l&agrave; c&aacute;c script được thực thi bởi tr&igrave;nh duyệt trong một thread t&aacute;ch biệt. Giao tiếp với c&aacute;c thread đ&oacute; trong tr&igrave;nh duyệt sẽ c&oacute; thể gửi tin nhắn. Nh&igrave;n chung, web worker kh&ocirc;ng li&ecirc;n quan đến Angular nhưng điểm ch&iacute;nh l&agrave; c&aacute;c script n&agrave;y cần được xem x&eacute;t trong build process của ứng dụng. Mục ti&ecirc;u ch&iacute;nh l&agrave; cung cấp một bundle cho tất cả web worker. Giờ Angular 8 đ&atilde; thực hiện c&ocirc;ng việc n&agrave;y bởi Angular CLI.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Theo đố ch&uacute;ng ta c&oacute; thể cấu h&igrave;nh Angular CLI nếu ch&uacute;ng ta th&ecirc;m web worker cho lần đầu ti&ecirc;n. Trong qu&aacute; tr&igrave;nh n&agrave;y, CLI sẽ loại bỏ c&aacute;c worker.ts từ tsconfig.json v&agrave; th&ecirc;m c&aacute;c cấu h&igrave;nh TypeScript với t&ecirc;n l&agrave; tsconfig.worder.json để quản l&yacute; file worker.ts. C&Aacute;c th&ocirc;ng tin n&agrave;y cũng được th&ecirc;m v&agrave;o angular.json</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>&quot;webWorkerTsConfig&quot;:&nbsp;&quot;tsconfig.worker.json&quot;&nbsp;</code></span></pre>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Thu thập&nbsp;ph&acirc;n t&iacute;ch dữ liệu sử dụng</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Với Angular 8, Angluar CLI thu thập c&aacute;c dữ liệu ph&acirc;n t&iacute;ch cho Angular team c&oacute; thể sắp xếp ưu ti&ecirc;n c&aacute;c t&iacute;nh năng v&agrave; c&aacute;c cải tiến. Khi ch&uacute;ng ta update ứng dụng CLI, n&oacute; sẽ hỗ trợ t&ugrave;y chọn&nbsp;<strong>ng analytics</strong>. Nếu ch&uacute;ng ta cho ph&eacute;p n&oacute; chạy to&agrave;n bộ, n&oacute; sẽ thu thập dữ liệu như c&aacute;c lệnh được sử dụng, c&aacute;c cờ được sử dụng, hệ điều h&agrave;nh, phi&ecirc;n bản Node, CPU count, Ram Size, thời gian thực thi, lỗi nếu c&oacute; v&agrave; Angular team c&oacute; thể cải tiến v&agrave; chỉnh sửa trong phi&ecirc;n bản tiếp.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Loại bỏ @angular/http</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Từ Angular 8, angular ngừng hỗ trợ @angular/http. N&oacute; đ&atilde; bị deprecated (b&aacute;o ngừng hỗ trợ phi&ecirc;n bản sau)&nbsp;từ Angular 4 v&agrave; cung cấp một thư viện hiệu quả v&agrave; bảo mật hơn gọi l&agrave; @angular/common/http. Nhưng ở Angular 7 vẫn cho ph&eacute;p ch&uacute;ng ta sử dụng @angular/http trong ứng dụng. Nhưng Angular 8 th&igrave; n&oacute; đ&atilde; dừng hỗ trợ. Ch&uacute;ng ta cần chỉnh code l&ecirc;n @angular/common/http</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Thay đổi ViewChild v&agrave; ContentChild</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong Angular 8 c&oacute; một số thay đổi lớn với việc sử dụng ViewChild v&agrave; ContentChild. N&oacute; l&agrave; những thứ kh&ocirc;ng mong đợi của team Angular. Hiện tại Angular 8 n&oacute; bắt buộc cung cấp một cờ Boolean static để định nghĩa instance cho ViewChild v&agrave; ConetntChild. Ch&uacute;ng ta đ&atilde; c&oacute; kinh nghiệm khi 1 issue thỉnh thoảng l&agrave; c&aacute;c instance n&agrave;y c&oacute; sẵn trong ngOnInit, nhưng đ&ocirc;i khi ch&uacute;ng ta kh&ocirc;ng t&igrave;m thấy c&aacute;c instance n&agrave;y cho đến khi ngAfterContentInit hoặc ngAfterViewInit. N&oacute; xuất hiện ch&iacute;nh khi c&aacute;c phần thử được load v&agrave;o DOM sau khi dữ liệu được binding. (giống *ngIf hoặc *ngFor) sau đ&oacute; c&aacute;c phần thử được insert v&agrave;o DOM trong ngAfterViewInit hoặc ngAfterContentInit. Điều n&agrave;y g&acirc;y hiểu lầm v&agrave; đ&oacute; l&agrave; v&igrave; sao ch&uacute;ng ta cần chỉ ra compoinent:</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>@ViewChild(&#39;info&#39;,&nbsp;{&nbsp;static:&nbsp;false&nbsp;})&nbsp;_cityViewChild:&nbsp;CityComponent;&nbsp;&nbsp;</code></span></pre>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu gi&aacute; trị static l&agrave; true, angular sẽ cố t&igrave;m kiếm phần tử tở thời điểm khởi tạo component, v&iacute; dụ ngOnInit. N&oacute; c&oacute; thể ho&agrave;n th&agrave;nh khi phần tử kh&ocirc;ng được sử dụng bất kỳ structure directive n&agrave;o. Khi static value l&agrave; false, angular sẽ t&igrave;m phần tử sau khi khởi tạo view.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỗ trợ SVG Template</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiện tại Angular 8 hỗ trợ template với đu&ocirc;i mở rộng l&agrave; SVG. Nghĩa l&agrave; ch&uacute;ng ta c&oacute; thể d&ugrave;ng file SVG để l&agrave;m template như HTML m&agrave; kh&ocirc;ng cần cấu h&igrave;nh g&igrave; kh&aacute;c cả. Nhưng c&acirc;u hỏi l&agrave; tại sao phải sử dụng .svg như l&agrave; template thay v&igrave; sử dụng image trong HTML file. L&yacute; do l&agrave; khi sử dụng SVG template, ch&uacute;ng ta c&oacute; thể sử dụng như l&agrave; một directive v&agrave; kết quả ch&uacute;ng ta c&oacute; thể bind n&oacute; như HTML template. Với c&aacute;c tiếp cận n&agrave;y, ch&uacute;ng ta c&oacute; thể tạo động c&aacute;c đồ hoạt tương t&aacute;c trong ứng dụng.</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>@Component({&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;selector:&nbsp;&quot;app-icon&quot;,&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;templateUrl:&nbsp;&quot;./icon.component.svg&quot;,&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;styleUrls:&nbsp;[&quot;./icon.component.css&quot;]&nbsp;&nbsp;\r\n})&nbsp;&nbsp;\r\nexport&nbsp;class&nbsp;DashboardComponent&nbsp;{...}</code></span></pre>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỗ trợ PNPM</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong Angular 8, Angular CLI cũng hỗ trợ một package manager mới PNPM bao gồm NPM v&agrave; Yarn. Trong c&acirc;u lệnh ng add giờ cung cấp th&ecirc;m 1 c&aacute;i cờ nữa l&agrave; &ndash;registry để th&ecirc;m c&aacute;c packages từ bất cứ NPM private n&agrave;o. C&acirc;u lệnh n&agrave;y đ&atilde; c&oacute; sẵn tr&ecirc;n Angular CLI với ng update command.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỗ trợ Codelyzer 5.0</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Angular 8 hỗ trợ cấu h&igrave;nh TSLint bởi Codelyzer 5.0. Phi&ecirc;n bản n&agrave;y của Codelyzer, một v&agrave;i quy tắc được đổi t&ecirc;n. Nhưng khi ch&uacute;ng ta upgrade Angular CLI, n&oacute; sẽ update file cấu h&igrave;nh TSLint. CLI update sẽ loại bỏ c&aacute;c lệnh import es6 từ file polyfills.ts. Bởi v&igrave; n&oacute; tự động được th&ecirc;m v&agrave;o nếu được y&ecirc;u cầu bởi CLI.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Hỗ trợ thư viện mới New Builders/ Architect API</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Phi&ecirc;n bản mới của Angular CLI cho ph&eacute;p ch&uacute;ng ta sử dụng phi&ecirc;n bản mới của Builders được biết đến l&agrave; Architect API. Angular sử dụng Builders API để thực hiện c&aacute;c hoạt động như server, build, test, lint v&agrave; e2e. Ch&uacute;ng ta c&oacute; thể sử dụng builders trong file angular.json</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>&quot;projects&quot;:&nbsp;{&nbsp;&nbsp;\r\n&nbsp;&nbsp;&quot;app-name&quot;:&nbsp;{&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&quot;architect&quot;:&nbsp;{&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;build&quot;:&nbsp;{&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;builder&quot;:&nbsp;&quot;@angular-devkit/build-angular:browser&quot;,&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;},&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;serve&quot;:&nbsp;{&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;builder&quot;:&nbsp;&quot;@angular-devkit/build-angular:dev-server&quot;,&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;},&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;test&quot;:&nbsp;{&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;builder&quot;:&nbsp;&quot;@angular-devkit/build-angular:karma&quot;,&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;},&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;lint&quot;:&nbsp;{&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;builder&quot;:&nbsp;&quot;@angular-devkit/build-angular:tslint&quot;,&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;},&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;e2e&quot;:&nbsp;{&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;builder&quot;:&nbsp;&quot;@angular-devkit/build-angular:protractor&quot;,&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;\r\n&nbsp;&nbsp;&nbsp;&nbsp;}&nbsp;&nbsp;\r\n&nbsp;&nbsp;}&nbsp;&nbsp;\r\n} &nbsp;</code></span></pre>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">L&agrave;m sao để n&acirc;ng cấp l&ecirc;n Angular 8</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đển n&acirc;n cấp l&ecirc;n Angular 8 từ bất cứ ứng dụng c&oacute; sẵn n&agrave;o chạy Angular 7, bạn cần thực hiện c&aacute;c bước sau đ&acirc;y:</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Angular 8 sử dụng TypeScript 3.4, cần c&agrave;i TypeScript 3.4</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn cần sử dụng Node 12 hoặc cao hơn</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">H&atilde;y thực thi lệnh n&acirc;ng cấp Angular CLI &agrave; ng update @angular/cli @angular/core.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;ch nữa l&agrave; bạn cần kiểm tra qu&aacute; tr&igrave;nh upgrade trong link:&nbsp;https://update.angular.io/</span></p>\r\n\r\n<h1><span style=\"font-family:Arial,Helvetica,sans-serif\">Kết luận</span></h1>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">B&agrave;i viết n&agrave;y n&oacute;i về c&aacute;c t&iacute;nh năng mới trong Angular 8. Ch&uacute;ng ta cũng thảo luận về một số thay đổi đột pahts trong Angular 8 nghĩa n&oacute; l&agrave; được thay đổi ho&agrave;n to&agrave;n so với version cũ.</span></p>\r\n', 'angular8.jpg', 2, 4, 1, 4382, NULL, '2020-04-10 15:26:21', NULL),
(7, '10 Lời khuyên từ các chuyên gia PHP dành cho người mới bắt đầu', '<p>PHP l&agrave; ng&ocirc;n ngữ đằng sau một số ứng dụng web mạnh nhất v&agrave; phổ biến nhất hiện nay, bao gồm cả Facebook v&agrave; WordPress.</p>', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://images.careerbuilder.vn/content/news/20160923/coding-php-1024x497-696x338_1474567211.jpg\" style=\"height:338px; width:696px\" title=\"coding-php-1024x497\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">PHP l&agrave; ng&ocirc;n ngữ đằng sau một số ứng dụng web mạnh nhất v&agrave; phổ biến nhất hiện nay, bao gồm cả Facebook v&agrave; WordPress.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Việc học một ng&ocirc;n ngữ lập tr&igrave;nh mới c&oacute; thể kh&oacute; khăn đối với bạn, v&igrave; vậy ch&uacute;ng t&ocirc;i đ&atilde; li&ecirc;n hệ với một v&agrave;i chuy&ecirc;n gia PHP để t&igrave;m lời khuy&ecirc;n của họ d&agrave;nh cho người mới học lập tr&igrave;nh PHP.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu bạn l&agrave; một người mới bắt đầu học PHP (hoặc đ&atilde; l&agrave; một lập tr&igrave;nh vi&ecirc;n c&oacute; kinh nghiệm), th&igrave; h&atilde;y để lại một v&agrave;i &yacute; kiến trong phần b&igrave;nh luận ph&iacute;a dưới để cho c&aacute;c độc giả kh&aacute;c biết được c&aacute;c bước cụ thể m&agrave; bạn đ&atilde; thực hiện gi&uacute;p cho việc học ng&ocirc;n ngữ n&agrave;y trở n&ecirc;n dễ d&agrave;ng hơn.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">1. Elizabeth Naramore: Bắt đầu với OOP</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Học lập trình web PHP căn bản\" src=\"https://images.careerbuilder.vn/content/news/20160923/hoc-lap-trinh-php-24102015-1_1474567211.jpg\" style=\"height:128px; width:128px\" title=\"Học lập trình web PHP căn bản\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Naramore l&agrave; một nh&acirc;n vi&ecirc;n của SourceForge v&agrave; l&agrave; founder của PHPWomen.org. Đối với những người mới bắt đầu, c&ocirc; đề nghị n&ecirc;n c&oacute; một nền tảng vững chắc trong lập tr&igrave;nh hướng đối tượng (OO) trước khi học s&acirc;u v&agrave;o PHP.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Nếu bạn l&agrave; người chưa c&oacute; kiến thức g&igrave; về lập tr&igrave;nh, th&igrave; h&atilde;y d&agrave;nh thời gian để t&igrave;m hiểu c&aacute;c nguy&ecirc;n tắc cơ bản trong ph&aacute;t triển phần mềm. Những thứ như lập tr&igrave;nh hướng đối tượng, test-driven development, version control, debugging, design patterns, v.v&hellip;</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Nếu bạn đ&atilde; thử v&agrave; kh&ocirc;ng thể giải quyết vấn đề của bạn, th&igrave; đừng ngại hỏi. C&aacute;c cộng đồng PHP n&oacute;i chung l&agrave; rất hữu &iacute;ch v&agrave; th&acirc;n thiện, v&agrave; c&oacute; v&ocirc; số t&agrave;i nguy&ecirc;n cho người mới bắt đầu c&oacute; sẵn trực tuyến. H&atilde;y tham gia v&agrave;o một cộng đồng, cho d&ugrave; đ&oacute; l&agrave; một nh&oacute;m lập tr&igrave;nh vi&ecirc;n tại địa phương bạn ở, hay một dự &aacute;n cộng đồng m&atilde; nguồn mở, hoặc một diễn đ&agrave;n PHP n&agrave;o đ&oacute;.&rdquo;</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">2. Keith Casey: Google trước khi hỏi</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Học lập trình web PHP nâng cao\" src=\"https://images.careerbuilder.vn/content/news/20160923/hoc-lap-trinh-php-24102015-2_1474567211.jpg\" style=\"height:128px; width:128px\" title=\"Học lập trình web PHP nâng cao\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Casey đ&atilde; th&agrave;nh lập một c&ocirc;ng ty phần mềm v&agrave; l&agrave;m rất nhiều c&ocirc;ng việc li&ecirc;n quan đến lĩnh vực n&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Lời khuy&ecirc;n của anh xoay quanh việc t&igrave;m kiếm vị tr&iacute; của bạn trong cộng đồng PHP v&agrave; h&atilde;y &aacute;p dụng một c&acirc;u ch&acirc;m ng&ocirc;n ng&agrave;y c&agrave;ng quan trọng: Google trước khi bạn đặt c&acirc;u hỏi.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;H&atilde;y kết nối với c&aacute;c User Group ngay b&acirc;y giờ. C&oacute; những Group PHP ở khắp nơi tr&ecirc;n thế giới, nơi m&agrave; những người th&ocirc;ng minh tập hợp để tr&ograve; chuyện, kh&aacute;m ph&aacute; những &yacute; tưởng v&agrave; gi&uacute;p đỡ lẫn nhau.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;H&atilde;y nhớ thử t&igrave;m kiếm tr&ecirc;n Google trước khi đặt c&acirc;u hỏi. Kh&ocirc;ng ai th&iacute;ch một kẻ lười biếng cả.&rdquo;</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">3. Eamon Leonard: Tham gia v&agrave;o c&aacute;c dự &aacute;n m&atilde; nguồn mở</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Học lập trình web PHP trực tuyến\" src=\"https://images.careerbuilder.vn/content/news/20160923/hoc-lap-trinh-php-24102015-3_1474567211.jpg\" style=\"height:128px; width:128px\" title=\"Học lập trình web PHP trực tuyến\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Leonard đang điều h&agrave;nh một c&ocirc;ng ty phần mềm tại Ireland v&agrave; l&agrave; đồng s&aacute;ng lập của CloudSplit, một dịch vụ ph&acirc;n t&iacute;ch thời gian thực tr&ecirc;n điện to&aacute;n đ&aacute;m m&acirc;y. Giống như nhiều đồng nghiệp của m&igrave;nh, anh khuy&ecirc;n h&atilde;y tham gia c&aacute;c dự &aacute;n m&atilde; nguồn mở, thậm ch&iacute; d&ugrave; bạn l&agrave; một người mới bắt đầu.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;H&atilde;y tham gia v&agrave;o dự &aacute;n nguồn mở sớm nhất c&oacute; thể để hiểu thấu đ&aacute;o những vấn đề cơ bản&hellip; Điều n&agrave;y cho ph&eacute;p bạn truy cập v&agrave;o m&atilde; nguồn v&agrave; một cơ hội để học hỏi từ c&aacute;c lập tr&igrave;nh vi&ecirc;n d&agrave;y dạn kinh nghiệm trong ng&agrave;nh c&ocirc;ng nghiệp n&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;T&igrave;m kiếm v&agrave; viết t&agrave;i liệu về c&aacute;c bug l&agrave; một c&ocirc;ng việc tốn nhiều thời gian, v&agrave; đ&oacute; l&agrave; một trong những việc sẽ được đ&aacute;nh gi&aacute; cao bởi bất kỳ nh&oacute;m m&atilde; nguồn mở n&agrave;o&hellip; Khi n&oacute;i đến việc thu&ecirc; c&aacute;c nh&agrave; ph&aacute;t triển để l&agrave;m việc với m&igrave;nh, ch&uacute;ng t&ocirc;i sẽ đ&aacute;nh gi&aacute; cao những đ&oacute;ng g&oacute;p tiềm năng của một ai đ&oacute; đến với c&ocirc;ng ty ch&uacute;ng t&ocirc;i nhiều hơn nếu họ đ&atilde; l&agrave;m việc tr&ecirc;n một dự &aacute;n m&atilde; nguồn mở.&rdquo;</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">4. Lorna Jane Mitchell: Cứ l&agrave;m đi</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Học lập trình web PHP online\" src=\"https://images.careerbuilder.vn/content/news/20160923/hoc-lap-trinh-php-24102015-4_1474567211.png\" style=\"height:128px; width:128px\" title=\"Học lập trình web PHP online\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;i t&ecirc;n &ldquo;Lornajane&rdquo;, cũng như Mitchell thường được biết đến nổi tiếng l&agrave; một nh&agrave; tư vấn PHP, một lập tr&igrave;nh vi&ecirc;n, một writer v&agrave; speaker.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&ocirc; đưa ra một lời khuy&ecirc;n kh&aacute; quen thuộc trong lập tr&igrave;nh l&agrave;: Cứ l&agrave;m đi.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;H&atilde;y nhảy v&agrave;o! PHP l&agrave; một ng&ocirc;n ngữ rất dễ tiếp cận v&agrave; học một c&aacute;ch nhanh ch&oacute;ng. C&aacute;ch tốt nhất để học một c&aacute;i g&igrave; đ&oacute; l&agrave; h&atilde;y bắt tay v&agrave;o thử vọc vậy n&oacute;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Bất cứ ai cũng c&oacute; thể lập tr&igrave;nh PHP. V&igrave; r&agrave;o cản gia nhập thấp đồng nghĩa với việc c&oacute; rất nhiều lập tr&igrave;nh vi&ecirc;n PHP tồi tr&ecirc;n thế giới. C&aacute; nh&acirc;n t&ocirc;i nghĩ rằng nếu bạn c&oacute; thể giải quyết vấn đề của m&igrave;nh với PHP, th&igrave; bạn h&atilde;y l&agrave;m điều đ&oacute;, thậm ch&iacute; nếu n&oacute; kh&ocirc;ng phải l&agrave; ho&agrave;n hảo.&rdquo;</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">5. Chris Cornutt: Tr&aacute;nh việc bị hết động lực trong lập tr&igrave;nh</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Học lập trình web PHP tìm việc\" src=\"https://images.careerbuilder.vn/content/news/20160923/hoc-lap-trinh-php-24102015-5_1474567211.jpg\" style=\"height:128px; width:128px\" title=\"Học lập trình web PHP tìm việc\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cornutt l&agrave; founder của PHPDeveloper.org v&agrave; Joind.in, anh đ&atilde; sử dụng PHP từ năm 1998. Trong lời khuy&ecirc;n của anh cho c&aacute;c lập tr&igrave;nh vi&ecirc;n PHP mới v&agrave;o nghề, anh khuy&ecirc;n về c&aacute;ch chống lại sự mệt mỏi v&agrave; ch&aacute;n nản trong lập tr&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;T&ocirc;i nghĩ rằng c&aacute;c lập tr&igrave;nh vi&ecirc;n mới thường trở n&ecirc;n dễ bị qu&aacute; tải&hellip; Người mới bắt đầu thường rất th&iacute;ch ng&ocirc;n ngữ n&agrave;y v&agrave; họ vọc vậy với t&acirc;m l&yacute; &ldquo;chỉ cần l&agrave;m cho n&oacute; chạy l&agrave; được&rdquo;, nhưng t&ocirc;i d&aacute;m n&oacute;i rằng hơn một nửa sẽ dừng lại ở đ&oacute;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Cố gắng t&igrave;m một mentor c&oacute; thể hướng dẫn bạn th&ocirc;ng qua một số bước ban đầu. Điều n&agrave;y tạo ra một thế giới kh&aacute;c biệt khi bạn c&oacute; một người n&agrave;o đ&oacute; thực sự để n&oacute;i chuyện. Qua c&aacute;c k&ecirc;nh chat l&agrave; một lựa chọn tốt, nhưng trực tiếp mặt đối mặt th&igrave; vẫn tốt hơn nhiều.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">6. Abraham Williams: Thử sử dụng Drupal</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Học lập trình web PHP kiếm tiền\" src=\"https://images.careerbuilder.vn/content/news/20160923/hoc-lap-trinh-php-24102015-6_1474567212.png\" style=\"height:128px; width:128px\" title=\"Học lập trình web PHP kiếm tiền\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Williams l&agrave; một lập tr&igrave;nh vi&ecirc;n th&agrave;nh danh nhờ tự học. &Ocirc;ng cũng khuy&ecirc;n c&aacute;c lập tr&igrave;nh vi&ecirc;n PHP mới n&ecirc;n nhảy v&agrave;o một dự &aacute;n lập tr&igrave;nh thực tế, ưu ti&ecirc;n cho c&aacute;c dự &aacute;n m&atilde; nguồn mở.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;T&igrave;m một dự &aacute;n chất lượng hoặc cộng đồng (tốt hơn l&agrave; theo định hướng m&atilde; nguồn mở) để đ&oacute;ng g&oacute;p v&agrave;o. Học về code, về con người v&agrave; c&aacute;ch l&agrave;m việc nh&oacute;m. Kh&ocirc;ng chỉ bạn sẽ học hỏi từ c&aacute;c nh&agrave; ph&aacute;t triển c&oacute; kinh nghiệm, m&agrave; bạn c&ograve;n được l&agrave;m việc với code chất lượng v&agrave; một cộng đồng th&acirc;n thiện. Họ cũng sẽ sẵn s&agrave;ng đề xuất cải tiến code trong c&aacute;c bản v&aacute; lỗi v&agrave; thậm ch&iacute; cả c&aacute;ch trở th&agrave;nh một th&agrave;nh vi&ecirc;n tốt hơn của cộng đồng.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;T&ocirc;i đề xuất dự &aacute;n Drupal l&agrave; một điểm khởi đầu tốt. N&oacute; đ&atilde; trưởng th&agrave;nh, c&oacute; một cộng đồng rộng lớn, một tốc độ tăng trưởng tuyệt vời v&agrave; rất nhiều c&ocirc;ng ăn việc l&agrave;m cho c&aacute;c nh&agrave; ph&aacute;t triển Drupal giỏi.&rdquo;</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">7. Demian Turner: Học hỏi từ những coder d&agrave;y dạn</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Học lập trình web PHP kiếm việc\" src=\"https://images.careerbuilder.vn/content/news/20160923/hoc-lap-trinh-php-24102015-7_1474567212.jpg\" style=\"height:128px; width:128px\" title=\"Học lập trình web PHP kiếm việc\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Turner đ&atilde; l&agrave;m việc với c&aacute;c dự &aacute;n web v&agrave; m&atilde; nguồn mở từ năm 1996. Anh điều h&agrave;nh trang web PHPKitchen.com v&agrave; đ&atilde; lọt v&agrave;o v&ograve;ng chung kết Seedcamp gần đ&acirc;y.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Anh đ&atilde; cho ch&uacute;ng t&ocirc;i rất nhiều lời khuy&ecirc;n tuyệt vời đối với c&aacute;c nh&agrave; ph&aacute;t triển PHP mới, lời khuy&ecirc;n đ&oacute; c&oacute; thể gi&uacute;p tiết kiệm thời gian, l&agrave;m cho bạn trở th&agrave;nh một lập tr&igrave;nh vi&ecirc;n tốt hơn v&agrave; gi&uacute;p duy tr&igrave; niềm đam m&ecirc;m của bạn trong lập tr&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;H&atilde;y đọc code của những lập tr&igrave;nh vi&ecirc;n d&agrave;y dạn; lu&ocirc;n lu&ocirc;n c&oacute; một c&aacute;ch tốt hơn, sạch hơn để l&agrave;m việc. Đừng ph&aacute;t minh lại bất kỳ b&aacute;nh xe n&agrave;o; bạn sẽ lu&ocirc;n lu&ocirc;n c&oacute; đủ thứ để lập tr&igrave;nh; sử dụng c&aacute;c thư viện nổi tiếng bất cứ khi n&agrave;o bạn c&oacute; thể để tr&aacute;nh phải tự lập tr&igrave;nh lại c&aacute;i đ&atilde; c&oacute;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Đảm bảo rằng code của bạn c&oacute; thể đọc được; nếu bạn kh&ocirc;ng thể hiểu n&oacute; sau 6 th&aacute;ng, th&igrave; l&agrave;m thế n&agrave;o m&agrave; c&aacute;c lập tr&igrave;nh vi&ecirc;n kh&aacute;c c&oacute; thể hiểu được?</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Lu&ocirc;n lu&ocirc;n cố gắng v&agrave; đơn giản h&oacute;a c&aacute;c interface của bạn; việc viết code đơn giản l&agrave; kh&oacute; hơn rất nhiều, nhưng việc refactoring code sẽ gi&uacute;p bạn tiết kiệm rất nhiều thời gian v&agrave; c&ocirc;ng sức khi bảo tr&igrave; n&oacute;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Cuối c&ugrave;ng, h&atilde;y t&igrave;m hiểu về một số c&aacute;c lập tr&igrave;nh vi&ecirc;n nổi tiếng v&agrave; l&agrave;m thế n&agrave;o họ vẫn đam m&ecirc; nghệ thuật lập tr&igrave;nh sau rất nhiều năm sau.&rdquo;</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">8. Stuart Herbert: Biết về Test-Driven Development, đ&oacute;ng g&oacute;i v&agrave; Source Control</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Học lập trình web PHP cấp tốc\" src=\"https://images.careerbuilder.vn/content/news/20160923/hoc-lap-trinh-php-24102015-8_1474567212.jpg\" style=\"height:128px; width:128px\" title=\"Học lập trình web PHP cấp tốc\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Herbert đ&atilde; lập tr&igrave;nh PHP từ năm 1999; anh đ&atilde; viết về PHP trong nhiều năm v&agrave; đ&atilde; đ&oacute;ng g&oacute;p rất nhiều cho Gentoo Linux.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đối với c&aacute;c nh&agrave; ph&aacute;t triển PHP mới, anh khuy&ecirc;n, &ldquo;T&igrave;m hiểu về test-driven development v&agrave; đ&oacute;ng g&oacute;i. Một khi bạn đ&atilde; th&agrave;nh thạo n&oacute;, bạn sẽ viết code nhanh hơn, v&agrave; bất cứ ai kế thừa code của bạn cũng sẽ cảm ơn bạn v&igrave; điều đ&oacute;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Oh, v&agrave; t&igrave;m hiểu về c&aacute;ch kiểm so&aacute;t m&atilde; nguồn; đ&acirc;y l&agrave; một y&ecirc;u cầu bắt buộc!&rdquo;</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Anh cũng n&oacute;i rằng sức mạnh lớn nhất của ng&ocirc;n ngữ n&agrave;y l&agrave; &ldquo;t&agrave;i liệu tuyệt vời c&oacute; sẵn tại trang PHP.net miễn ph&iacute;. Với một số ng&ocirc;n ngữ kh&aacute;c, bạn cần phải đi ra ngo&agrave;i v&agrave; mua c&aacute;c t&agrave;i liệu như s&aacute;ch, nhưng điều đ&oacute; kh&ocirc;ng đ&uacute;ng với PHP.&rdquo;</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">9. Maggie Nelson: Th&agrave;nh thạo về lưu trữ dữ liệu</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Học lập trình web PHP hiệu quả\" src=\"https://images.careerbuilder.vn/content/news/20160923/hoc-lap-trinh-php-24102015-9_1474567212.gif\" style=\"height:140px; width:128px\" title=\"Học lập trình web PHP hiệu quả\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nelson l&agrave; một nh&agrave; ph&aacute;t triển PHP hiện đang l&agrave;m việc tại Flickr.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&ocirc; n&oacute;i người mới bắt đầu học PHP n&ecirc;n quan t&acirc;m về lưu trữ dữ liệu sớm trong sự nghiệp của họ.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Trong nhiều trường hợp, bạn sẽ sử dụng PHP cho c&aacute;c ứng dụng web. Ứng dụng web hấp dẫn c&oacute; xu hướng sử dụng dữ liệu theo những c&aacute;ch bất ngờ hoặc những c&aacute;ch để giải quyết vấn đề h&agrave;ng ng&agrave;y. Nếu bạn mới bắt đầu với PHP v&agrave; đ&acirc;y l&agrave; ng&ocirc;n ngữ đầu ti&ecirc;n của bạn, h&atilde;y d&agrave;nh một hoặc hai ng&agrave;y đọc về c&aacute;ch lưu trữ dữ liệu v&agrave; c&oacute; thể t&igrave;m hiểu một số về SQL. PHP được biết đến với việc hỗ trợ rất tốt c&aacute;c cơ sở dữ liệu. H&atilde;y t&igrave;m hiểu về MySQL, c&aacute;c cơ sở dữ liệu quan hệ kh&aacute;c v&agrave; kh&aacute;m ph&aacute; một số giải ph&aacute;p lưu trữ dữ liệu NoSQL.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;H&atilde;y tự m&igrave;nh viết &iacute;t nhất một ứng dụng từ đầu đến cuối. Thế giới PHP cung cấp nhiều framework tuyệt vời c&oacute; thể dễ d&agrave;ng truy cập dữ liệu theo c&aacute;ch trừu tượng, nhưng lu&ocirc;n lu&ocirc;n chắc chắn rằng bạn thực sự hiểu c&aacute;ch dữ liệu được thực sự được truy vấn ph&iacute;a sau hậu trường!&rdquo;</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">10. Michael Maclean: T&igrave;m hiểu về bảo mật</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Học lập trình web PHP chất lượng\" src=\"https://images.careerbuilder.vn/content/news/20160923/hoc-lap-trinh-php-24102015-10_1474567212.jpg\" style=\"height:128px; width:128px\" title=\"Học lập trình web PHP chất lượng\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Maclean l&agrave; một lập tr&igrave;nh vi&ecirc;n PHP v&agrave; Python tại Outer Hebrides, Scotland.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Anh n&oacute;i, &ldquo;PHP kh&aacute; dễ học, đ&oacute; l&agrave; l&yacute; do tại sao rất nhiều người sử dụng n&oacute;, nhưng t&ocirc;i nghĩ điều quan trọng l&agrave; phải c&oacute; một c&aacute;i nh&igrave;n v&agrave;o những g&igrave; người ta đ&atilde; sử dụng n&oacute; trong một thời gian d&agrave;i. Thay v&igrave; viết tất cả mọi thứ từ đầu, h&atilde;y t&igrave;m một v&agrave;i framework c&oacute; sẵn, ch&uacute;ng sẽ cung cấp cho bạn một sự khởi đầu rất tốt.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Một thứ kh&aacute;c cần phải học đ&oacute; l&agrave; về bảo mật. PHP c&oacute; một số nhược điểm trong qu&aacute; khứ, đ&oacute; l&agrave; mặt tr&aacute;i của việc dễ d&agrave;ng sử dụng. C&oacute; rất nhiều nguồn t&agrave;i nguy&ecirc;n trực tuyến về l&agrave;m thế n&agrave;o để tr&aacute;nh c&aacute;c vấn đề bảo mật. H&atilde;y xem c&aacute;c cuốn s&aacute;ch của Chris Shiflett v&agrave; Ilia Alshanetsky để biết th&ecirc;m về c&aacute;ch khắc phục.&rdquo;</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nguồn: Techtalk.vn</span></p>', 'hoc-lap-trinh-web-php-truc-tuyen-24102015.png', 7, 2, 1, 368, NULL, '2020-04-10 15:32:57', '2020-07-04 02:51:09');
INSERT INTO `posts` (`id`, `title`, `description`, `contents`, `thumbnail`, `user_id`, `category_id`, `status`, `view`, `rate`, `created_at`, `updated_at`) VALUES
(8, '11 Cách học lập trình Java chỉ trong', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn đừng n&ecirc;n nhầm lẫn ng&ocirc;n ngữ lập tr&igrave;nh Java với JavaScript, n&oacute; l&agrave; ng&ocirc;n ngữ được tạo ra để &ldquo;viết một lần, chạy mọi nơi&rdquo;.</span></p>', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><em><strong>B&agrave;i viết được dịch từ trang web&nbsp;<a href=\"http://codecondo.com/learn-java-programming-10-ways/\" target=\"_blank\">Codecondo</a></strong></em></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn đừng n&ecirc;n nhầm lẫn ng&ocirc;n ngữ lập tr&igrave;nh Java với JavaScript, n&oacute; l&agrave; ng&ocirc;n ngữ được tạo ra để&nbsp;<a href=\"http://en.wikipedia.org/wiki/Write_once,_run_anywhere\" target=\"_blank\">&ldquo;viết một lần, chạy mọi nơi&rdquo;</a>&nbsp;- n&oacute;i theo lối kỹ thuật nghĩa l&agrave; n&oacute; c&oacute; thể chạy tốt tr&ecirc;n bất kỳ thiết bị n&agrave;o hiện c&oacute;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Java là một trong những ngôn ngữ lập trình được sử dụng nhiều nhất hiện nay.\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/hoc-lap-trinh-java-co-ban-1.jpg\" title=\"Java là một trong những ngôn ngữ lập trình được sử dụng nhiều nhất hiện nay.\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Java l&agrave; một trong những ng&ocirc;n ngữ lập tr&igrave;nh được sử dụng nhiều nhất hiện nay.</em></span></p>\r\n\r\n<blockquote>\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://techmaster.vn/khoa-hoc/25556/thanh-thao-java-qua-tung-buoc\">kh&oacute;a học lập tr&igrave;nh Java cơ bản đến n&acirc;ng cao khai giảng ng&agrave;y&nbsp;&nbsp;th&aacute;ng 7 năm 2018 tại Techmaster&nbsp;</a></span></h3>\r\n</blockquote>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Ng&ocirc;n ngữ lập tr&igrave;nh Java l&agrave; g&igrave;?</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&uacute;ng ta tương t&aacute;c với Java trong c&aacute;c hoạt động h&agrave;ng ng&agrave;y, d&ugrave; c&oacute; nhận ra thực tế đ&oacute; hay kh&ocirc;ng, v&agrave; c&oacute; nhiều trường hợp - một trang web sẽ đ&ograve;i ch&uacute;ng ta cần phải c&agrave;i Java th&igrave; mới c&oacute; thể xem được, c&oacute; rất nhiều trang web sử dụng flash v&agrave; c&oacute; một số component flash được t&iacute;ch hợp trong l&otilde;i hệ thống.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Java cũng l&agrave; một chương tr&igrave;nh bạn thường tải về ngay sau khi mua một chiếc m&aacute;y t&iacute;nh mới - t&ocirc;i kh&ocirc;ng biết l&agrave; liệu c&oacute; ai sẽ c&agrave;i sẵn Java như l&agrave; một chương tr&igrave;nh mặc định trong c&aacute;c hệ điều h&agrave;nh hay kh&ocirc;ng. Lịch sử của n&oacute; với những vấn đề về bảo mật th&igrave; kh&ocirc;ng phải l&agrave; điều th&uacute; vị, nhưng ch&iacute;nh những vấn đề đ&oacute; đ&atilde; l&agrave;m cho ng&ocirc;n ngữ n&agrave;y ng&agrave;y ho&agrave;n thiện như b&acirc;y giờ.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Từ c&aacute;c m&aacute;y laptop cho đến c&aacute;c m&aacute;y chủ dữ liệu (datacenter), c&aacute;c game console cho đến c&aacute;c si&ecirc;u m&aacute;y t&iacute;nh trong nghi&ecirc;n cứu khoa học, điện thoại di động đến Internet, Java c&oacute; mặt ở khắp mọi nơi!</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">97% Enterprise Desktops chạy Java</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">89% m&aacute;y Desktops (hoặc Computers) ở Mỹ chạy Java</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">9 triệu lập tr&igrave;nh vi&ecirc;n Java tr&ecirc;n khắp thế giới</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Java l&agrave; lựa chọn số #1 d&agrave;nh cho c&aacute;c lập tr&igrave;nh vi&ecirc;n</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Java l&agrave; nền tảng ph&aacute;t triển số #1</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; 3 tỷ thiết bị di động chạy Java</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">100% đầu đĩa Blu-ray chạy Java</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">5 tỷ Java Card đang được sử dụng</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">125 triệu thiết bị TV chạy Java</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">5 trong Top 5 c&aacute;c nh&agrave; sản xuất thiết bị gốc (Original Equipment Manufacturers) sử dụng Java ME</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">như ch&uacute;ng ta c&oacute; thể thấy c&aacute;c số liệu thống k&ecirc; ở tr&ecirc;n, ng&ocirc;n ngữ lập tr&igrave;nh Java rất được ưa chuộng, v&agrave; chắc chắn c&oacute; một thị trường rất lớn d&agrave;nh cho n&oacute;.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Mức lương cho c&aacute;c lập tr&igrave;nh vi&ecirc;n Java</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">T&ocirc;i rất th&iacute;ch đề cập một ch&uacute;t đến chủ đề lương của c&aacute;c lập tr&igrave;nh vi&ecirc;n Java,&nbsp;<a href=\"http://vinacode.net/2015/02/09/lap-trinh-kiem-tien/\" target=\"_blank\">bao nhi&ecirc;u tiền m&agrave; bạn c&oacute; thể kiếm được</a>&nbsp;- trong một lượng thời gian hợp l&yacute; - bằng c&aacute;ch trở th&agrave;nh một lập tr&igrave;nh vi&ecirc;n Java to&agrave;n thời gian.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Mức lương của lập trình viên Java\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/luong-lap-trinh-vien-java-2.png\" title=\"Mức lương của lập trình viên Java\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y l&agrave; một thống k&ecirc; lương to&agrave;n cầu, kh&ocirc;ng x&aacute;c định cho bất cứ khu vực n&agrave;o. N&oacute; xấp xỉ khoảng $85,000 đ&ocirc;-la/năm (gần 1,8 tỷ VNĐ/năm) l&agrave; một con số lương rất cao, khi m&agrave; bạn c&oacute; thể học ng&ocirc;n ngữ n&agrave;y miễn ph&iacute;. Với suy nghĩ đ&oacute;, bạn h&atilde;y xem danh s&aacute;ch c&aacute;c nguồn t&agrave;i nguy&ecirc;n sau đ&acirc;y m&agrave; đ&atilde; gi&uacute;p ch&uacute;ng t&ocirc;i học Java chỉ trong một v&agrave;i tuần.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&oacute;a học&nbsp;<a href=\"https://techmaster.vn/khoa-hoc/25467/java-can-ban-thuc-hanh\" target=\"_blank\">&quot;Java căn bản thực h&agrave;nh&quot;</a>&nbsp;lấy người học l&agrave;m trung t&acirc;m, nhấn mạnh v&agrave;o kỹ năng tự đ&agrave;o tạo của mỗi học vi&ecirc;n trong đ&oacute; giảng vi&ecirc;n chỉ l&agrave; người trợ gi&uacute;p, bổ trợ, định hướng. Kh&oacute;a học&nbsp;<a href=\"https://techmaster.vn/khoa-hoc/8227/lap-trinh-java-spring\" target=\"_blank\">&quot;Lập tr&igrave;nh Java Spring&quot;</a>&nbsp;tập trung v&agrave;o thực h&agrave;nh bằng c&aacute;c v&iacute; dụ đơn giản, ngắn gọn gi&uacute;p người học l&agrave;m quen, trải nghiệm v&agrave; tự thu nạp kiến thức cho m&igrave;nh.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://www.udemy.com/learn-to-program-with-java/\" target=\"_blank\">1. Lập tr&igrave;nh Java cho người mới bắt đầu</a>&nbsp;</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Lập trình Java cho người mới bắt đầu\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/lap-trinh-java-cho-nguoi-moi-bat-dau-3.png\" title=\"Lập trình Java cho người mới bắt đầu\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">B&agrave;i viết n&agrave;y đ&atilde; được đăng một thời gian, v&agrave; kể từ l&uacute;c đ&oacute; đến nay t&ocirc;i đ&atilde; nhận được kh&aacute; nhiều phản hồi kh&aacute;c nhau, hầu hết mọi người n&oacute;i rằng trong thực tế cần phải mất nhiều thời gian hơn chứ kh&ocirc;ng chỉ v&agrave;i tuần m&agrave; c&oacute; thể học được ng&ocirc;n ngữ lập tr&igrave;nh n&agrave;y. T&ocirc;i kh&ocirc;ng đồng &yacute; với quan điểm của họ, v&igrave; vậy - t&ocirc;i đ&atilde; bổ sung th&ecirc;m kh&oacute;a học n&agrave;y v&agrave; đề nghị bạn n&ecirc;n tham gia.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&oacute;a học n&agrave;y tốn một &iacute;t tiền, nhưng n&ecirc;n nhớ rằng bạn sẽ được truy cập v&agrave;o một cộng đồng chuy&ecirc;n biệt v&agrave; duy nhất, nơi m&agrave; bạn sẽ t&igrave;m thấy c&oacute; tất cả 35,000 học vi&ecirc;n đang tham gia kh&oacute;a học n&agrave;y. N&oacute; chứa tr&ecirc;n 10 giờ nội dung, v&agrave; nhiều hơn 100 b&agrave;i giảng, c&ugrave;ng h&agrave;ng trăm thảo luận tr&ecirc;n hầu hết c&aacute;c chủ đề li&ecirc;n quan.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn sẽ kh&ocirc;ng thể t&igrave;m thấy một c&aacute;ch n&agrave;o để học Java tốt hơn c&aacute;ch tham gia v&agrave;o kh&oacute;a học n&agrave;y, c&aacute;ch thay thế tốt hơn c&oacute; thể l&agrave; học trong thực tế từ một chuy&ecirc;n gia, nhưng điều n&agrave;y kh&ocirc;ng phải khi n&agrave;o cũng thực hiện được dễ d&agrave;ng. T&ocirc;i cũng rất sẵn l&ograve;ng trả lời c&aacute;c c&acirc;u hỏi về kh&oacute;a học n&agrave;y.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"http://www.oracle.com/technetwork/topics/newtojava/overview/index.html\" target=\"_blank\">2. Lập tr&igrave;nh Java cơ bản</a>&nbsp;</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Lập trình Java cơ bản\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/lap-trinh-java-co-ban-4.png\" title=\"Lập trình Java cơ bản\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Việc học ng&ocirc;n ngữ lập tr&igrave;nh n&agrave;o kh&ocirc;ng thực sự quan trọng, ch&uacute;ng ta sẽ lu&ocirc;n bắt đầu với những thứ cơ bản, v&agrave; Java kh&ocirc;ng phải l&agrave; trường hợp ngoại lệ. Thật biết ơn trang web ch&iacute;nh thức của Oracle (c&ocirc;ng ty đứng sau hỗ trợ ng&ocirc;n ngữ Java) khi c&oacute; một t&agrave;i liệu tuyệt vời giới thiệu về ng&ocirc;n ngữ n&agrave;y, giải th&iacute;ch n&oacute; l&agrave; g&igrave; v&agrave; chỉ cho bạn về những thứ cơ bản v&agrave; c&aacute;ch n&oacute; l&agrave;m việc.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn cũng sẽ t&igrave;m thấy c&oacute; rất nhiều nguồn t&agrave;i nguy&ecirc;n để học, nhưng điều quan trọng nhất l&agrave; - trang web n&agrave;y sẽ gi&uacute;p bạn bắt đầu với tất cả những c&ocirc;ng cụ cần thiết v&agrave; những y&ecirc;u cầu kh&aacute;c khi bắt đầu học Java.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"http://introcs.cs.princeton.edu/java/home/\" target=\"_blank\">3. Giới thiệu về lập tr&igrave;nh Java</a>&nbsp;</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Giới thiệu về lập trình Java\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/gioi-thieu-lap-trinh-java-5.png\" title=\"Giới thiệu về lập trình Java\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y l&agrave; một nguồn t&agrave;i nguy&ecirc;n tuyệt vời kh&aacute;c, kh&ocirc;ng chỉ bởi v&igrave; n&oacute; được giới thiệu bởi một trong những trường đại học tốt nhất thế giới, bạn sẽ học được rất nhiều kiến thức chất lượng bằng việc tham gia kh&oacute;a học nhỏ n&agrave;y. N&oacute; sẽ bắt bạn phải đọc h&agrave;ng tấn t&agrave;i liệu, tất cả điều đ&oacute; l&agrave; cần thiết trong qu&aacute; tr&igrave;nh học lập tr&igrave;nh Java.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y cũng được xem l&agrave; một trong những nguồn t&agrave;i nguy&ecirc;n th&acirc;n thiện nhất d&agrave;nh cho người mới bắt đầu học lập tr&igrave;nh Java, kh&ocirc;ng quan trọng việc dường như c&oacute; một ch&uacute;t kỹ thuật ngay l&uacute;c đầu ti&ecirc;n. C&aacute;c b&agrave;i học gồm nhiều h&igrave;nh ảnh minh họa, c&aacute;c v&iacute; dụ code mẫu v&agrave; t&agrave;i liệu sẽ gi&uacute;p bạn tiến l&ecirc;n ph&iacute;a trước.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"http://www.learnjavaonline.org/\" target=\"_blank\">4. Học lập tr&igrave;nh Java trực tuyến</a>&nbsp;</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Học lập trình Java trực tuyến\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/lap-trinh-java-truc-tuyen-6.png\" title=\"Học lập trình Java trực tuyến\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong một v&agrave;i năm trở lại đ&acirc;y, việc học qua c&aacute;c tutorial c&oacute; t&iacute;nh tương t&aacute;c đ&atilde; trở n&ecirc;n kh&aacute; phổ biến, v&igrave; vậy t&ocirc;i cảm thấy bắt buộc phải th&ecirc;m v&agrave;o một số li&ecirc;n kết tới những trang web đ&oacute; trong danh s&aacute;ch n&agrave;y. Nhưng phải n&ecirc;n nhớ rằng bạn sẽ kh&ocirc;ng học to&agrave;n bộ từ những tutorial kiểu tương t&aacute;c đ&oacute;, t&ocirc;i cũng đề xuất bạn n&ecirc;n bắt đầu bằng một v&agrave;i tutorial cho c&aacute;c chương đầu ti&ecirc;n của bất kỳ cuốn s&aacute;ch n&agrave;o m&agrave; bạn chọn.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Th&ecirc;m nữa, trang web cho ph&eacute;p bạn viết một số code v&agrave; chạy thử ngay tr&ecirc;n tr&igrave;nh duyệt, m&agrave; kh&ocirc;ng cần phải lo lắng về việc c&agrave;i đặt IDE để l&agrave;m việc với n&oacute;.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://learnjavathehardway.org/\" target=\"_blank\">5. Học lập tr&igrave;nh Java theo Hard Way</a>&nbsp;</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Học lập trình Java theo Hard Way\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/lap-trinh-java-hard-way-7.png\" title=\"Học lập trình Java theo Hard Way\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y l&agrave; cuốn s&aacute;ch duy nhất trong danh s&aacute;ch n&agrave;y kh&ocirc;ng miễn ph&iacute;, nhưng cũng đ&aacute;ng đồng tiền b&aacute;t gạo khi n&oacute; l&agrave; một nguồn t&agrave;i nguy&ecirc;n hữu &iacute;ch v&agrave; th&acirc;n thiện cho người mới bắt đầu, t&ocirc;i nghĩ rằng sẽ l&agrave; một &yacute; tưởng hay khi t&ocirc;i liệt k&ecirc; n&oacute; v&agrave;o đ&acirc;y. N&oacute; chỉ c&oacute; gi&aacute; khoảng $20 đ&ocirc;-la, rẻ hơn gi&aacute; của một chiếc &aacute;o sơ-mi h&agrave;ng hiệu m&agrave; bạn đang mặc tr&ecirc;n người.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn c&oacute; thể xem (v&agrave; l&agrave;m việc với) 16 chương đầu ti&ecirc;n một c&aacute;ch miễn ph&iacute; v&agrave; trực tuyến&nbsp;<a href=\"https://learnjavathehardway.org/book/\" target=\"_blank\">tại link n&agrave;y</a>. T&ocirc;i nghĩ rằng c&oacute; nhiều người sẽ y&ecirc;u th&iacute;ch cuốn s&aacute;ch n&agrave;y, bởi thiết kế th&acirc;n thiện của n&oacute; kết hợp với c&aacute;c b&agrave;i tập rất hay, bạn c&ograve;n đ&ograve;i hỏi th&ecirc;m điều g&igrave; nữa chứ.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"http://programmingbydoing.com/\" target=\"_blank\">6. Lập tr&igrave;nh bằng c&aacute;ch thực h&agrave;nh</a>&nbsp;</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Lập trình bằng cách thực hành\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/lap-trinh-java-thuc-hanh-8.png\" title=\"Lập trình bằng cách thực hành\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trang web n&agrave;y được tạo ra bởi ch&iacute;nh người đ&atilde; xuất bản cuốn s&aacute;ch m&agrave; t&ocirc;i đ&atilde; liệt k&ecirc; ở tr&ecirc;n, n&oacute; cũng l&agrave; nguồn cảm hứng để tạo ra cuốn s&aacute;ch đ&oacute;. Bạn sẽ t&igrave;m thấy rất nhiều th&aacute;ch thức m&agrave; kh&ocirc;ng y&ecirc;u cầu nhiều kỹ năng lập tr&igrave;nh l&uacute;c ban đầu, nhưng khi bạn vượt qua những th&aacute;ch thức n&agrave;y th&igrave; ch&uacute;ng sẽ ng&agrave;y c&agrave;ng trở n&ecirc;n kh&oacute; hơn.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">T&ocirc;i lu&ocirc;n tin rằng c&aacute;ch tốt nhất để học một c&aacute;i g&igrave; đ&oacute; l&agrave; bằng c&aacute;ch thực h&agrave;nh n&oacute; trước ti&ecirc;n, testing v&agrave; sau đ&oacute; lại l&agrave;m lại n&oacute; một lần nữa. H&atilde;y chọn lấy bất kỳ một tutorial hoặc cuốn s&aacute;ch n&agrave;o c&oacute; trong danh s&aacute;ch n&agrave;y v&agrave; bạn sẽ sẵn s&agrave;ng tiến l&ecirc;n ph&iacute;a trước.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://www.udemy.com/java-tutorial\" target=\"_blank\">7. Nhập m&ocirc;n lập tr&igrave;nh Java</a>&nbsp;</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Nhập môn lập trình Java\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/lap-trinh-java-bat-dau-9.png\" title=\"Nhập môn lập trình Java\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Một số người c&oacute; sở th&iacute;ch học từ c&aacute;c nội dung video hơn, v&agrave; điều đ&oacute; l&agrave; ho&agrave;n to&agrave;n chấp nhận được. Thỉnh thoảng, khi bạn đang bận rộn v&agrave; c&oacute; rất nhiều thứ phải l&agrave;m, th&igrave; việc học qua video sẽ tốt hơn v&igrave; bạn c&oacute; thể ph&aacute;t lại bất cứ l&uacute;c n&agrave;o, h&atilde;y nắm lấy một số kiến thức quan trọng của một ng&ocirc;n ngữ lập tr&igrave;nh, trong trường hợp n&agrave;y l&agrave; Java.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y l&agrave; một kh&oacute;a học miễn ph&iacute; tại Udemy (bạn sẽ cần một t&agrave;i khoản để xem n&oacute;), kh&oacute;a học đ&atilde; nhận được tr&ecirc;n 100,000 sinh vi&ecirc;n đăng k&yacute; tham gia, v&agrave; đ&oacute; l&agrave; một trong những kh&oacute;a học nổi tiếng nhất tại trang web n&agrave;y. Kh&oacute;a học được dẫn dắt bởi John Purcell, một kỹ sư phần mềm c&oacute; nhiều năm kinh nghiệm lập tr&igrave;nh Java.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn sẽ nhận được gần 20 giờ nội dung học, c&ugrave;ng với tr&ecirc;n 70 b&agrave;i giảng. Nếu bạn vẫn chưa biết đến Udemy, th&igrave; bạn cũng sẽ truy cập tới một cộng đồng hỗ trợ rất rộng lớn (tất cả c&oacute; tr&ecirc;n 100,000 sinh vi&ecirc;n đ&atilde; v&agrave; đang theo học kh&oacute;a học n&agrave;y), v&agrave; c&oacute; một hệ thống hỗ trợ nội bộ để hỏi đ&aacute;p c&aacute;c vấn đề li&ecirc;n quan.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"http://codingbat.com/\" target=\"_blank\">8. CodingBat</a></span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"CodingBat\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/coding-bat-10.png\" title=\"CodingBat\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đừng để cho giao diện của trang web n&agrave;y đ&aacute;nh lừa bạn, nếu n&oacute; đơn giản - l&agrave; v&igrave; n&oacute; sẽ giữ cho bạn kh&ocirc;ng bị mất tập trung v&agrave;o những thứ chẳng li&ecirc;n quan. CodingBat (trước đ&acirc;y l&agrave; JavaBat) l&agrave; một trong những c&aacute;ch tốt nhất để học Java miễn ph&iacute; v&agrave; tương t&aacute;c trong tr&igrave;nh duyệt của bạn. N&oacute; l&agrave; trang web thứ hai trong danh s&aacute;ch của ch&uacute;ng t&ocirc;i c&oacute; đưa ra h&igrave;nh thức học tập qua tương t&aacute;c, t&ocirc;i phải n&oacute;i rằng n&oacute; c&ograve;n tốt hơn cả trang trước đ&oacute;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Vấn đề l&agrave; bạn c&oacute; thể thấy CodingBat kh&ocirc;ng giống những trang như Codecademy, ch&uacute;ng thường giảng giải mọi thứ từ dưới l&ecirc;n tr&ecirc;n, step-by-step. Ở CodingBat th&igrave; nhiều hơn vậy, ngo&agrave;i việc thực h&agrave;nh c&aacute;i m&agrave; bạn đ&atilde; biết th&igrave; ở đ&acirc;y bạn c&ograve;n chọn c&aacute;i m&agrave; bạn c&oacute; khả năng l&agrave;m. Đừng bối rối bởi sự thiếu trật tự của n&oacute;, c&oacute; v&ocirc; số c&aacute;c tutorial cho mỗi phần v&agrave; bạn sẽ học được nhanh hơn rất nhiều.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://www.youtube.com/course?list=ECFE2CE09D83EE3E28\" target=\"_blank\">9. C&aacute;c tutorial về lập tr&igrave;nh Java cho người mới học</a>&nbsp;</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Các tutorial về lập trình Java cho người mới học\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/java-cho-nguoi-moi-hoc-10.png\" title=\"Các tutorial về lập trình Java cho người mới học\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">New Boston giờ đ&acirc;y đ&atilde; được biết đến l&agrave; nơi c&oacute; những video tuyệt vời v&agrave; to&agrave;n diện hướng dẫn về c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh, v&agrave; Java kh&ocirc;ng phải l&agrave; một ngoại lệ. Bạn sẽ t&igrave;m thấy 80 video dạng step-by-step để học Java v&agrave; tất cả n&oacute; được cung cấp miễn ph&iacute;, mặc d&ugrave; t&ocirc;i nghĩ rằng n&oacute; c&oacute; đ&ocirc;i ch&uacute;t chưa được cập nhật lắm.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đừng để &yacute; đến điều m&agrave; t&ocirc;i n&oacute;i về t&iacute;nh cập nhật của n&oacute;, đ&acirc;y vẫn l&agrave; một nguồn t&agrave;i nguy&ecirc;n hữu &iacute;ch đến kh&oacute; tin cho bất kỳ ai c&oacute; quyết t&acirc;m muốn bắt đầu học lập tr&igrave;nh Java, th&ecirc;m nữa n&oacute; kh&ocirc;ng y&ecirc;u cầu bạn phải đăng nhập giống như trang Udemy. C&aacute;c b&igrave;nh luận kh&aacute; s&acirc;u sắc tr&ecirc;n một số video.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"http://mooc.cs.helsinki.fi/programming-part1/registration-and-how-get-started\" target=\"_blank\">10. Lập tr&igrave;nh hướng đối tượng với Java</a>&nbsp;</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Lập trình hướng đối tượng với Java\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/lap-trinh-huong-doi-tuong-trong-java-11.png\" title=\"Lập trình hướng đối tượng với Java\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y l&agrave; một kh&oacute;a học của một trường đại học kh&aacute;c, bạn sẽ cần khoảng xấp xỉ 6 tuần để ho&agrave;n th&agrave;nh n&oacute;. Kh&oacute;a học rất th&acirc;n thiện với người mới bắt đầu, v&agrave; mọi thứ c&oacute; thể ho&agrave;n th&agrave;nh tr&ecirc;n tr&igrave;nh duyệt. N&oacute; cũng l&agrave; một trong những tutorial tốt nhất để bắt đầu học c&aacute;ch l&agrave;m thế n&agrave;o để c&agrave;i đặt Java v&agrave; c&aacute;c c&ocirc;ng cụ cần thiết. Trong kh&oacute;a học n&agrave;y, bạn sẽ học tất cả những kiến thức cơ bản của lập tr&igrave;nh m&aacute;y t&iacute;nh, c&aacute;c thuật to&aacute;n v&agrave; sử dụng lập tr&igrave;nh hướng đối tượng trong ng&ocirc;n ngữ lập tr&igrave;nh Java.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"http://mooc.cs.helsinki.fi/programming-part2\" target=\"_blank\">Phần 2</a>&nbsp;của kh&oacute;a học cũng c&oacute; sẵn tại đ&acirc;y, phần n&agrave;y c&oacute; thời lượng th&ecirc;m khoảng 6 tuần lập tr&igrave;nh nữa. Tổng cộng, bạn sẽ cần khoảng 12-13 tuần để học lập tr&igrave;nh Java. Cuối kh&oacute;a học đ&oacute;, bạn sẽ biết được c&aacute;ch l&agrave;m thế n&agrave;o để x&acirc;y dựng c&aacute;c ứng dụng cho ri&ecirc;ng m&igrave;nh, v&agrave; l&agrave;m c&aacute;ch n&agrave;o để nghĩ giống như một lập tr&igrave;nh vi&ecirc;n Java thực thụ. Bạn cũng sẵn s&agrave;ng n&acirc;ng cao kiến thức v&agrave; kh&aacute;m ph&aacute; ng&ocirc;n ngữ n&agrave;y s&acirc;u hơn.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"http://www.home.hs-karlsruhe.de/~pach0003/informatik_1/aufgaben/en/java.html\" target=\"_blank\">11. C&aacute;c b&agrave;i tập lập tr&igrave;nh Java</a>&nbsp;</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Các bài tập lập trình Java\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/bai-tap-lap-trinh-java-12.png\" title=\"Các bài tập lập trình Java\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">T&ocirc;i kết th&uacute;c danh s&aacute;ch n&agrave;y bằng c&aacute;ch cung cấp tới bạn một trang web thực h&agrave;nh lập tr&igrave;nh kh&aacute;c, được thiết kế đặc biệt d&agrave;nh cho Java v&agrave; chứa c&aacute;c c&acirc;u trả lời cho nhiều c&acirc;u hỏi li&ecirc;n quan. Việc thực h&agrave;nh c&aacute;c b&agrave;i tập n&agrave;y l&agrave; rất quan trọng, đặc biệt khi bạn học một ng&ocirc;n ngữ như Java - ng&ocirc;n ngữ m&agrave; chẳng dễ d&agrave;ng với ai khi mới bắt đầu học n&oacute; cả.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tổng cộng c&oacute; tất cả 30 b&agrave;i tập cho bạn thực h&agrave;nh, v&agrave; thay v&igrave; n&oacute;i rằng &quot;kh&oacute; qu&aacute;, t&ocirc;i kh&ocirc;ng thể l&agrave;m được&quot; - h&atilde;y giải lao một l&aacute;t v&agrave; khi quay lại bạn thường c&oacute; c&acirc;u trả lời xuất hiện trong đầu.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Kết luận</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">B&agrave;i viết n&agrave;y c&oacute; vẻ như kh&ocirc;ng r&otilde; r&agrave;ng bằng b&agrave;i viết về&nbsp;<a href=\"http://codecondo.com/10-ways-to-learn-python/\" target=\"_blank\">c&aacute;ch học ng&ocirc;n ngữ Python</a>&nbsp;do t&ocirc;i viết trước đ&acirc;y, t&ocirc;i chắc chắn sẽ đổ lỗi cho một thực tế rằng Java kh&ocirc;ng phải l&agrave; một ng&ocirc;n ngữ lập tr&igrave;nh dễ học, v&agrave; n&oacute; y&ecirc;u cầu mức độ tập trung cao hơn đến từng chi tiết. N&oacute; cũng nằm trong&nbsp;<a href=\"http://vinacode.net/2014/04/21/ngon-ngu-lap-trinh/\" target=\"_blank\">top c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh n&ecirc;n học</a>&nbsp;trong năm nay, v&agrave; c&oacute; một cộng đồng lớn ở ph&iacute;a sau.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn cũng n&ecirc;n tham gia trao đổi tr&ecirc;n c&aacute;c trang như StackOverflow v&agrave; Reddit - để t&igrave;m kiếm c&acirc;u trả lời cho những c&acirc;u hỏi thường gặp, v&agrave; học được nhiều hơn bằng c&aacute;ch đặt c&aacute;c c&acirc;u hỏi cho ch&iacute;nh m&igrave;nh. Nếu kh&ocirc;ng đặt c&acirc;u hỏi th&igrave; ch&uacute;ng ta thừa nhận với bản th&acirc;n m&igrave;nh rằng ch&uacute;ng ta c&oacute; thể sống m&agrave; kh&ocirc;ng cần một giải ph&aacute;p, trong khi kh&ocirc;ng phải l&agrave; ho&agrave;n to&agrave;n như vậy.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">T&ocirc;i hy vọng với c&aacute;c t&agrave;i nguy&ecirc;n, s&aacute;ch v&agrave; trang web trong b&agrave;i viết n&agrave;y sẽ c&oacute; thể gi&uacute;p &iacute;ch cho bạn, v&agrave; d&ugrave; cho bạn c&oacute; trở th&agrave;nh một lập tr&igrave;nh vi&ecirc;n Java chuy&ecirc;n nghiệp hay kh&ocirc;ng, th&igrave; t&ocirc;i cũng nhờ bạn share b&agrave;i viết n&agrave;y đến với những người đang t&igrave;m c&aacute;ch gia nhập v&agrave;o cộng đồng ph&aacute;t triển phần mềm. Ch&uacute;c bạn may mắn!</span></p>', 'hoc-lap-trinh-java-co-ban-1.jpg', 1, 1, 0, 160, NULL, '2020-04-11 08:39:13', '2020-06-14 09:03:19'),
(9, 'Những điểm nổi bật và ưu điểm của Angular 2', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Với sự nổi l&ecirc;n của React do Facebook ph&aacute;t h&agrave;nh, Angular cũng chuyển m&igrave;nh thay đổi với một phi&ecirc;n bản mới ho&agrave;n to&agrave;n k&egrave;m với nhiều sự thay đổi cũng như những t&iacute;ch hợp mới để trở n&ecirc;n mạnh mẽ hơn nữa với lời hứa của Google.</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Em xin chia sẻ về một số điểm nổi bật v&agrave; ưu điểm về Angular2 m&agrave; em biết:</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Với sự nổi l&ecirc;n của React do Facebook ph&aacute;t h&agrave;nh, Angular cũng chuyển m&igrave;nh thay đổi với một phi&ecirc;n bản mới ho&agrave;n to&agrave;n k&egrave;m với nhiều sự thay đổi cũng như những t&iacute;ch hợp mới để trở n&ecirc;n mạnh mẽ hơn nữa với lời hứa của Google:</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiệu năng cao hơn. Dễ sử dụng hơn. Tương th&iacute;ch với ES6. D&ugrave;ng theo ti&ecirc;u chuẩn web (web standards). B&agrave;i viết n&agrave;y sẽ l&agrave; điểm của phi&ecirc;n bản Angular 2.</span></p>\r\n\r\n<h1><span style=\"font-family:Arial,Helvetica,sans-serif\">Component-Based</span></h1>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tạm biệt Controller v&agrave; $scope, những thứ quen thuộc trong Angular 1. Angular 2 ho&agrave;n to&agrave;n l&agrave; c&aacute;c Component v&agrave; Directive. Mỗi Component l&agrave; c&aacute;c chỉ dẫn tới c&aacute;c template tương ứng. Để h&igrave;nh dung r&otilde; hơn c&oacute; thể nh&igrave;n đoạn code v&iacute; dụ, ta sẽ thấy component n&agrave;y sẽ gọi trong file HTML với tag v&agrave; thực hiện c&aacute;c logic b&ecirc;n trong class AngularComponent.</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>/// \r\nimport {Component, View, bootstrap} from &#39;angular2/angular2&#39;;\r\n\r\n// Annotation section\r\n@Component({\r\n    selector: &#39;angularComponent&#39;\r\n})\r\n\r\n@View({\r\n    templateUrl: &#39;component.html&#39;\r\n})\r\n\r\nclass AngularComponent {\r\n    constructor() {\r\n        // Do something...\r\n    }\r\n}\r\n\r\nbootstrap(AngularComponent);\r\n</code></span></pre>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Lưu &yacute;:</strong>&nbsp;tất cả c&aacute;c component được sử dụng phải được khai b&aacute;o trong bootstrap.</span></p>\r\n\r\n<h1><span style=\"font-family:Arial,Helvetica,sans-serif\">Directives</span></h1>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Directive trong Angular 2 cũng đ&atilde; được đơn giản ho&aacute; đ&aacute;ng kể, chỉ cần g&otilde;&nbsp;<a href=\"https://viblo.asia/u/Directive\" target=\"_blank\">@Directive</a>&nbsp;l&agrave; một directive đ&atilde; được khai b&aacute;o. B&ecirc;n dưới l&agrave; một v&iacute; dụ về khai b&aacute;o một Directive, thậm ch&iacute; trong block &lsquo;host listeners&rsquo;, c&oacute; thể thấy ch&uacute;ng ta c&oacute; thể li&ecirc;n kết c&aacute;c stardard funtion với mục đ&iacute;ch ri&ecirc;ng.</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>/// \r\nimport {Directive} from &#39;angular2/angular2&#39;;\r\n\r\n@Directive({\r\n    selector: &#39;[hover]&#39;,\r\n    properties: [&#39;text: hover&#39;],\r\n    hostListeners: {\r\n        hostListeners: {\r\n            &#39;onmouseenter&#39;: &#39;onMouseEnter()&#39;,\r\n            &#39;onmouseleave&#39;: &#39;onMouseLeave()&#39;\r\n        }\r\n    }\r\n})\r\n\r\nexport class Hover {\r\n    constructor() {\r\n        console.debug(&quot;constructed&quot;);\r\n    }\r\n\r\n    set text(value: string) {\r\n        console.debug(value);\r\n    }\r\n}\r\n</code></span></pre>\r\n\r\n<h1><span style=\"font-family:Arial,Helvetica,sans-serif\">Dependency Injection</span></h1>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bao gồm 3 phần thực hiện:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Injector, gồm c&aacute;c API d&ugrave;ng để inject c&aacute;c dependency v&agrave; l&agrave;m ch&uacute;ng hoạt động.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Bindings, gi&uacute;p định nghĩa c&aacute;c dependency dưới một t&ecirc;n biến để sử dụng.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Cuối c&ugrave;ng, c&aacute;c dependency thực sẽ được khởi tạo v&agrave; c&oacute; thể được inject. Một object c&oacute; thể được truyền th&ocirc;ng qua constructor n&ecirc;n ta chỉ cần sử dụng injector view để thực hiện việc inject đơn giản.</span></li>\r\n</ul>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>// Annotation section\r\n@Component({\r\n    selector: &#39;my-app&#39;,\r\n    viewInjector: [MessageList, httpInjectables]\r\n    // Something else ...\r\n})\r\n</code></span></pre>\r\n\r\n<h1><span style=\"font-family:Arial,Helvetica,sans-serif\">TypeScript v&agrave; lợi &iacute;ch của ECMAScript 6</span></h1>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">EcmaScript 6 ta c&oacute; c&aacute;c class, điều n&agrave;y thực sự rất &yacute; nghĩa.</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>class Person {\r\n    name: string;\r\n\r\n    constructor(name: string) {\r\n        this.name = name;\r\n    }\r\n}\r\n</code></span></pre>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ở c&aacute;c phi&ecirc;n bản ES trước, mọi thứ đều phải định nghĩa qua prototype. Giờ ta c&oacute; thể sử dụng Class giống như c&aacute;c ng&ocirc;n ngữ hướng đối tượng kh&aacute;c một c&aacute;ch rất dễ d&agrave;ng. V&agrave; TypeScript ch&iacute;nh l&agrave; một phần mở rộng của ES6, thực tế cho thấy:</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>TypeScript = ES6 + Types + Annotations</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">TypeScript bắt nguồn từ Microsoft n&ecirc;n được hỗ trợ rất nhiều v&agrave; n&oacute; cũng nổi tiếng trong cộng đồng .NET Developers. TypeScript tuy viết dưới khu&ocirc;n mẫu hướng đối tượng gồm class, kế thừa,&hellip; đầy đủ những c&oacute; thể được bi&ecirc;n dịch ra Javascript, v&igrave; vậy t&iacute;nh tương th&iacute;ch rất cao. Với việc sử dụng TypeScript n&ecirc;n Angular 2 đồng thời cũng ho&agrave;n to&agrave;n c&oacute; thể sử dụng c&aacute;c thư viện d&agrave;nh cho TypeScript một c&aacute;ch dễ d&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tất cả điều đ&oacute; đ&atilde; gi&uacute;p việc hiểu những logic code cực kỳ nhanh v&agrave; hiệu quả nhờ sự quen thuộc.</span></p>\r\n\r\n<h1><span style=\"font-family:Arial,Helvetica,sans-serif\">Generics</span></h1>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y l&agrave; một kh&aacute;i niệm kh&aacute; nổi tiếng v&agrave; c&oacute; phần hơi kh&oacute; nắm bắt trong C#, tuy nhi&ecirc;n khi hiểu th&igrave; việc sử dụng n&oacute; rất hiệu quả, v&agrave; TypeScript đ&atilde; đưa generics l&ecirc;n front-end. V&iacute; dụ b&ecirc;n dưới minh hoạ cho việc ẩn giấu của generics khi c&ugrave;ng một lớp Person nhưng dường như việc chuẩn bị thức ăn sẽ cho ra kết quả kh&aacute;c nhau.</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>export class Person {\r\n    prepareFood() {\r\n        return new Food(&quot;what to prepare&quot;);\r\n    }\r\n}\r\n\r\nexport class Food {\r\n    typeOfFood: string;\r\n\r\n    constructor(typeOfFood: string) {\r\n        this.typeOfFood = typeOfFood;\r\n    }\r\n}\r\n\r\nexport class Female extends Person {\r\n    prepareFood() {\r\n        return new Food(&quot;yummie&quot;);\r\n    }\r\n}\r\n\r\nexport class Male extends Person {\r\n    prepareFood() {\r\n        return new Food(&quot;raw&quot;);\r\n    }\r\n}\r\n</code></span></pre>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong Angular, mọi class hoặc directive được sử dụng đều phải được import v&agrave;o component trước khi ch&uacute;ng thực sự được d&ugrave;ng. V&iacute; dụ b&ecirc;n dưới ta c&oacute; thể thấy NgFor l&agrave; directive đ&atilde; được import để sử dụng cho tất cả c&aacute;c generic (được tao ra từ function prepareFood).</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>/// \r\nimport {Component, View, NgFor, boostrap} from &#39;angular2/angular2&#39;;\r\nimport {Person, Male, Female, Food} from &#39;person&#39;;\r\n\r\n@Component({\r\n    selector: &#39;food-preparation&#39;\r\n})\r\n\r\n@View({\r\n    templateUrl: &#39;food.html&#39;,\r\n    directives: [NgFor]\r\n})\r\n\r\nclass FoodPreparationComponent {\r\n    foods: Array;\r\n\r\n    constructor() {\r\n        this.foods = [];\r\n        this.foods.push(prepareFood(new Male()));\r\n        this.foods.push(prepareFood(new Female()));\r\n    }\r\n}\r\n\r\nfunction prepareFood(cooker: T): string {\r\n    return cooker.prepareFood().typeOfFood;\r\n}\r\n\r\nboostrap(FoodPreparationComponent);\r\n</code></span></pre>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cuối c&ugrave;ng ta chỉ việc show ra HTML, trong đ&oacute; kiểu T sẽ tự nhận biệt đối tượng v&agrave; function tương ứng cần thực hiện.</span></p>\r\n\r\n<pre>\r\n\r\n&nbsp;</pre>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><code>Dishes:</code></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><code>{{ food }} </code></span></li>\r\n</ul>\r\n\r\n<h1><span style=\"font-family:Arial,Helvetica,sans-serif\">Lambdas với TypeScript</span></h1>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Một kiểu khai b&aacute;o th&uacute; vị nữa tiếp tục được đưa v&agrave;o sử dụng với TypeScript, c&oacute; thể hiểu Lambdas expression sẽ định nghĩa một anonymous function, một c&aacute;ch khai b&aacute;o đẹp v&agrave; nhanh gọn. Nếu bạn đ&atilde; sử dụng C# hoặc Scala th&igrave; sẽ cảm nhận r&otilde; hơn sự th&uacute; vị của n&oacute;.</span></p>\r\n\r\n<pre>\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\"><code>pushMessage(...messageStrings: Array) {\r\n    this.messages.push(\r\n        ...messageStrings.map((m) =&gt; {\r\n            return new Message(m, new Date(), &quot;Sender name&quot;);\r\n        })\r\n    );\r\n}\r\n</code></span></pre>\r\n\r\n<h1><span style=\"font-family:Arial,Helvetica,sans-serif\">Ưu điểm</span></h1>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">1. Angular2 sẽ dễ d&agrave;ng hơn:</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Angular2 được ph&aacute;t triển tr&ecirc;n nền tảng hiện đại hơn, nhiều t&iacute;nh năng, v&agrave; dễ d&agrave;ng cho người mới học hơn l&agrave; Angular 1.x v&agrave; cũng dễ d&agrave;ng hơn cho c&aacute;c chiến binh l&acirc;u năm.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Với Angular 1, c&aacute;c nh&agrave; ph&aacute;t triển đ&atilde; biết về sự kh&aacute;c nhau giữa Controllers, Services, Factories, Providers v&agrave; c&aacute;c kh&aacute;i niệm kh&aacute;c c&oacute; thể g&acirc;y ra sự kh&oacute; hiểu đặc biệt l&agrave; c&aacute;c coder mới.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Angular 2 l&agrave; một framework với sự sắp xếp hợp l&yacute; hơn cho ph&eacute;p lập tr&igrave;nh vi&ecirc;n tập trung v&agrave;o việc tạo l&ecirc;n c&aacute;c class JavaScript đơn giản. Views v&agrave; c&aacute;c controllers sẽ được thay thế bởi Components, thứ m&agrave; c&oacute; thể m&ocirc; tả l&agrave; một sự tinh chế mới của directives. Ngay cả c&aacute;c lập tr&igrave;nh vi&ecirc;n c&oacute; kinh nghiệm với Angular cũng kh&ocirc;ng chắc l&agrave; c&oacute; thể biết tất cả những khả năng của directives. Angular 2 component đ&atilde; tăng khả năng đọc đ&aacute;ng kể, c&aacute;c API của ch&uacute;ng giảm đ&aacute;ng kể c&aacute;c biệt ngữ hơn l&agrave; Angular 1.x directive.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">2. TypeScript</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Angular2 được viết với TypeScript, một superset của JavaScript, n&oacute; triển khai nhiều t&iacute;nh năng của ES2016 v&agrave; c&aacute;c t&iacute;nh năng của TypeScript.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bằng c&aacute;ch tập trung tạo n&ecirc;n một framework dễ d&agrave;ng hơn trong việc xử l&yacute;, Angular2 cho ph&eacute;p hệ sinh th&aacute;i ph&aacute;t triển phong ph&uacute; hơn. C&aacute;c lập tr&igrave;nh vi&ecirc;n c&oacute; thể sử dụng c&aacute;c tr&igrave;nh soạn thảo tinh vi hơn với c&aacute;c t&iacute;nh năng gợi &yacute; code hay tự động ho&agrave;n th&agrave;nh code. Những t&iacute;nh năng n&agrave;y gi&uacute;p giảm thời gian học Angular2. May mắn hơn nữa cho c&aacute;c nh&agrave; ph&aacute;t triển ES5 truyền thống rằng kh&ocirc;ng nhất thiết phải viết ES6 hay TypeScript, c&aacute;c coder c&oacute; thể vẫn viết JavaScript thuần m&agrave; kh&ocirc;ng cần bi&ecirc;n dịch.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">3. Quen thuộc</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Mặc d&ugrave; được viết lại ho&agrave;n to&agrave;n, Angular2 vẫn giữ lại c&aacute;c kh&aacute;i niệm cốt l&otilde;i v&agrave; c&aacute;c quy tắc của Angular1.x. Điều đ&oacute; c&oacute; nghĩa l&agrave; c&aacute;c lập tr&igrave;nh vi&ecirc;n giỏi v&oacute;i Angular1 sẽ dễ d&agrave;ng chuyển đổi sang Angular2 hơn l&agrave; c&aacute;c thư viện kh&aacute;c như ReactJS....</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">4. Hiệu năng v&agrave; tương th&iacute;ch mobile</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Angular2 được thiết kế cho mobile từ ban đầu. Bỏ qua một b&ecirc;n c&aacute;c vấn đề về sức mạnh xử l&yacute;, c&aacute;c thiết bị di động c&oacute; c&aacute;c t&iacute;nh năng kh&aacute;c v&agrave; sự giới hạn đ&oacute; ph&acirc;n biệt ch&uacute;ng với m&aacute;y t&iacute;nh truyền thống. Giao diện chạm, m&agrave;n h&igrave;nh nhỏ, phần cứng đ&atilde; được t&iacute;nh to&aacute;n trong Angular2. M&aacute;y t&iacute;nh để b&agrave;n cũng sẽ được cải tiến về hiệu suất v&agrave; khả năng đ&aacute;p ứng.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Angular2 giống React v&agrave; c&aacute;c framework hiện đại kh&aacute;c, c&oacute; thể tăng hiệu năng bằng c&aacute;ch render ra HTML tr&ecirc;n server hoặc ngay cả tr&ecirc;n browser. Phụ thuộc v&agrave;o thiết kế cấu tr&uacute;c gi&uacute;p cho trải nghiệm người d&ugrave;ng tốt hơn.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Y&ecirc;u cầu về hiệu năng kh&ocirc;ng bao giờ l&agrave; dừng lại với việc tiền xử l&yacute;. Angular 2 c&oacute; khả năng t&iacute;ch hợp với mobile bằng c&aacute;ch t&iacute;ch hợp với NativeScript, một thư viện m&atilde; nguồn mở l&agrave; cậu nỗi giữa JavaScript v&agrave; mobile. Bổ sung th&ecirc;m, Ionic cũng đang được ph&aacute;t triển tr&ecirc;n Angular2 cung cấp một c&aacute;ch kh&aacute;c gi&uacute;p tăng hiệu quả của ứng dụng tr&ecirc;n mobile.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">5. Kiến tr&uacute;c dự &aacute;n v&agrave; bảo tr&igrave;</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Phi&ecirc;n bản đầu ti&ecirc;n của Angular cung cấp cho c&aacute;c lập tr&igrave;nh vi&ecirc;n một thư viện mềm dẻo để ph&aacute;t triển ứng dụng. Đ&acirc;y l&agrave; sự thay đổi đ&aacute;ng kể cho c&aacute;c lập tr&igrave;nh vi&ecirc;n web, v&agrave; trong khi framework rất hữu &iacute;ch th&igrave; n&oacute; l&agrave; một bằng chứng cho sự linh hoạt đ&oacute;. Qua thời gian, c&aacute;c best practice từ cộng đồng được t&aacute;n th&agrave;nh rộng r&atilde;i.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Angular 1.x đ&atilde; cố gắng l&agrave;m việc xung quanh c&aacute;c hạn chế tr&ecirc;n tr&igrave;nh duyệt li&ecirc;n quan đến JavaScript. Điều n&agrave;y được giải quyết bằng c&aacute;ch cung cấp một hệ thống c&aacute;c module sử dụng cơ chế Dependency Injection. Điều n&agrave;y thật tuyệt tuy nhi&ecirc;n c&oacute; một v&agrave;i vấn đề.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Angular 2.x sử dụng hệ thống module ECMA Script 6 (ES6), v&agrave; c&aacute;c c&ocirc;ng cụ đ&oacute;ng g&oacute;i hiện đại như Webpack hoặc SystemJS. Module được t&ugrave;y biến theo c&aacute;ch ri&ecirc;ng của Angular để dễ d&agrave;ng viết c&aacute;c Generic cho JS để cắm v&agrave;o Angular. Hệ thống module mới gi&uacute;p dễ d&agrave;ng ph&aacute;t triển c&aacute;c dự &aacute;n lớn hiệu quả hơn.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">6. T&iacute;nh năng mới</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Một v&agrave;i t&iacute;nh năng th&uacute; vị tr&ecirc;n Angular2:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Form Builder</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Change Detection</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Templating</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Routing</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Annotations</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Observables</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Shadow DOM</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">T&oacute;m lại: tr&ecirc;n đ&acirc;y l&agrave; những điều m&igrave;nh đ&atilde; t&igrave;m hiểu đc ở Angular2, phần tới ch&uacute;ng ta sẽ nghi&ecirc;n cứu kỹ hơn về những t&iacute;nh năng mới của Angular2 đ&atilde; kể ở tr&ecirc;n, cảm ơn mọi người.</span></p>\r\n', 'angularJS.png', 2, 4, 1, 20, NULL, '2020-04-11 08:49:20', '2020-06-03 02:22:28');
INSERT INTO `posts` (`id`, `title`, `description`, `contents`, `thumbnail`, `user_id`, `category_id`, `status`, `view`, `rate`, `created_at`, `updated_at`) VALUES
(10, 'PHP 8 đã được phát triển, 2 năm nữa ra mắt', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">PHP sẽ được n&acirc;ng cấp l&ecirc;n phi&ecirc;n bản 8 v&agrave;o ch&iacute;nh x&aacute;c ng&agrave;y 23/9/2021, liệu đợi chờ c&oacute; hạnh ph&uacute;c khi m&agrave; Python v&agrave; Nodejs đ&atilde; trở n&ecirc;n qu&aacute; thịnh h&agrave;nh trong lập tr&igrave;nh web những năm vừa qua?</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">PHP sẽ được n&acirc;ng cấp l&ecirc;n phi&ecirc;n bản 8 v&agrave;o ch&iacute;nh x&aacute;c ng&agrave;y 23/9/2021, liệu đợi chờ c&oacute; hạnh ph&uacute;c khi m&agrave; Python v&agrave; Nodejs đ&atilde; trở n&ecirc;n qu&aacute; thịnh h&agrave;nh trong lập tr&igrave;nh web những năm vừa qua? H&atilde;y c&ugrave;ng Fullstack Station điểm qua những th&agrave;nh tựu v&agrave; ng&oacute;ng chờ tương lai của PHP 8 xem như thế n&agrave;o nh&eacute;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trước khi t&igrave;m hiểu PHP 8, nếu bạn chưa biết PHP 7 c&oacute; g&igrave; mới, th&igrave; h&atilde;y xem b&agrave;i&nbsp;<a href=\"https://fullstackstation.com/tim-hieu-php-7/\">n&agrave;y</a>.</span></p>\r\n\r\n<blockquote><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://twitter.com/Crell\"><img alt=\"\" src=\"https://pbs.twimg.com/profile_images/859893683319123969/BVz-GOIl_normal.jpg\" /></a></span>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://twitter.com/Crell\">Larry Garfield@Crell</a></span></p>\r\n\r\n<p dir=\"ltr\"><span style=\"font-family:Arial,Helvetica,sans-serif\">September 23rd, 2021, release date for <a dir=\"ltr\" href=\"https://twitter.com/hashtag/PHP?src=hash\" rel=\"tag\">#PHP</a> 8. <a dir=\"ltr\" href=\"https://twitter.com/SaraMG\">@SaraMG</a> predicts it! <a dir=\"ltr\" href=\"https://twitter.com/hashtag/SymfonyCon?src=hash\" rel=\"tag\">#SymfonyCon</a></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://twitter.com/intent/like?tweet_id=931427244760846336\" title=\"Thích\">20</a></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://twitter.com/Crell/status/931427244760846336\">14:42 - 17 thg 11, 2017</a></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://support.twitter.com/articles/20175256\" title=\"Thông tin và quyền riêng tư Quảng cáo Twitter\">Th&ocirc;ng tin v&agrave; quyền ri&ecirc;ng tư Quảng c&aacute;o Twitter</a></span></p>\r\n</blockquote>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://twitter.com/Crell\" title=\"Xem hồ sơ của Larry Garfield trên Twitter\">Xem c&aacute;c Tweet kh&aacute;c của Larry Garfield</a></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nội dung ch&iacute;nh&nbsp;[<a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#\">ẩn</a>]</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#De_che_PHP\">Đế chế PHP</a></span>\r\n\r\n	<ul>\r\n		<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#Nhung_cong_ty_lon_su_dung_PHP\">Những c&ocirc;ng ty lớn sử dụng PHP</a></span></li>\r\n		<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#Lieu_PHP_co_bien_mat\">Liệu PHP c&oacute; biến mất?</a></span></li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#PHP_8_se_co_Just_In_Time_JIT\">PHP 8 sẽ c&oacute; Just In Time (JIT)</a></span>\r\n	<ul>\r\n		<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#Just_In_Time_JIT_la_gi\">Just In Time (JIT) l&agrave; g&igrave;</a></span></li>\r\n		<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#Tai_sao_PHP_can_JIT\">Tại sao PHP cần JIT</a></span>\r\n		<ul>\r\n			<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#PHP_da_phat_trien_het_muc_co_the\">PHP đ&atilde; ph&aacute;t triển hết mức c&oacute; thể</a></span></li>\r\n			<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#PHP_danh_cho_nhung_tac_vu_khac\">PHP d&agrave;nh cho những t&aacute;c vụ kh&aacute;c</a></span></li>\r\n			<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#Phat_trien_cac_tinh_nang_khac_cho_PHP\">Ph&aacute;t triển c&aacute;c t&iacute;nh năng kh&aacute;c cho PHP</a></span></li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#Can_lam_gi_de_tro_thanh_lap_trinh_vien_PHP_gioi\">Cần l&agrave;m g&igrave; để trở th&agrave;nh lập tr&igrave;nh vi&ecirc;n PHP giỏi?</a></span>\r\n	<ul>\r\n		<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#Ky_nang_lap_trinh\">Kỹ năng lập tr&igrave;nh</a></span></li>\r\n		<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#Hieu_ro_ngon_ngu_PHP\">Hiểu r&otilde; ng&ocirc;n ngữ PHP</a></span></li>\r\n		<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#Su_dung_framework_hieu_qua\">Sử dụng framework hiệu quả</a></span></li>\r\n	</ul>\r\n	</li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#Fullstack_Station_Tip\">Fullstack Station Tip</a></span>\r\n	<ul>\r\n		<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#Co_nen_hoc_PHP_khong\">C&oacute; n&ecirc;n học PHP kh&ocirc;ng?</a></span></li>\r\n		<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#Tham_khao\">Tham khảo</a></span></li>\r\n		<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://fullstackstation.com/php-8-doi-cho-la-hanh-phuc/#Comments\">Comments</a></span></li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Đế chế PHP</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">PHP l&agrave; ng&ocirc;n ngữ lập tr&igrave;nh của năm 2004 (theo&nbsp;<a href=\"https://www.tiobe.com/tiobe-index/php/\" rel=\"noreferrer noopener\" target=\"_blank\">Tiobe Index</a>), đ&atilde; trải qua 25 năm (từ 1994) ph&aacute;t triển, chỉ duy nhất 1 lần đạt danh hiệu &ldquo;Ng&ocirc;n ngữ lập tr&igrave;nh của năm&rdquo; của Tiobe (tuổi đời PHP&gt;Tiobe), tuy nhi&ecirc;n độ phủ cao với việc lu&ocirc;n nằm trong top 10 ng&ocirc;n ngữ lập tr&igrave;nh được sử dụng v&agrave; những th&agrave;nh tựu m&agrave; PHP mang lại cho lập tr&igrave;nh web thật to lớn với 79% website sử dụng PHP (theo&nbsp;<a href=\"https://w3techs.com/technologies/overview/programming_language/all\" rel=\"noreferrer noopener\" target=\"_blank\">w3techs</a>). PHP l&agrave; một đế chế h&ugrave;ng mạnh!</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Những c&ocirc;ng ty lớn sử dụng PHP</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c c&ocirc;ng ty Google, Yahoo, Facebook, Wikipedia, WordPress đều sử dụng PHP, v&agrave; đặc biệt nhất c&oacute; lẽ l&agrave; Facebook mạng x&atilde; hội lớn nhất hiện nay, sử dụng PHP ngay trong những ng&agrave;y đầu v&agrave; đ&atilde; tạo ra ng&ocirc;n ngữ Hack tương th&iacute;ch với PHP, v&agrave; vẫn c&ograve;n lượng lớn m&atilde; code sử dụng PHP cho đến nay.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Liệu PHP c&oacute; biến mất?</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">PHP sẽ tồn tại &iacute;t nhất 10 năm nữa, m&igrave;nh tin l&agrave; như vậy. Phải n&oacute;i PHP gi&uacute;p ch&uacute;ng ta tạo ra web rất nhanh v&agrave; cần &iacute;t kinh nghiệm, cũng như c&aacute;c điều kiện để vận h&agrave;nh đơn giản nhất. Đ&oacute; l&agrave; l&yacute; do v&igrave; sao c&aacute;c c&ocirc;ng ty lớn ở tr&ecirc;n vẫn c&ograve;n sử dụng PHP, v&agrave; khoảng 79% website đang sử dụng PHP, vậy tại sao phải lo PHP sẽ biến mất?</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">D&ugrave; c&ocirc;ng nghệ n&agrave;o ra đời, c&ocirc;ng ty n&agrave;o ra đời họ cũng cần c&oacute; 1 website, l&agrave; bộ mặt, l&agrave; nơi kết nối mọi người. H&atilde;y nhớ: mạng x&atilde; hội chỉ l&agrave; nơi chia sẽ th&ocirc;ng tin t&oacute;m lược, website l&agrave; nơi chứa th&ocirc;ng tin chi tiết. C&ograve;n website, c&ograve;n PHP!</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">So s&aacute;nh PHP với Node v&agrave; Python, th&igrave; hiện tại Python đang kh&aacute; mạnh. Tuy nhi&ecirc;n, PHP vẫn hơn Nodejs, v&agrave; chỉ thua Python kh&ocirc;ng nhiều lắm.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đế chế PHP sẽ c&ograve;n tồn tại l&acirc;u lắm, nếu bạn c&ograve;n lập tr&igrave;nh PHP th&igrave; h&atilde;y xem th&ecirc;m b&agrave;i &ldquo;<a href=\"https://fullstackstation.com/top-nhung-loi-mysql-cua-lap-trinh-vien-php-cu-ma-moi/\">Những lỗi lập tr&igrave;nh vi&ecirc;n PHP hay mắc phải</a>&ldquo;</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">PHP 8 sẽ c&oacute; Just In Time (JIT)</span></h2>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Just In Time (JIT) l&agrave; g&igrave;</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;JIT&rdquo; viết tắt cho Just In Time. JIT l&agrave; một kĩ thuật gi&uacute;p hệ thống tối ưu lại những đoạn code.</span></p>\r\n\r\n<blockquote>\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Jit compiler chạy sau khi chương tr&igrave;nh được khởi tạo v&agrave; bi&ecirc;n dịch lại những đoạn code (thường l&agrave; bytecode hoặc l&agrave; một dạng tương tự để c&aacute;c Virtual Machine c&oacute; thể hiểu được) gi&uacute;p cho ứng dụng chạy nhanh hơn. Jit c&oacute; thể truy xuất v&agrave;o c&aacute;c c&acirc;u truy xuất tại thời điểm &ldquo;Runtime&rdquo; trong khi c&aacute;c compiler kh&aacute;c kh&ocirc;ng thể v&agrave; từ đ&oacute; chọn lọc được ra những function m&agrave; thường xuy&ecirc;n được sử dụng từ đ&oacute; c&oacute; thể tối ưu gi&uacute;p chương tr&igrave;nh thực thi hiệu quả v&agrave; nhanh hơn.</span></p>\r\n</blockquote>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Zeev, một trong những core developer của PHP đ&atilde; c&oacute; một demo kh&aacute; l&agrave; trực quan cho những g&igrave; JIT c&oacute; thể mang lại.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Tại sao PHP cần JIT</span></h3>\r\n\r\n<h4><span style=\"font-family:Arial,Helvetica,sans-serif\">PHP đ&atilde; ph&aacute;t triển hết mức c&oacute; thể</span></h4>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&atilde; c&oacute; rất nhiều cải tiến cho PHP từ phi&ecirc;n bản 7.0 như tối ưu HashTable (một kiến tr&uacute;c lưu trữ data trong php), đặc biệt tối ưu Zend VM cho opcodes v&agrave; nhiều chức năng kh&aacute;c (xem th&ecirc;m b&agrave;i&nbsp;<a href=\"https://fullstackstation.com/tim-hieu-php-7/\">t&igrave;m hiểu PHP 7</a>). Sau tất cả những cải tiến đ&oacute;, PHP đ&atilde; đến cực đại để c&oacute; thể ph&aacute;t triển th&ecirc;m. V&igrave; vậy PHP 8 với JIT c&oacute; thể sẽ l&agrave; người thay đổi cuộc chơi.</span></p>\r\n\r\n<h4><span style=\"font-family:Arial,Helvetica,sans-serif\">PHP d&agrave;nh cho những t&aacute;c vụ kh&aacute;c</span></h4>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">N&oacute;i tới PHP, ta nghĩ ngay tới một ng&ocirc;n ngữ d&agrave;nh cho ph&aacute;t triển Web. Tuy nhi&ecirc;n, những t&aacute;c vụ li&ecirc;n quan tới việc t&iacute;nh to&aacute;n cần sử dụng CPU ở cường độ cao th&igrave; &iacute;t khi được sử dụng. Với việc đưa v&agrave;o hỗ trợ JIT, PHP c&oacute; thể được sử dụng cho nhiều mục đ&iacute;ch kh&aacute;c kh&ocirc;ng phải web, c&oacute; thể tập trung v&agrave;o CPU nơi m&agrave; lợi &iacute;ch về hiệu năng l&agrave; rất quan trọng.</span></p>\r\n\r\n<h4><span style=\"font-family:Arial,Helvetica,sans-serif\">Ph&aacute;t triển c&aacute;c t&iacute;nh năng kh&aacute;c cho PHP</span></h4>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Với việc sử dụng JIT, đội ngũ ph&aacute;t triển c&oacute; thể sử dụng ch&iacute;nh PHP để tạo ra c&aacute;c t&iacute;nh năng mới thay v&igrave; sử dụng ng&ocirc;n ngữ C m&agrave; kh&ocirc;ng c&oacute; sự kh&aacute;c biệt qu&aacute; nhiều về hiệu năng. Điều n&agrave;y gi&uacute;p cho giảm thiểu sự ảnh hưởng trong việc quản l&yacute; bộ nhớ, tr&agrave;n bộ nhớ v&agrave; c&aacute;c vấn đề li&ecirc;n quan tương tự khi ph&aacute;t triển dựa tr&ecirc;n C.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Cần l&agrave;m g&igrave; để trở th&agrave;nh lập tr&igrave;nh vi&ecirc;n PHP giỏi?</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://fullstackstation.com/wp-content/uploads/2019/05/4r72lpfh4ik.jpg\" style=\"height:400px; width:600px\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">V&igrave; điều kiện để trở th&agrave;nh lập tr&igrave;nh vi&ecirc;n PHP tương đối thấp v&agrave; khả năng triển khai website bằng PHP đơn giản n&ecirc;n thường th&igrave; lập tr&igrave;nh vi&ecirc;n PHP l&agrave; lập tr&igrave;nh vi&ecirc;n tồi, v&igrave; t&iacute;nh m&igrave; ăn liền, đơn giản, uyển chuyển của ng&ocirc;n ngữ. Nhiều lập tr&igrave;nh vi&ecirc;n rất gh&eacute;t PHP, đặc biệt l&agrave; c&aacute;c bạn từ c&aacute;c ng&ocirc;n ngữ C, C++, Java, Rust, Go (static typed), h&atilde;y &ldquo;kệ người ta&rdquo;, đừng bắt con c&aacute; leo c&acirc;y, n&oacute; sẽ tưởng m&igrave;nh ngu cả đời, đ&acirc;y l&agrave; website l&agrave; đất diễn của PHP. Sau đ&acirc;y l&agrave; một số ti&ecirc;u ch&iacute; v&agrave; định hướng để trở th&agrave;nh lập tr&igrave;nh vi&ecirc;n v&agrave; lập tr&igrave;nh vi&ecirc;n PHP giỏi m&agrave; m&igrave;nh tổng hợp muốn chia sẽ với c&aacute;c bạn.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Kỹ năng lập tr&igrave;nh</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Kỹ năng lập tr&igrave;nh bao gồm thuật to&aacute;n, cấu tr&uacute;c v&agrave; dữ liệu, kiến thức về hệ thống, lập tr&igrave;nh hướng đối tượng OOP, kỹ thuật tối ưu, kỹ thuật viết code sạch, s&aacute;ng sủa, khả năng viết test, unit test. Những kỹ năng n&agrave;y th&igrave; lập tr&igrave;nh vi&ecirc;n cho mảng n&agrave;o cũng cần (Xem th&ecirc;m&nbsp;<a href=\"https://fullstackstation.com/cac-chieu-thuc-trong-lap-trinh/\">C&aacute;c chi&ecirc;u thức trong lập tr&igrave;nh</a>).</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cao hơn th&igrave; quản l&yacute; m&aacute;y chủ, CI/CD; quản l&yacute;, sử dụng vận h&agrave;nh docker, v&iacute; dụ&nbsp;<a href=\"https://fullstackstation.com/docker-thuc-chien-moi-truong-phat-trien/\">sử dụng docker trong m&ocirc;i trường ph&aacute;t triển.</a>&nbsp;Nghi&ecirc;n cứu về bảo mật (xem&nbsp;<a href=\"https://fullstackstation.com/cong-cu-tim-kiem-loi-bao-mat-web-chat-luong-ma-mien-phi/\">C&ocirc;ng cụ t&igrave;m lỗi bảo mật</a>)</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiểu r&otilde; ng&ocirc;n ngữ PHP</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn cần nắm vững c&aacute;c h&agrave;m, c&aacute;ch thức tối ưu trong PHP, hạn chế c&aacute;c lỗi m&agrave; lập tr&igrave;nh vi&ecirc;n PHP hay mắc phải, v&iacute; dụ&nbsp;<a href=\"https://fullstackstation.com/top-nhung-loi-mysql-cua-lap-trinh-vien-php-cu-ma-moi/\">C&aacute;c lỗi khi l&agrave;m việc với mysql lập tr&igrave;nh vi&ecirc;n PHP</a>, hay ph&acirc;n biệt isset(), is_null(), empty().</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Phải sử dụng c&aacute;c c&ocirc;ng cụ như PHPMD, PHPCS để viết code hiệu quả, tăng cường t&iacute;nh dễ đọc, dễ bảo tr&igrave;, hiệu quả thực thi cho code.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Sử dụng framework hiệu quả</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; rất nhiều PHP Framework như: CodeIgniter, Zend, Symfony, Laravel, Yii,.. cao hơn l&agrave; CMS như WordPress, Joomla, Drupal&hellip; Sử dụng framework hay CMS n&agrave;o l&agrave; c&aacute;i duy&ecirc;n của mỗi người, v&iacute; dụ bạn th&iacute;ch m&agrave; c&ocirc;ng ty đang kh&ocirc;ng sử dụng th&igrave; cũng chịu. V&iacute; dụ m&igrave;nh chưa bao giờ sử dụng Laravel, hơi sốc khi biết Laravel nắm khoảng ~40% thị phần PHP Framework nhưng tới giờ vẫn chưa c&oacute; dự &aacute;n n&agrave;o d&ugrave;ng Laravel.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Việc biết, sử dụng nhiều framework l&agrave;&nbsp;<strong>kh&ocirc;ng tốt</strong>, v&igrave; kiến thức bị ph&acirc;n mảnh. Kiến thức từ c&aacute;c framework thực sự kh&aacute; kh&aacute;c biệt n&ecirc;n kh&oacute; tận dụng được kinh nghiệm từ framework trước. Bạn cần x&aacute;c định r&otilde; con đường m&igrave;nh muốn đi ra sao, nếu muốn đi l&agrave;m việc tại quốc gia n&agrave;o, th&igrave; t&igrave;m hiểu framework thịnh h&agrave;nh tại quốc gia đ&oacute;. Tốt nhất l&agrave; đừng chạy theo framework, đừng nghe ai đ&oacute; n&oacute;i Framework n&agrave;y tốt, c&aacute;i kia tốt. Tập trung 1 c&aacute;i th&ocirc;i, rồi học c&aacute;c kỹ năng lập tr&igrave;nh th&ecirc;m.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Fullstack Station Tip</span></h2>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; n&ecirc;n học PHP kh&ocirc;ng?</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">M&igrave;nh c&oacute; dự &aacute;n đầu ti&ecirc;n, c&ocirc;ng việc đầu ti&ecirc;n v&agrave; đi l&agrave;m việc ở Nhật nhờ ng&ocirc;n ngữ lập tr&igrave;nh PHP, tất nhi&ecirc;n đ&oacute; l&agrave; c&acirc;u chuyện của 10 năm trước, nhưng với sự ph&aacute;t triển kh&aacute; mạnh mẽ của WordPress, c&ocirc;ng việc bằng ng&ocirc;n ngữ lập tr&igrave;nh PHP sẽ rất nhiều. &Iacute;t nhất l&agrave; hiện tại ở Nhật Bản, PHP vẫn được ưu chuộng v&agrave; kh&aacute; dễ kiếm việc.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>V&igrave; vậy c&acirc;u trả lời l&agrave; c&oacute;.</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ngo&agrave;i ra, kế hoạch ra đời PHP 8 minh chứng cho sự ph&aacute;t triển kh&ocirc;ng dừng lại của PHP. Mặc d&ugrave; trong lĩnh vực kh&ocirc;ng phải l&agrave; web, th&igrave; PHP kh&ocirc;ng thực sự mạnh, nhưng PHP 8 c&oacute; thể sẽ thay đổi cuộc chơi. PHP 7 được ra mắt đ&atilde; gi&uacute;p PHP lấy điểm trở lại trong lĩnh vực lập tr&igrave;nh web với tốc độ cải thiện hơn 50% so với 5. PHP 8 sẽ c&ograve;n nhanh hơn PHP 7, vậy c&ograve;n g&igrave; tuyệt hơn?</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ng&agrave;y nay, sự ph&aacute;t triển của c&ocirc;ng nghệ qu&aacute; nhanh ch&oacute;ng, tr&agrave;o lưu AI v&agrave; Blockchain nở rộ, việc học PHP c&oacute; thể được xem như lỗi thời. Nhưng m&igrave;nh nghĩ c&ocirc;ng nghệ g&igrave; cũng cần phải no bụng đ&atilde;, PHP l&agrave; lựa chọn kh&ocirc;ng hề tệ để dễ d&agrave;ng kiếm việc. Việc l&agrave;m trong AI v&agrave; Blockchain kh&ocirc;ng hề đơn giản, c&oacute; thể lương cao, nhưng cạnh tranh cũng rất khốc liệt. M&agrave;i dũa kỹ năng lập tr&igrave;nh với PHP, rồi học tiếp th&ecirc;m c&aacute;c ng&ocirc;n ngữ kh&aacute;c, c&ocirc;ng nghệ kh&aacute;c tuỳ theo nhu cầu v&agrave; thời điểm.</span></p>\r\n', 'php8.png', 6, 2, 1, 52, NULL, '2020-04-12 02:18:36', '2020-07-04 03:04:48'),
(11, 'Ứng dụng AI trong bảo mật thông tin và an ninh mạng', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tr&iacute; tuệ nh&acirc;n tạo (AI) đ&atilde; v&agrave; đang ph&aacute;t triển nhanh ch&oacute;ng, vượt ra ngo&agrave;i c&aacute;c lĩnh vực học thuật, nghi&ecirc;n cứu để bước v&agrave;o d&ograve;ng chảy thương mại, với c&aacute;c sản phẩm s&aacute;ng tạo, gi&uacute;p con người thay đổi c&aacute;ch tiếp cận v&agrave; tận dụng th&ocirc;ng tin.</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Tr&iacute; tuệ nh&acirc;n tạo (AI)</em>&nbsp;đ&atilde; v&agrave; đang ph&aacute;t triển nhanh ch&oacute;ng, vượt ra ngo&agrave;i c&aacute;c lĩnh vực học thuật, nghi&ecirc;n cứu để bước v&agrave;o d&ograve;ng chảy thương mại, với c&aacute;c sản phẩm s&aacute;ng tạo, gi&uacute;p con người thay đổi c&aacute;ch tiếp cận v&agrave; tận dụng th&ocirc;ng tin. Tuy nhi&ecirc;n, bạn đ&atilde; thực sự hiểu thế n&agrave;o l&agrave; AI v&agrave; ML, cũng như ứng dụng của ch&uacute;ng trong lĩnh vực bảo mật th&ocirc;ng tin &amp; an ninh mạng?</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">AI V&Agrave; ML L&Agrave; G&Igrave;?</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Trí tuệ nhân tạo artificial intelligence cystack\" src=\"https://s.cystack.net/resource/home/content/03134706/tech-the-tri%CC%81_tue%CC%A3%CC%82_nha%CC%82n_ta%CC%A3o-01-1024x585.png\" style=\"height:343px; width:600px\" /></span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Tr&iacute; tuệ nh&acirc;n tạo (AI)</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tr&iacute; tuệ nh&acirc;n tạo (Artificial Intelligence, hay AI) l&agrave; tr&iacute; tuệ được thể hiện bởi hệ thống m&aacute;y m&oacute;c với những điểm gần giống với tr&iacute; tuệ con người. Những hệ thống n&agrave;y c&oacute; c&aacute;c khả năng như học kiến thức mới, nhận diện v&agrave; phản ứng với th&ocirc;ng tin, hay tự t&igrave;m c&aacute;ch giải quyết một vấn đề.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Học m&aacute;y (ML)</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Học m&aacute;y (Machine Learning, hay ML) l&agrave; một nh&aacute;nh của tr&iacute; tuệ nh&acirc;n tạo đề cập đến c&ocirc;ng nghệ cho ph&eacute;p c&aacute;c m&aacute;y t&iacute;nh học hỏi v&agrave; th&iacute;ch nghi một c&aacute;ch tự động. N&oacute; m&ocirc; phỏng nhận thức của con người &ndash; tức l&agrave; học dựa tr&ecirc;n kinh nghiệm v&agrave; khu&ocirc;n mẫu.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;ch mạng c&ocirc;ng nghệ 4.0 nổ ra, chứng kiến sự b&ugrave;ng nổ v&agrave; ph&aacute;t triển mạnh mẽ của c&ocirc;ng nghệ AI v&agrave; ML. C&aacute;c th&agrave;nh tựu to lớn đạt được thể hiện trong sức mạnh t&iacute;nh to&aacute;n v&agrave; sự ph&aacute;t triển của c&aacute;c m&ocirc; h&igrave;nh như t&iacute;nh to&aacute;n ph&acirc;n to&aacute;n, big data, điện to&aacute;n đ&aacute;m m&acirc;y.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Ứng dụng của AI v&agrave; ML</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c ứng dụng thương mại đầu ti&ecirc;n của ML được ti&ecirc;n phong bởi những g&atilde; khổng lồ c&ocirc;ng nghệ như Google (trong c&ocirc;ng cụ t&igrave;m kiếm), Amazon (với hệ thống gợi &yacute; sản phẩm) v&agrave; Facebook (với trang news feed). C&aacute;c doanh nghiệp n&agrave;y đ&atilde; x&acirc;y dựng một kho t&agrave;ng dữ liệu h&agrave;nh vi v&ocirc; c&ugrave;ng qu&yacute; gi&aacute; từ h&agrave;ng trăm triệu người d&ugrave;ng. Để thu thập, tổ chức, ph&acirc;n t&iacute;ch dữ liệu một c&aacute;ch hiệu quả, họ đ&atilde; kh&ocirc;ng ngừng x&acirc;y dựng, ph&aacute;t triển v&agrave; nghi&ecirc;n cứu thuật to&aacute;n, c&ocirc;ng nghệ.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">ỨNG DỤNG AI TRONG BẢO MẬT TH&Ocirc;NG TIN &amp; AN NINH MẠNG</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"cystack ai trí tuệ nhân tạo bảo vệ website\" src=\"https://s.cystack.net/resource/home/content/03134656/tech-the-robot_ba%CC%89o_ve%CC%A3%CC%82_website-01.png\" style=\"height:343px; width:600px\" /></span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Ph&aacute;t hiện sớm c&aacute;c cuộc tấn c&ocirc;ng</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">AI ph&aacute;t triển nhanh ch&oacute;ng, t&iacute;nh ứng dụng của n&oacute; với c&aacute;c lĩnh vực chỉ c&ograve;n phụ thuộc v&agrave;o khả năng s&aacute;ng tạo của con người. C&aacute;c tổ chức đ&atilde; bắt đầu sử dụng AI để tăng cường an ninh mạng v&agrave; cung cấp nhiều biện ph&aacute;p bảo vệ chống lại c&aacute;c tin tặc tinh vi. AI c&oacute; thể tự động h&oacute;a c&aacute;c quy tr&igrave;nh phức tạp để ph&aacute;t hiện&nbsp;<a href=\"https://resources.cystack.net/news/tan-cong-mang-lon-nhat-viet-nam-the-gioi/\" rel=\"noopener noreferrer\" target=\"_blank\">c&aacute;c cuộc tấn c&ocirc;ng</a>&nbsp;v&agrave; phản ứng với c&aacute;c vi phạm tr&aacute;i ph&eacute;p. C&aacute;c ứng dụng n&agrave;y ng&agrave;y c&agrave;ng trở n&ecirc;n hữu dụng v&agrave; an to&agrave;n hơn khi AI được triển khai để bảo mật.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c sản phẩm c&ocirc;ng nghệ ứng dụng bảo mật AI c&oacute; thể tự động ph&aacute;t hiện, ph&acirc;n t&iacute;ch v&agrave; bảo vệ chống lại c&aacute;c cuộc tấn c&ocirc;ng n&acirc;ng cao bằng c&aacute;ch chủ động ph&aacute;t hiện v&agrave; ngăn chặn những&nbsp;<a href=\"https://resources.cystack.net/news/tai-sao-website-bi-hacker-tan-cong/\" rel=\"noopener noreferrer\" target=\"_blank\">kẻ tấn c&ocirc;ng</a>.&nbsp;<a href=\"https://resources.cystack.net/news/lo-hong-bao-mat-website-la-gi/\" rel=\"noopener noreferrer\" target=\"_blank\">C&aacute;c lỗ hổng</a>, c&aacute;ch thức tấn c&ocirc;ng mới c&oacute; thể được cập nhật l&ecirc;n hệ thống huấn luyện để m&aacute;y c&oacute; thể học một c&aacute;ch nhanh ch&oacute;ng.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">T&igrave;m kiếm lỗ hổng bảo mật</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"white hat black hat hacker ai trí tuệ nhân tạo cystack\" src=\"https://s.cystack.net/resource/home/content/03134646/tech-the-white_hat_hacker-01-1024x585.png\" style=\"height:343px; width:600px\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Mặt kh&aacute;c, ch&iacute;nh AI cũng c&oacute; thể t&igrave;m ra lỗ hổng (hay điểm yếu bảo mật) của ứng dụng &ndash; thứ gi&uacute;p tin tặc tấn c&ocirc;ng, khai th&aacute;c v&agrave;&nbsp;<a href=\"https://resources.cystack.net/news/6-ly-do-lo-du-lieu-nguoi-dung/\" rel=\"noopener noreferrer\" target=\"_blank\">đ&aacute;nh cắp th&ocirc;ng tin</a>.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Mặt tr&aacute;i</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Những kẻ tấn c&ocirc;ng cũng bắt đầu triển khai Tr&iacute; Tuệ Nh&acirc;n Tạo, lợi dụng n&oacute; v&agrave;o c&aacute;c mục đ&iacute;ch vụ lợi. Điều n&agrave;y đồng nghĩa với việc sẽ dần dần xuất hiện c&aacute;c c&ocirc;ng cụ hack tự động, c&oacute; khả năng nghi&ecirc;n cứu v&agrave; t&igrave;m hiểu về c&aacute;c hệ thống được nhắm đến l&agrave;m mục ti&ecirc;u v&agrave; x&aacute;c định c&aacute;c lỗ hổng ngay lập tức.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">KẾT LUẬN</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">AI v&agrave; ML cũng chỉ l&agrave; c&ocirc;ng nghệ, việc sử dụng c&ocirc;ng v&agrave;o việc g&igrave;, với mục đ&iacute;ch g&igrave; mới l&agrave; điều quan trọng. V&agrave; người quyết định điều đ&oacute; lại ch&iacute;nh l&agrave; con người.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong phần sau, ch&uacute;ng ta sẽ c&ugrave;ng đi s&acirc;u v&agrave;o t&igrave;m hiểu c&aacute;ch ph&ograve;ng chống c&aacute;c cuộc tấn c&ocirc;ng bằng Tr&iacute; tuệ nh&acirc;n tạo như thế n&agrave;o.</span></p>\r\n', 'tech-the-robot.png', 2, 5, 1, 37, NULL, '2020-04-12 03:35:05', '2020-07-15 16:53:05'),
(12, '.NET 5 - tương lai của công nghệ .Net', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">.NET 5 &ndash; tương lai của c&aacute;c c&ocirc;ng nghệ .NET &ndash; c&oacute; g&igrave; mới?</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://neelbhatt40.files.wordpress.com/2019/10/turquoise-orbs-general-twitch-banner.png\" style=\"height:281px; width:527px\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">ASP.NET web forms</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://tuhocict.com/cong-nghe-microsoft-2-winforms-wpf-asp-net-core/\">ASP.NET Web Forms</a>&nbsp;l&agrave; c&ocirc;ng nghệ vốn đ&atilde; kh&ocirc;ng được khuyến kh&iacute;ch sử dụng tr&ecirc;n .NET Framework hiện nay. Trong tương lai Web Forms sẽ kh&ocirc;ng c&oacute; mặt tr&ecirc;n .NET 5.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu bạn hiện d&ugrave;ng ASP.NET MVC (full stack) th&igrave; c&oacute; thể tiếp tục với ASP.NET Core MVC hoặc&nbsp;<a href=\"https://docs.microsoft.com/en-us/aspnet/core/razor-pages/?view=aspnetcore-3.0&amp;tabs=visual-studio\">Razor page</a>. Razor page mới được giới thiệu trong .NET Core 2.0 v&agrave; nh&igrave;n tương tự như web forms d&ugrave;ng để x&acirc;y dựng nhanh c&aacute;c ứng dụng web m&agrave; kh&ocirc;ng cần đến view v&agrave; controller.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tuy nhi&ecirc;n, nếu cần ph&aacute;t triển c&aacute;c ứng dụng web hiện đại cho doanh nghiệp th&igrave; n&ecirc;n xem x&eacute;t c&aacute;c c&ocirc;ng nghệ SPA (single page application) như&nbsp;<a href=\"http://blazor.net/\">Blazor</a>, Angular hoặc React. Hiện Microsoft đang khuyến nghị lập tr&igrave;nh vi&ecirc;n web chuyển sang sử dụng Blazor, c&ocirc;ng nghệ ph&aacute;t triển ứng dụng web SPA sử dụng&nbsp;<a href=\"https://webassembly.org/\">WebAssembly&nbsp;</a>v&agrave; C# (thay v&igrave; JavaScript).</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">WCF (Windows Communication Foundation)</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">WCF l&agrave; một trong những c&ocirc;ng nghệ l&acirc;u đời nhất trong .NET framework. Những năm gần đ&acirc;y WCF dần &ldquo;thất sủng&rdquo; v&agrave; được thay thế một phần bởi ASP.NET Web API. Microsoft đ&atilde; quyết định kh&ocirc;ng đưa WCF v&agrave;o .NET core. Đồng thời c&ocirc;ng nghệ n&agrave;y sẽ kh&ocirc;ng c&oacute; mặt tr&ecirc;n .NET 5.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiện nay WCF được sử dụng chủ yếu như một bộ&nbsp;<a href=\"https://tuhocict.com/rpc-library-don-gian-voi-socket-tcp-1/\">thư viện RPC</a>&nbsp;(remote procedure call). Microsoft khuyến nghị sử dụng&nbsp;<a href=\"https://grpc.io/\">gRPC</a>&nbsp;l&agrave;m giải ph&aacute;p thay thế. gRPC l&agrave; một framework m&atilde; mở hiện đại v&agrave; hiệu suất cao. Hạn chế duy nhất l&agrave; gRPC hiện kh&ocirc;ng thể host tr&ecirc;n IIS.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Update</strong>: template gRPC Service đ&atilde; c&oacute; thể sử dụng được tr&ecirc;n Visual Studio 16.3.9 (ASP.NET Core 3.0).</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">WF (Workflow Foundation)</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">WF thường chỉ được sử dụng trong c&aacute;c ứng dụng enterprise để xử l&yacute; c&aacute;c quy tr&igrave;nh phức tạp v&agrave; biến động. WF sẽ kh&ocirc;ng được đưa v&agrave;o .NET Core.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Mỗi ứng dụng doanh nghiệp sẽ tự phải c&oacute; quy tr&igrave;nh xử l&yacute; nghiệp vụ hoặc c&ocirc;ng cụ BPM được t&iacute;ch hợp. Nếu đ&atilde; sử dụng WF, Microsoft khuyến nghị sử dụng một&nbsp;<a href=\"https://github.com/UiPath/corewf\">nh&aacute;nh kh&ocirc;ng ch&iacute;nh thức của WF runtime</a>&nbsp;để chuyển sang .NET Core.</span></p>\r\n', 'ra-mat-net-5-preview-11.jpg', 7, 3, 1, 13, NULL, '2020-04-12 04:35:11', NULL),
(13, '6 lý do Laravel là framework PHP tốt nhất để phát triển ứng dụng Web', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn đang đắn do sẽ sử dụng framework PHP n&agrave;o cho project web tiếp theo của m&igrave;nh? PHP platform n&agrave;o sẽ được sử dụng? Nếu bạn l&agrave; một web developer th&igrave; c&oacute; thể bạn đ&atilde; nghe qua về &ldquo;Laravel&rdquo;.</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://techtalk.vn/wp-content/uploads/2016/03/laravel-696x281.jpg\" style=\"height:281px; width:696px\" title=\"laravel\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn đang đắn do sẽ sử dụng framework PHP n&agrave;o cho project web tiếp theo của m&igrave;nh? PHP platform n&agrave;o sẽ được sử dụng? &nbsp;Nếu bạn l&agrave; một web&nbsp;developer th&igrave; c&oacute; thể bạn đ&atilde; nghe qua về &ldquo;<strong>Laravel&rdquo;&nbsp;</strong>v&agrave; phần c&ograve;n lại của b&agrave;i viết n&agrave;y m&igrave;nh sẽ m&igrave;nh sẽ ph&acirc;n t&iacute;ch xem v&igrave; sao bạn cần phải d&ugrave;ng n&oacute; cho project web của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://techmaster.vn/fileman/Uploads/users/923/laravel/laravel.jpg\" /></span></p>\r\n\r\n<blockquote>\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn c&oacute; thể tham khảo kh&oacute;a học Laravel của Techmaster&nbsp;tại&nbsp;<a href=\"https://techmaster.vn/khoa-hoc/25536/lap-trinh-laravel\" rel=\"nofollow external noopener noreferrer\" target=\"_new\">đ&acirc;y</a>. Kh&oacute;a học hiện đang trong qu&aacute; tr&igrave;nh ho&agrave;n thiện b&agrave;i giảng. Hi vọng mọi người c&oacute; thể g&oacute;p &yacute; cho m&igrave;nh tại địa chỉ email :&nbsp;<a href=\"mailto:nghia@techmaster.vn?subject=G%C3%B3p%20%C3%BD%20v%E1%BB%81%20kh%C3%B3a%20h%E1%BB%8Dc%20Laravel%20c%E1%BB%A7a%20Techmaster\">nghia@techmaster.vn</a></span></h3>\r\n</blockquote>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://laravel.com/\" rel=\"nofollow external noopener noreferrer\" target=\"_new\">Laravel Framework</a>&nbsp;l&agrave; một framework m&atilde; nguồn mở, to&agrave;n bộ code của n&oacute; được đặt tr&ecirc;n github, bạn c&oacute; thể dễ d&agrave;ng pull về v&agrave; xem c&aacute;ch n&oacute; hoạt động như n&agrave;o? Code của Laravel trong s&aacute;ng v&agrave; dễ đọc n&oacute; gi&uacute;p qu&aacute; tr&igrave;nh viết code của bạn trở n&ecirc;n đơn giản v&agrave; nhanh ch&oacute;ng. N&oacute; v&ocirc; c&ugrave;ng đ&aacute;ng tin cậy v&agrave; số lượng lập tr&igrave;nh vi&ecirc;n l&agrave;m việc với n&oacute; thực sự lớn, n&ecirc;n t&igrave;m t&agrave;i liệu cũng như l&agrave; thắc mắc những vấn đề về code lu&ocirc;n t&igrave;m được c&aacute;ch giải quyết. Bạn c&oacute; thể xem h&igrave;nh dưới đ&acirc;y v&agrave; hiểu tại sau m&igrave;nh n&oacute;i như vậy.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://topdev.vn/\" target=\"_blank\">Những việc l&agrave;m hấp dẫn</a></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://topdev.vn/partners/detail-jobs/senior-front-end-developer-23-14648/?token=ZKSuE%2BDgRSAzEOBNUcWfv9XiWteBtt6KdNdNI11aNYUzfyjT2VC0ZXq97IPeU9i0ITF9vI9NA30ibg73TVnifg%3D%3D\"><img src=\"https://assets.topdev.vn/files/logos/fd279c6f268c44938e23f32ae75cb4f7.png\" /></a></span>\r\n\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://topdev.vn/partners/detail-jobs/senior-front-end-developer-23-14648/?token=ZKSuE%2BDgRSAzEOBNUcWfv9XiWteBtt6KdNdNI11aNYUzfyjT2VC0ZXq97IPeU9i0ITF9vI9NA30ibg73TVnifg%3D%3D\" target=\"_blank\" title=\"Senior Front-End Developer\">Senior Front-End Developer</a></span></p>\r\n\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://topdev.vn/partners/detail-jobs/senior-front-end-developer-23-14648/?token=ZKSuE%2BDgRSAzEOBNUcWfv9XiWteBtt6KdNdNI11aNYUzfyjT2VC0ZXq97IPeU9i0ITF9vI9NA30ibg73TVnifg%3D%3D\" target=\"_blank\" title=\"Senior Front-End Developer\">C&ocirc;ng ty Cổ Phần BRAVOHRTh&agrave;nh phố Hồ Ch&iacute; Minh1,000-1,500 USD</a></span></p>\r\n	<span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://topdev.vn/partners/detail-jobs/senior-front-end-developer-23-14648/?token=ZKSuE%2BDgRSAzEOBNUcWfv9XiWteBtt6KdNdNI11aNYUzfyjT2VC0ZXq97IPeU9i0ITF9vI9NA30ibg73TVnifg%3D%3D\" target=\"_blank\" title=\"Senior Front-End Developer\">CSS3ReactJSHTMLFront-End</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://topdev.vn/partners/detail-jobs/09-senior-php-developer-14761/?token=ZKSuE%2BDgRSAzEOBNUcWfv9XiWteBtt6KdNdNI11aNYUzfyjT2VC0ZXq97IPeU9i0ITF9vI9NA30ibg73TVnifg%3D%3D\"><img src=\"https://assets.topdev.vn/files/logos/6ecf5c479a113babdfa0719b3920c24e.jpg\" /></a></span>\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://topdev.vn/partners/detail-jobs/09-senior-php-developer-14761/?token=ZKSuE%2BDgRSAzEOBNUcWfv9XiWteBtt6KdNdNI11aNYUzfyjT2VC0ZXq97IPeU9i0ITF9vI9NA30ibg73TVnifg%3D%3D\" target=\"_blank\" title=\"09 Senior PHP Developer\">09 Senior PHP Developer</a></span></p>\r\n\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://topdev.vn/partners/detail-jobs/09-senior-php-developer-14761/?token=ZKSuE%2BDgRSAzEOBNUcWfv9XiWteBtt6KdNdNI11aNYUzfyjT2VC0ZXq97IPeU9i0ITF9vI9NA30ibg73TVnifg%3D%3D\" target=\"_blank\" title=\"09 Senior PHP Developer\">RiverCrane VietnamUp to 1,500 USD</a></span></p>\r\n	<span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://topdev.vn/partners/detail-jobs/09-senior-php-developer-14761/?token=ZKSuE%2BDgRSAzEOBNUcWfv9XiWteBtt6KdNdNI11aNYUzfyjT2VC0ZXq97IPeU9i0ITF9vI9NA30ibg73TVnifg%3D%3D\" target=\"_blank\" title=\"09 Senior PHP Developer\">WebLaravelPHP</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://topdev.vn/partners/detail-jobs/php-developer-123-14706/?token=ZKSuE%2BDgRSAzEOBNUcWfv9XiWteBtt6KdNdNI11aNYUzfyjT2VC0ZXq97IPeU9i0ITF9vI9NA30ibg73TVnifg%3D%3D\"><img src=\"https://assets.topdev.vn/images/2020/03/31/4rdlHbar35J16aDuPNFBDEBShfGJ9fgE.png\" /></a></span>\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://topdev.vn/partners/detail-jobs/php-developer-123-14706/?token=ZKSuE%2BDgRSAzEOBNUcWfv9XiWteBtt6KdNdNI11aNYUzfyjT2VC0ZXq97IPeU9i0ITF9vI9NA30ibg73TVnifg%3D%3D\" target=\"_blank\" title=\"PHP DEVELOPER\">PHP DEVELOPER</a></span></p>\r\n\r\n	<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://topdev.vn/partners/detail-jobs/php-developer-123-14706/?token=ZKSuE%2BDgRSAzEOBNUcWfv9XiWteBtt6KdNdNI11aNYUzfyjT2VC0ZXq97IPeU9i0ITF9vI9NA30ibg73TVnifg%3D%3D\" target=\"_blank\" title=\"PHP DEVELOPER\">C&Ocirc;NG TY TNHH G&Oacute;C CỦA TIN - TINCORNERTh&agrave;nh phố H&agrave; Nội400 - 1,000 USD</a></span></p>\r\n	<span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://topdev.vn/partners/detail-jobs/php-developer-123-14706/?token=ZKSuE%2BDgRSAzEOBNUcWfv9XiWteBtt6KdNdNI11aNYUzfyjT2VC0ZXq97IPeU9i0ITF9vI9NA30ibg73TVnifg%3D%3D\" target=\"_blank\" title=\"PHP DEVELOPER\">YiiLaravelYii2PHP</a></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"15 PHP framework phổ biến nhất năm 2015\" src=\"https://techmaster.vn/fileman/Uploads/users/923/laravel/chart.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Vẫn chưa đủ thuyết phục bạn đ&uacute;ng kh&ocirc;ng? Tiếp tục đọc n&agrave;o? Hi vọng những g&igrave; m&igrave;nh cung cấp dưới đ&acirc;y c&oacute; thể thuyết phục bạn.</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Quick Emergence</strong>: Sự ra đời của Laravel như một luồng gi&oacute; mới thổi v&agrave;o cộng đồng framework PHP. N&oacute; c&oacute; thể giải quyết tốt những web project lớn. Rất nhiều c&ocirc;ng ty đ&atilde; sử dụng n&oacute; để ph&aacute;t triển c&aacute;c sản phẩm cho c&ocirc;ng ty của họ. Như h&igrave;nh b&ecirc;n tr&ecirc;n bạn c&oacute; thể nh&igrave;n thấy n&oacute; chiếm phần lớn nhất trong &ldquo;miếng b&aacute;nh&rdquo; framework PHP.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Open Source</strong>: N&oacute; mở v&agrave; ho&agrave;n to&agrave;n free. Bạn chẳng phải quan t&acirc;m đến việc trả ph&iacute; khi ứng dụng web của bạn to l&ecirc;n. Điều bạn cần l&agrave;m l&agrave; hiểu n&oacute; v&agrave; code những t&iacute;nh năng tuyệt với m&agrave; th&ocirc;i.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Templete engine</strong>: N&oacute; sử dụng&nbsp;<strong>Blade template</strong>&nbsp;v&agrave; c&aacute;c c&ocirc;ng cụ như&nbsp;<strong>Elixir</strong>&nbsp;gi&uacute;p bạn dễ d&agrave;ng quản l&yacute; c&aacute;c assets( bi&ecirc;n dịch, gh&eacute;p nối, n&eacute;n &hellip;), cũng như chia nhỏ code HTML ra th&agrave;nh nhiều phần. Gi&uacute;p cho việc thiết kế cũng như quản l&yacute; c&aacute;c assets trở n&ecirc;n v&ocirc; c&ugrave;ng đơn giản.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Modular</strong>: Laravel được x&acirc;y dựng dựa tr&ecirc;n hơn 20 thư viện kh&aacute;c nhau. Hiểu được c&aacute;ch thiết kế framework khiến c&aacute;c dev ho&agrave;n to&agrave;n c&oacute; thể đ&oacute;ng g&oacute;p cho framework cũng như mở rộng ch&uacute;ng một c&aacute;ch dễ d&agrave;ng. Dễ hiểu khi Laravel lu&ocirc;n c&oacute; cộng đồng support đ&ocirc;ng đảo v&agrave; &ldquo;tr&acirc;u&rdquo; như vậy.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>MVC Architecture Support:&nbsp;</strong>&nbsp;Laravel sử dụng MVC pattern để x&acirc;y dựng framework. N&oacute; đảm bảo rằng c&aacute;c code được tr&igrave;nh b&agrave;y logic v&agrave; hợp l&yacute;. Dễ d&agrave;ng để c&oacute; thể hiểu được n&oacute;. V&igrave; vậy n&oacute; l&agrave;m cho web project của bạn được trở n&ecirc;n dễ quản l&yacute; cũng như ph&aacute;t triển rất&nbsp;rất nhiều.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Libraries and configuration</strong>: Laravel tạo điều kiện cho c&aacute;c developer c&oacute; thể thay đổi t&ugrave;y chọn v&agrave; cấu h&igrave;nh để ph&ugrave; hợp với nhiều m&ocirc;i trường. C&aacute;c developer cũng c&oacute; thể viết c&aacute;c g&oacute;i ( package ) c&oacute; thể l&agrave; một t&iacute;nh năng hay một đoạn code g&igrave; đ&oacute;. N&oacute; dễ d&agrave;ng được khai b&aacute;o trong Laravel th&ocirc;ng qua c&aacute;c provider. Ch&iacute;nh điều n&agrave;y &nbsp;+ với số lượng c&aacute;c developer đ&ocirc;ng đảo th&igrave; số lượng package m&agrave; Laravel c&oacute; ng&agrave;y c&agrave;ng tăng l&ecirc;n. Bạn c&oacute; thể dễ d&agrave;ng t&igrave;m được một v&agrave;i đoạn code cũng như một v&agrave;i t&iacute;nh năng m&agrave; bạn mong muốn trong project của bạn chỉ trong v&agrave;i ph&uacute;t. N&oacute; tiết kiệm hơn so với việc phải ngồi viết code cho t&iacute;nh năng đ&oacute;.</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong khuổn khổ c&aacute;c b&agrave;i post n&agrave;y m&igrave;nh kh&ocirc;ng thể giới thiệu hết được to&agrave;n bộ những t&iacute;nh năng hay ho v&agrave; tuyệt vời của Framework Laravel n&agrave;y được. N&oacute; thật sự l&agrave; một &ldquo;Awesome Framework&rdquo; m&agrave; chỉ khi bước ch&acirc;n v&agrave;o bạn mới cảm nhận được. Ở những b&agrave;i post sau m&igrave;nh sẽ cố gắng đưa những t&iacute;nh năng tuyệt với của Laravel đến gần hơn với c&aacute;c bạn. Biết đ&acirc;u một ng&agrave;y n&agrave;o đấy ch&iacute;nh t&ocirc;i cũng đang d&ugrave;ng 1 package của ch&iacute;nh bạn viết được v&agrave; chia sẻ l&ecirc;n cộng đồng Laravel&nbsp;<a href=\"http://packalyst.com/\" rel=\"nofollow external noopener noreferrer\" target=\"_new\">packalyst</a>.</span></p>\r\n', 'laravel-696x281.jpg', 6, 2, 1, 15, NULL, '2020-04-12 05:35:17', NULL);
INSERT INTO `posts` (`id`, `title`, `description`, `contents`, `thumbnail`, `user_id`, `category_id`, `status`, `view`, `rate`, `created_at`, `updated_at`) VALUES
(14, 'Trí tuệ nhân tạo thống trị mọi lĩnh vực trong thập niên qua như thế nào?', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&ograve;n nhớ hồi năm 2010, tr&iacute; tuệ nh&acirc;n tạo (AI) hầu như chỉ mới xuất hiện trong c&aacute;c bộ phim khoa học viễn tưởng hơn l&agrave; trong cuộc sống thực hằng ng&agrave;y.</span></p>', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&ograve;n nhớ hồi năm 2010, tr&iacute; tuệ nh&acirc;n tạo (AI) hầu như chỉ mới xuất hiện trong c&aacute;c bộ phim khoa học viễn tưởng hơn l&agrave; trong&nbsp;<a href=\"https://thanhnien.vn/doi-song/\" rel=\"\">cuộc sống</a>&nbsp;thực hằng ng&agrave;y. Tuy nhi&ecirc;n, c&ocirc;ng nghệ n&agrave;y giờ đ&acirc;y đ&atilde; gần gũi hơn bao giờ hết.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"javascript:void();\"><img alt=\"AI đang dần xâm chiếm mọi lĩnh vực trong cuộc sống của con người  /// Ảnh: AFP\" src=\"https://image.thanhnien.vn/660/uploaded/nthanhluan/2019_12_27/ai-cover_htvp.jpg\" /></a></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">AI đang dần x&acirc;m chiếm mọi lĩnh vực trong cuộc sống của con người</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">ẢNH: AFP</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&atilde; c&oacute; rất nhiều thay đổi kể từ l&uacute;c đ&oacute; tới giờ, 10 năm đ&atilde; tr&ocirc;i qua v&agrave; AI đang được sử dụng cho gần như tất cả mọi thứ li&ecirc;n quan đến c&ocirc;ng nghệ, từ chụp ảnh tr&ecirc;n điện thoại th&ocirc;ng minh cho đến ph&acirc;n t&iacute;ch t&iacute;nh c&aacute;ch của nh&acirc;n vi&ecirc;n trong c&aacute;c cuộc phỏng vấn xin việc, thậm ch&iacute; c&aacute;c th&oacute;i quen mua h&agrave;ng ở si&ecirc;u thị cũng được ph&acirc;n t&iacute;ch kỹ lưỡng nhờ v&agrave;o AI.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&agrave;ng ng&agrave;y AI c&agrave;ng phổ biến nhưng cũng g&acirc;y ra nhiều tranh c&atilde;i, nhất l&agrave; kể từ khi n&oacute; được sử dụng để gi&aacute;m s&aacute;t, nhận diện khu&ocirc;n mặt hoặc truyền b&aacute; c&aacute;c th&ocirc;ng tin sai lệch (fake news) v&agrave; cả những video giả mạo bằng c&ocirc;ng nghệ deep fake (dựa tr&ecirc;n thuật to&aacute;n m&aacute;y học), rất nhiều mặt tr&aacute;i của c&ocirc;ng nghệ đang bị khai th&aacute;c theo chiều hướng ti&ecirc;u cực&hellip;</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">AI đ&atilde; đổ bộ v&agrave; x&acirc;m chiếm nhiều lĩnh vực kh&aacute;c nhau trong cuộc sống của ch&uacute;ng ta trong thập kỷ qua như thế n&agrave;o? C&acirc;u trả lời nằm ở những tiến bộ c&ocirc;ng nghệ trong lĩnh vực n&agrave;y, kết hợp với chi ph&iacute; truy cập Internet ng&agrave;y c&agrave;ng phải chăng hơn v&agrave; c&aacute;c m&aacute;y t&iacute;nh ng&agrave;y c&agrave;ng mạnh mẽ hơn.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Pedro Sebastos, Gi&aacute;o sư khoa học m&aacute;y t&iacute;nh tại Đại học Washington (Mỹ) cho rằng, &quot;mười năm trước, học s&acirc;u kh&ocirc;ng nằm trong tiềm thức của bất kỳ ai, giờ đ&acirc;y n&oacute; c&oacute; trong mọi thứ&quot;. Tuy nhi&ecirc;n, thực tế hiện nay AI vẫn c&ograve;n kh&aacute; đơn giản. Chẳng hạn, một thuật to&aacute;n m&aacute;y học thường chỉ l&agrave;m một việc nhất định v&agrave; thường y&ecirc;u cầu nhập liệu h&agrave;ng n&uacute;i dữ liệu để học c&aacute;ch l&agrave;m tốt n&oacute;. Do vậy, hiện rất nhiều nghi&ecirc;n cứu về AI đang tập trung đ&agrave;o tạo c&aacute;c hệ thống m&aacute;y học tốt hơn trong việc kh&aacute;i qu&aacute;t h&oacute;a v&agrave; cần tới &iacute;t dữ liệu đầu v&agrave;o hơn để học hỏi.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Sebastos nhận định, &quot;ch&uacute;ng ta đ&atilde; đi được một ng&agrave;n dặm, nhưng vẫn c&ograve;n một triệu dặm phải vượt qua&quot;. Chỉ mới đ&oacute; đ&atilde; mười năm tr&ocirc;i qua, v&agrave; ng&agrave;n dặm đ&oacute; đ&atilde; nằm lại ở ph&iacute;a sau gương chiếu hậu của h&agrave;nh tr&igrave;nh đi về tương lai. H&atilde;y c&ugrave;ng CNN nh&igrave;n lại cuộc h&agrave;nh tr&igrave;nh của AI trong 10 năm v&agrave; điểm lại 6 trong số nhiều lĩnh vực m&agrave; tr&iacute; tuệ th&ocirc;ng minh đ&atilde; tạo ra ảnh hưởng s&acirc;u rộng nhất trong cuộc sống của ch&uacute;ng ta:</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Điện thoại th&ocirc;ng minh</span></h2>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Trí tuệ nhân tạo thống trị mọi lĩnh vực trong thập niên qua như thế nào? - ảnh 1\" src=\"https://image.thanhnien.vn/660/uploaded/nthanhluan/2019_12_27/smartphone_ifwt.jpg\" /></span></p>\r\n\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">iPhone 11 Pro được t&iacute;ch hợp nhiều thuật to&aacute;n AI để tăng cường chất lượng camera</span></p>\r\n\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">ẢNH: CNN</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ng&agrave;y nay, AI c&oacute; mặt tr&ecirc;n tất cả c&aacute;c điện thoại th&ocirc;ng minh, từ phần mềm nhận dạng khu&ocirc;n mặt để mở kh&oacute;a điện thoại cho đến c&aacute;c ứng dụng phổ biến như Google Maps. C&agrave;ng ng&agrave;y, c&aacute;c c&ocirc;ng ty lớn như Apple v&agrave; Google c&agrave;ng cố gắng chạy đua nh&uacute;ng AI trực tiếp v&agrave;o c&aacute;c thiết bị cầm tay, với c&aacute;c chip đặc biệt hỗ trợ khả năng điều khiển bằng AI.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Do đ&oacute;, c&aacute;c hoạt động thường ng&agrave;y như nhận diện giọng n&oacute;i hiện được thực hiện ngay tr&ecirc;n điện thoại thay v&igrave; từ m&aacute;y t&iacute;nh từ xa (m&aacute;y chủ) như trước, gi&uacute;p n&oacute; c&oacute; thể cung cấp nhanh c&aacute;c bản dịch từ ng&ocirc;n ngữ n&agrave;y sang ng&ocirc;n ngữ kh&aacute;c m&agrave; vẫn đảm bảo giữ được sự ri&ecirc;ng tư của bản dịch.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">V&iacute; dụ đơn giản, v&agrave;o th&aacute;ng 10 vừa qua, Google giới thiệu ứng dụng ghi &acirc;m c&oacute; t&ecirc;n l&agrave; Recorder t&iacute;ch hợp tr&ecirc;n điện thoại Pixel 4 (v&agrave; giờ đ&atilde; c&oacute; mặt tr&ecirc;n Pixel 3, 3a&hellip;) cho ph&eacute;p ghi &acirc;m v&agrave; chuyển th&agrave;nh văn bản (text) theo thời gian thực. N&oacute; tự nhận diện những nội dung bạn n&oacute;i v&agrave; x&aacute;c định c&aacute;c nguồn &acirc;m kh&aacute;c nhau như &acirc;m nhạc hay tiếng vỗ tay, sau đ&oacute; cho ph&eacute;p t&igrave;m kiếm nội dung bản ghi bằng từ kh&oacute;a.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Sở dĩ ứng dụng chỉ mới chạy tr&ecirc;n Google Pixel v&igrave; Google cho biết điều n&agrave;y rất kh&oacute; thực hiện, do cần tới một số hoạt động AI &ldquo;ngốn pin&rdquo; v&agrave; chiếm nhiều t&agrave;i nguy&ecirc;n xử l&yacute;. Nếu người d&ugrave;ng th&iacute;ch th&uacute;, ng&agrave;y sẽ c&agrave;ng c&oacute; nhiều AI được nh&uacute;ng v&agrave;o điện thoại.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Mạng x&atilde; hội</span></h2>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Trí tuệ nhân tạo thống trị mọi lĩnh vực trong thập niên qua như thế nào? - ảnh 2\" src=\"https://image.thanhnien.vn/660/uploaded/nthanhluan/2019_12_27/mxh_belu.jpeg\" /></span></p>\r\n\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Facebook ứng dụng AI v&agrave;o thuật to&aacute;n của họ để quản l&yacute; nội dung tự động</span></p>\r\n\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">ẢNH: AFP</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Khi&nbsp;<a href=\"https://thanhnien.vn/cong-nghe/facebook-xoa-cac-tai-khoan-su-dung-al-de-tao-anh-dai-dien-gia-1162446.html\" rel=\"\" target=\"_blank\">Facebook</a>&nbsp;ra mắt lần đầu ti&ecirc;n v&agrave;o năm 2004, n&oacute; chỉ mới tập trung v&agrave;o việc kết nối mọi người. Giờ đ&acirc;y, họ giao cho tr&iacute; tuệ nh&acirc;n tạo xử l&yacute; c&ocirc;ng việc đ&oacute;. AI đ&atilde; trở th&agrave;nh trung t&acirc;m của c&aacute;c sản phẩm của c&ocirc;ng ty m&agrave; một năm trước nh&agrave; khoa học AI của Facebook l&agrave; Yann LeCun cho rằng, &ldquo;nếu kh&ocirc;ng t&igrave;m hiểu kỹ th&igrave; mạng x&atilde; hội sẽ chỉ l&agrave; &ldquo;r&aacute;c bụi&rdquo;&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Sau nhiều năm đầu tư, giờ đ&acirc;y nhờ c&aacute;c thuật to&aacute;n học s&acirc;u đ&atilde; gi&uacute;p Facebook (v&agrave; c&aacute;c mạng x&atilde; hội kh&aacute;c) củng cố lại mọi thứ, từ c&aacute;c b&agrave;i đăng v&agrave; c&aacute;c quảng c&aacute;o bạn thấy tr&ecirc;n trang web cho đến c&aacute;c bức ảnh c&oacute; thể tự động gắn thẻ bạn b&egrave; của bạn.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ngo&agrave;i ra, n&oacute; c&ograve;n được lập tr&igrave;nh để gi&uacute;p nhận diện v&agrave; loại bỏ c&aacute;c nội dung hoặc ng&ocirc;n từ k&iacute;ch động sự th&ugrave; địch khỏi mạng x&atilde; hội. D&ugrave; vẫn c&ograve;n một chặng đường d&agrave;i ph&iacute;a trước, nhất l&agrave; trong việc ph&aacute;t hiện ra c&aacute;c h&agrave;nh vi bạo lực hoặc bắt nạt trực tuyến, nhưng những th&agrave;nh tựu đầu ti&ecirc;n đ&atilde; chứng minh khả năng ứng dụng của AI v&agrave;o thực tế l&agrave; rất r&otilde; r&agrave;ng.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Facebook kh&ocirc;ng phải l&agrave; c&ocirc;ng ty duy nhất đưa AI v&agrave;o thuật to&aacute;n của họ, ngo&agrave;i ra c&ograve;n Instagram, Twitter v&agrave; nhiều mạng x&atilde; hội kh&aacute;c cũng dựa v&agrave;o AI.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Trợ l&yacute; ảo</span></h2>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Trí tuệ nhân tạo thống trị mọi lĩnh vực trong thập niên qua như thế nào? - ảnh 3\" src=\"https://image.thanhnien.vn/660/uploaded/nthanhluan/2019_12_27/trolyao_mnvv.jpg\" /></span></p>\r\n\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Loa th&ocirc;ng minh Echo của&nbsp;<a href=\"https://thanhnien.vn/tin-tuc/amazon.html\" rel=\"\">Amazon</a>&nbsp;t&iacute;ch hợp sẵn trợ l&yacute; ảo Alexa để giao tiếp tại gia</span></p>\r\n\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">ẢNH: AMAZON</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bất cứ khi n&agrave;o bạn &ldquo;tr&ograve; chuyện&rdquo; với c&aacute;c trợ l&yacute; ảo như Alexa của Amazon , Siri của Apple, Cortana của Microsoft hoặc Assistant của Google, bạn sẽ c&oacute; những tương t&aacute;c gần hơn với AI. N&oacute; sẽ cố gắng hiểu những g&igrave; bạn đang trao đổi để trả lời bạn trong mức n&oacute; c&oacute; thể hiểu.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Sự trỗi dậy của c&aacute;c trợ l&yacute; ảo n&agrave;y bắt đầu từ năm 2011, khi Apple ph&aacute;t h&agrave;nh Siri tr&ecirc;n iPhone. Google đ&atilde; theo g&oacute;t với Google Now v&agrave;o năm 2012 v&agrave; giờ đ&acirc;y l&agrave; Google Assistant (kể từ 2016).</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhiều người cảm thấy sự trợ gi&uacute;p của c&aacute;c trợ l&yacute; ảo từ Apple v&agrave; Google đang qu&aacute; phụ thuộc v&agrave;o điện thoại th&ocirc;ng minh, th&igrave; Amazon nhanh ch&oacute;ng gi&uacute;p họ bớt r&agrave;ng buộc hơn th&ocirc;ng qua trợ l&yacute; ảo Alexa được ra mắt v&agrave;o năm 2014 th&ocirc;ng qua loa th&ocirc;ng minh Amazon Echo, gi&uacute;p thị trường trợ l&yacute; ảo thực sự b&ugrave;ng nồ v&agrave; gi&aacute;n tiếp đưa AI tới gần người d&ugrave;ng hơn.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Gi&aacute;m s&aacute;t</span></h2>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Trí tuệ nhân tạo thống trị mọi lĩnh vực trong thập niên qua như thế nào? - ảnh 4\" src=\"https://image.thanhnien.vn/660/uploaded/nthanhluan/2019_12_27/12_swbn.jpg\" style=\"float:left\" /></span></p>\r\n\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Một hệ thống của NVIDIA tr&igrave;nh diễn khả năng nhận dạng dựa tr&ecirc;n AI v&agrave; thực tế tăng cường</span></p>\r\n\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">ẢNH CHỤP M&Agrave;N H&Igrave;NH</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Khi AI đ&atilde; được cải thiện, người ta bắt đầu sử dụng n&oacute; như l&agrave; một c&ocirc;ng cụ gi&aacute;m s&aacute;t. Một trong những điều g&acirc;y tranh c&atilde;i nhất trong số n&agrave;y l&agrave; c&ocirc;ng nghệ nhận dạng khu&ocirc;n mặt, x&aacute;c định danh t&iacute;nh th&ocirc;ng qua video trực tiếp, video ghi lại hoặc ảnh tĩnh.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Dĩ nhi&ecirc;n l&agrave; n&oacute; nhận diện bằng c&aacute;ch so s&aacute;nh c&aacute;c đặc điểm của khu&ocirc;n mặt với cơ sở dữ liệu của n&oacute; (c&aacute;c ảnh chụp về khu&ocirc;n mặt). N&oacute; được sử dụng trong nhiều bối cảnh kh&aacute;c nhau: tại c&aacute;c buổi h&ograve;a nhạc, c&aacute;c camera an ninh của cảnh s&aacute;t v&agrave; tại c&aacute;c s&acirc;n bay.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tuy nhi&ecirc;n, c&aacute;c hệ thống nhận dạng khu&ocirc;n mặt đ&atilde; được nghi&ecirc;n cứu kỹ lưỡng, do lo ngại về quyền ri&ecirc;ng tư v&agrave; độ ch&iacute;nh x&aacute;c. Chẳng hạn, v&agrave;o th&aacute;ng 12 vừa qua một nghi&ecirc;n cứu của ch&iacute;nh phủ Mỹ cho thấy sự thi&ecirc;n vị về sắc tộc trong gần 200 thuật to&aacute;n nhận diện khu&ocirc;n mặt, những nh&oacute;m người thiểu số như da đỏ hay da đen c&oacute; khả năng bị nhận diện nhầm cao hơn so với người da trắng.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Chăm s&oacute;c sức khỏe</span></h2>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Trí tuệ nhân tạo thống trị mọi lĩnh vực trong thập niên qua như thế nào? - ảnh 5\" src=\"https://image.thanhnien.vn/660/uploaded/nthanhluan/2019_12_27/suckhoe_gofv.jpg\" style=\"height:434px; width:600px\" /></span></p>\r\n\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">AI được sử dụng để scan nhằm ph&aacute;t hiện ung thư phổi &aacute;c t&iacute;nh</span></p>\r\n\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">ẢNH: GOOGLE</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">AI ng&agrave;y c&agrave;ng được sử dụng để chẩn đo&aacute;n v&agrave; quản l&yacute; tất cả c&aacute;c&nbsp;<a href=\"https://thanhnien.vn/suc-khoe/\" rel=\"\">ph&acirc;n loại sức khỏe</a>, từ ph&aacute;t hiện ung thư phổi cho đến theo d&otilde;i c&aacute;c vấn đề về sức khỏe t&acirc;m thần v&agrave; c&aacute;c vấn đề về đường ti&ecirc;u h&oacute;a. Mặc d&ugrave; phần lớn c&ocirc;ng việc n&agrave;y vẫn đang trong giai đoạn nghi&ecirc;n cứu hoặc thử nghiệm, nhưng c&oacute; những c&ocirc;ng ty khởi nghiệp - như Mindstrong Health, đ&atilde; sử dụng một ứng dụng để đo lường t&acirc;m trạng ở những bệnh nh&acirc;n đang đối ph&oacute; với c&aacute;c vấn đề về sức khỏe t&acirc;m thần - đang thử nghiệm hệ thống AI với con người.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hai c&ocirc;ng ty khởi nghiệp ở gồm Auggi, một c&ocirc;ng ty khởi nghiệp về&nbsp;<a href=\"https://thanhnien.vn/suc-khoe/\" rel=\"\">sức khỏe</a>&nbsp;đường ruột x&acirc;y dựng một ứng dụng để gi&uacute;p theo d&otilde;i c&aacute;c vấn đề về đường ti&ecirc;u h&oacute;a v&agrave; Seed Health &ndash; c&ocirc;ng ty b&aacute;n men vi sinh v&agrave; hoạt động ở lĩnh vực &aacute;p dụng c&aacute;c loại vi khuẩn c&oacute; lợi để tăng cường sức khỏe cho con người.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">V&agrave;o th&aacute;ng 11 vừa qua, họ bắt đầu thu thập những bức ảnh ph&acirc;n t&iacute;ch từ dữ liệu c&ocirc;ng cộng m&agrave; họ dự định sử dụng để tạo ra một bộ dữ liệu h&igrave;nh ảnh về ph&acirc;n của con người. Auggi muốn sử dụng những h&igrave;nh ảnh n&agrave;y để tạo ra một ứng dụng c&oacute; thể sử dụng thị gi&aacute;c m&aacute;y t&iacute;nh để tự động ph&acirc;n loại c&aacute;c loại chất thải kh&aacute;c nhau nhằm nhận diện những người c&oacute; vấn đề li&ecirc;n quan đến đường ruột m&atilde;n t&iacute;nh - như hội chứng ruột k&iacute;ch th&iacute;ch hoặc IBS &ndash; phần việc m&agrave; trước đ&acirc;y c&aacute;c b&aacute;c sĩ thường phải theo d&otilde;i thủ c&ocirc;ng bằng tay bằng b&uacute;t v&agrave; giấy.</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Nghệ thuật</span></h2>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Trí tuệ nhân tạo thống trị mọi lĩnh vực trong thập niên qua như thế nào? - ảnh 6\" src=\"https://image.thanhnien.vn/660/uploaded/nthanhluan/2019_12_27/nghethuat_liea.jpeg\" style=\"height:315px; width:600px\" /></span></p>\r\n\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Pierre Fautrel - đồng s&aacute;ng lập một c&ocirc;ng ty nghệ thuật dựa tr&ecirc;n AI, đang đứng cạnh t&aacute;c phẩm &quot;Portrait of Edmond de Belamy&quot;, t&aacute;c phẩm đầu ti&ecirc;n vẽ bằng AI được b&aacute;n đấu gi&aacute;</span></p>\r\n\r\n			<p><span style=\"font-family:Arial,Helvetica,sans-serif\">ẢNH CHỤP M&Agrave;N H&Igrave;NH</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">AI c&oacute; thể tạo ra nghệ thuật? C&agrave;ng ng&agrave;y c&acirc;u trả lời c&agrave;ng củng cố l&agrave; c&oacute;. Trong 10 năm qua, kh&ocirc;ng &iacute;t lần AI đ&atilde; được sử dụng để tạo ra c&aacute;c t&aacute;c phẩm &acirc;m nhạc, tranh vẽ v&agrave; nhiều thứ kh&aacute;c c&oacute; vẻ &ldquo;rất giống&rdquo; với những thứ m&agrave; con người c&oacute; thể nghĩ ra, d&ugrave; ban gi&aacute;m khảo vẫn chưa biết liệu một cỗ m&aacute;y c&oacute; thực sự sở hữu sự s&aacute;ng tạo hay kh&ocirc;ng. V&agrave; đ&ocirc;i khi, nghệ thuật đ&oacute; thậm ch&iacute; c&oacute; thể sinh lợi.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Minh chứng r&otilde; r&agrave;ng nhất l&agrave; một t&aacute;c phẩm &ldquo;ảo diệu&rdquo; do AI tạo ra v&agrave;o năm 2018 c&oacute; t&ecirc;n l&agrave; &quot;Edmond de Belamy&quot; đ&atilde; trở th&agrave;nh t&aacute;c phẩm đầu ti&ecirc;n do một chiếc m&aacute;y t&iacute;nh (AI) tạo ra được b&aacute;n đấu gi&aacute; c&ocirc;ng khai. Dĩ nhi&ecirc;n, bản vẽ được tạo ra bằng kỹ thuật ti&ecirc;n tiến gọi l&agrave; Gans, bao gồm hai mạng thần kinh cạnh tranh với nhau để tạo ra một thứ g&igrave; đ&oacute; mới mẻ dựa tr&ecirc;n kho dữ liệu đầu v&agrave;o của n&oacute;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong trường hợp n&agrave;y, bộ dữ liệu bao gồm một loạt c&aacute;c bức tranh nghệ thuật hiện c&oacute;, t&aacute;c phẩm của AI tạo ra l&agrave; một bức tranh ho&agrave;n to&agrave;n mới dựa tr&ecirc;n khả năng học hỏi của n&oacute;.</span></p>', 'ai-cover_htvp.jpg', 2, 5, 1, 158, NULL, '2020-04-15 06:36:22', '2020-07-15 07:54:42'),
(15, 'Phát hành PHP 7.3, Google đã phá hoại trình duyệt của Microsoft', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Phi&ecirc;n bản PHP 7.3 l&agrave; phi&ecirc;n bản mới nhất của ng&ocirc;n ngữ lập tr&igrave;nh phổ biến PHP được tung ra v&agrave;o đầu th&aacute;ng 12.2018. Phi&ecirc;n bản n&agrave;y được cập nhật với c&aacute;c t&iacute;nh năng mới v&agrave; cải thiện về tốc độ xử l&yacute;.</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nội dung&nbsp;[<a href=\"https://codegym.vn/blog/2018/12/19/tin-tuc-phat-hanh-php-7-3-google-da-pha-hoai-trinh-duyet-cua-microsoft/#\">ẩn</a>]</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://codegym.vn/blog/2018/12/19/tin-tuc-phat-hanh-php-7-3-google-da-pha-hoai-trinh-duyet-cua-microsoft/#PHP_73_cai_thien_co_che_nhung_ma_C_va_toc_do_xu_ly\">PHP 7.3 cải thiện cơ chế nh&uacute;ng m&atilde; C v&agrave; tốc độ xử l&yacute;</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://codegym.vn/blog/2018/12/19/tin-tuc-phat-hanh-php-7-3-google-da-pha-hoai-trinh-duyet-cua-microsoft/#Cac_ngon_ngu_lap_trinh_co_toc_do_tang_truong_nhanh_nhat_nam_2018\">C&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh c&oacute; tốc độ tăng trưởng nhanh nhất năm 2018</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://codegym.vn/blog/2018/12/19/tin-tuc-phat-hanh-php-7-3-google-da-pha-hoai-trinh-duyet-cua-microsoft/#Thu_nghiem_AI_moi_nhat_cua_Google_cho_phep_ban_dieu_khien_ca_dan_giao_huong_ngay_trong_trinh_duyet\">Thử nghiệm AI mới nhất của Google cho ph&eacute;p bạn điều khiển cả d&agrave;n giao hưởng ngay trong tr&igrave;nh duyệt</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://codegym.vn/blog/2018/12/19/tin-tuc-phat-hanh-php-7-3-google-da-pha-hoai-trinh-duyet-cua-microsoft/#Firefox_8220quan_ngai_sau_sac8221_ve_viec_Microsoft_khai_tu_EdgeHTML_chuyen_sang_dung_Chromium\">Firefox &ldquo;quan ngại s&acirc;u sắc&rdquo; về việc Microsoft khai tử EdgeHTML chuyển sang d&ugrave;ng Chromium</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://codegym.vn/blog/2018/12/19/tin-tuc-phat-hanh-php-7-3-google-da-pha-hoai-trinh-duyet-cua-microsoft/#Cuu_ky_su_thuc_tap_trong_du_an_Edge_Google_da_pha_hoai_trinh_duyet_cua_Microsoft\">Cựu kỹ sư thực tập trong dự &aacute;n Edge: Google đ&atilde; ph&aacute; hoại tr&igrave;nh duyệt của Microsoft</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://codegym.vn/blog/2018/12/19/tin-tuc-phat-hanh-php-7-3-google-da-pha-hoai-trinh-duyet-cua-microsoft/#Lo_ngai_bi_lam_dung_Google_tuyen_bo_khong_ban_cong_nghe_nhan_dang_khuon_mat\">Lo ngại bị lạm dụng, Google tuy&ecirc;n bố kh&ocirc;ng b&aacute;n c&ocirc;ng nghệ nhận dạng khu&ocirc;n mặt</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://codegym.vn/blog/2018/12/19/tin-tuc-phat-hanh-php-7-3-google-da-pha-hoai-trinh-duyet-cua-microsoft/#Bai_viet_lien_quan\">B&agrave;i viết li&ecirc;n quan</a></span></li>\r\n</ul>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">PHP 7.3 cải thiện cơ chế nh&uacute;ng m&atilde; C v&agrave; tốc độ xử l&yacute;</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Phi&ecirc;n bản PHP 7.3 l&agrave; phi&ecirc;n bản mới nhất của ng&ocirc;n ngữ lập tr&igrave;nh phổ biến PHP được tung ra v&agrave;o đầu th&aacute;ng 12.2018. Phi&ecirc;n bản n&agrave;y được cập nhật với c&aacute;c t&iacute;nh năng mới v&agrave; cải thiện về tốc độ xử l&yacute;. Một trong số c&aacute;c cải tiến lớn nhất đ&oacute; l&agrave; việc hỗ trợ Giao diện H&agrave;m Ngoại (FFI &ndash; Foreign Function Interface), cho ph&eacute;p lập tr&igrave;nh vi&ecirc;n viết m&atilde; C b&ecirc;n trong m&atilde; PHP. Mặc d&ugrave; t&iacute;nh năng ng&agrave;y kh&ocirc;ng mang lại hiệu năng xử l&yacute; như khi l&agrave;m việc với m&atilde; PHP thuần nhưng trong một số t&igrave;nh huống cụ thể th&igrave; n&oacute; c&oacute; thể gi&uacute;p giảm được dung lượng bộ nhớ cần để xử l&yacute; một t&aacute;c vụ.</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://codegym.vn/blog/2018/12/14/tin-tuc-amazon-co-ban-java-rieng-google-cong-bo-cac-noi-dung-duoc-tim-kiem-nhieu-nhat-nam-2018/\">Tin tức: Amazon c&oacute; bản Java ri&ecirc;ng, Google c&ocirc;ng bố c&aacute;c nội dung được t&igrave;m kiếm nhiều nhất năm 2018</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://codegym.vn/blog/2018/12/11/tin-tuc-trinh-duyet-cua-microsoft-su-dung-nhan-chromium-google-dong-cua-som/\">Tin tức: Tr&igrave;nh duyệt của Microsoft sử dụng nh&acirc;n Chromium, Google+ đ&oacute;ng cửa sớm</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://codegym.vn/blog/2018/12/04/tin-tuc-google-co-ke-hoach-phat-trien-go-2-samsung-van-dan-dau-nganh-dien-thoai-di-dong/\">Tin tức: Google c&oacute; kế hoạch ph&aacute;t triển Go 2, Samsung vẫn dẫn đầu ng&agrave;nh điện thoại di động</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://codegym.vn/blog/2018/11/30/tin-tuc-ai-co-the-phan-biet-giong-noi-cua-nhieu-nguoi-phan-mem-ban-ve-cua-vff-te-hon-san-pham-cua-sinh-vien-nam-3/\">Tin tức: AI c&oacute; thể ph&acirc;n biệt giọng n&oacute;i của nhiều người, phần mềm b&aacute;n v&eacute; của VFF tệ hơn sản phẩm của sinh vi&ecirc;n năm 3</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://codegym.vn/blog/2018/11/29/tin-tuc-ai-duoc-su-dung-de-mo-ta-anh-microsoft-som-vuot-mat-apple/\">Tin tức: AI được sử dụng để m&ocirc; tả ảnh, Microsoft sớm vượt mặt Apple</a></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Xem th&ecirc;m tại:&nbsp;<a href=\"https://www.techrepublic.com/article/php-7-3-brings-c-inlining-code-readability-and-speed-improvements-to-the-programming-language/\">https://www.techrepublic.com/article/php-7-3-brings-c-inlining-code-readability-and-speed-improvements-to-the-programming-language/</a></span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh c&oacute; tốc độ tăng trưởng nhanh nhất năm 2018</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong năm 2018, theo thống k&ecirc; của GitHub, sự tăng trưởng của c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh được tập trung v&agrave;o c&aacute;c ng&ocirc;n ngữ c&oacute; c&uacute; ph&aacute;p kiểu dữ liệu cố định (statically typed). Đứng đầu danh s&aacute;ch l&agrave; ng&ocirc;n ngữ Kotlin, trong khi TypeScript v&agrave; Rust cũng c&oacute; được tốc độ tăng trưởng đ&aacute;ng kể. Ngo&agrave;i ra, HCI &ndash; ng&ocirc;n ngữ được sử dụng cho DevOps cũng ghi nhận số contributor tăng gấp đ&ocirc;i so với năm 2017. Ng&ocirc;n ngữ Python &ndash; ng&ocirc;n ngữ phổ biến được sử dụng trong học m&aacute;y (machine learning) th&igrave; vững v&agrave;ng ở vị tr&iacute; số 8. Số lượng contributor của ng&ocirc;n ngữ Go tăng l&ecirc;n 1,5 lần so với năm ngo&aacute;i.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Xem th&ecirc;m tại:&nbsp;<a href=\"https://fossbytes.com/fastest-growing-languages-in-2018-github/\">https://fossbytes.com/fastest-growing-languages-in-2018-github/</a></span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Thử nghiệm AI mới nhất của Google cho ph&eacute;p bạn điều khiển cả d&agrave;n giao hưởng ngay trong tr&igrave;nh duyệt</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Theo đ&oacute;, Semi-Conductor l&agrave; một AI c&oacute; khả năng theo d&otilde;i chuyển động của bạn như thể bạn đang vẫy tay trước mặt một d&agrave;n nhạc giao hưởng thực thụ v&agrave; tạo ra những t&aacute;c phẩm &acirc;m nhạc trong thời gian thực. Để thử AI th&uacute; vị n&agrave;y, bạn h&atilde;y v&agrave;o trang&nbsp;Semi-Conductor&nbsp;tr&ecirc;n tr&igrave;nh duyệt Google Chrome, cho ph&eacute;p n&oacute; truy cập webcam, đứng l&ugrave;i lại cho đến khi hai tay bạn dang rộng ra vừa với khung h&igrave;nh tr&ecirc;n trang web, v&agrave; bắt đầu chỉ huy d&agrave;n nhạc th&ocirc;i. Bạn kh&ocirc;ng nhất thiết phải sử dụng c&aacute;c động t&aacute;c vung tay th&ocirc;ng thường, nhưng AI của Google sẽ đưa ra đề nghị gi&uacute;p bạn điều khiển d&agrave;n nhạc tốt hơn.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Xem th&ecirc;m tại:&nbsp;<a href=\"http://genk.vn/thu-nghiem-ai-moi-nhat-cua-google-cho-phep-ban-dieu-khien-ca-dan-giao-huong-ngay-trong-trinh-duyet-20181217215625856.chn\">http://genk.vn/thu-nghiem-ai-moi-nhat-cua-google-cho-phep-ban-dieu-khien-ca-dan-giao-huong-ngay-trong-trinh-duyet-20181217215625856.chn</a></span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Firefox &ldquo;quan ngại s&acirc;u sắc&rdquo; về việc Microsoft khai tử EdgeHTML chuyển sang d&ugrave;ng Chromium</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Edge chưa bao giờ là m&ocirc;̣t trình duy&ecirc;̣t ph&ocirc;̉ bi&ecirc;́n và tại thời đi&ecirc;̉m hi&ecirc;̣n tại nó chỉ chi&ecirc;́m 4,34% thị ph&acirc;̀n. Và trong khi Google hoan ngh&ecirc;nh hành đ&ocirc;̣ng của Microsoft, Mozilla lại tỏ ra kh&ocirc;ng hài lòng. Mozilla, t&ocirc;̉ chức phi lợi nhu&acirc;̣n đứng đằng sau trình duy&ecirc;̣t Firefox, tỏ ra v&ocirc; cùng lo lắng v&ecirc;̀ đ&ocirc;̣ng thái g&acirc;̀n đ&acirc;y của Microsoft cũng như vi&ecirc;̃n cảnh Google ngày càng th&acirc;u tóm internet toàn c&acirc;̀u. Sau t&acirc;́t cả, chính Google là c&ocirc;ng ty đảo ngược th&ecirc;́ đ&ocirc;̣c quy&ecirc;̀n của Microsoft tr&ecirc;n thị trường trình duy&ecirc;̣t đ&acirc;̀u những năm 2000.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Xem th&ecirc;m tại:&nbsp;<a href=\"http://genk.vn/firefox-quan-ngai-sau-sac-ve-viec-microsoft-khai-tu-edgehtml-chuyen-sang-dung-chromium-20181218140912902.chn\">http://genk.vn/firefox-quan-ngai-sau-sac-ve-viec-microsoft-khai-tu-edgehtml-chuyen-sang-dung-chromium-20181218140912902.chn</a></span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Cựu kỹ sư thực tập trong dự &aacute;n Edge: Google đ&atilde; ph&aacute; hoại tr&igrave;nh duyệt của Microsoft</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Họ c&oacute; thể bắt đầu t&iacute;ch hợp c&aacute;c c&ocirc;ng nghệ m&agrave; họ độc quyền, hay &iacute;t nhất l&agrave; c&oacute; quyền truy cập &lsquo;đặc biệt&rsquo;. Bạn c&oacute; thể tưởng tượng được kh&ocirc;ng, khi m&agrave; bất chợt c&aacute;c ứng dụng của Google bắt đầu thực thi tốt hơn hẳn mọi ứng dụng kh&aacute;c? Điều n&agrave;y đ&atilde; xảy ra rồi. Mới đ&acirc;y t&ocirc;i đ&atilde; l&agrave;m việc trong nh&oacute;m ph&aacute;t triển Edge, v&agrave; một trong những l&yacute; do ch&uacute;ng t&ocirc;i quyết định chấm dứt EdgeHTML l&agrave; bởi Google li&ecirc;n tục thay đổi c&aacute;c trang web khiến ch&uacute;ng kh&ocirc;ng hoạt động tốt tr&ecirc;n c&aacute;c tr&igrave;nh duyệt kh&aacute;c, v&agrave; ch&uacute;ng t&ocirc;i kh&ocirc;ng thể theo kịp được. V&iacute; dụ, mới đ&acirc;y họ th&ecirc;m một thẻ div rỗng ẩn tr&ecirc;n c&aacute;c video YouTube khiến t&iacute;nh năng tăng tốc phần cứng của ch&uacute;ng t&ocirc;i gặp vấn đề (đ&atilde; được khắc phục trong Windows 10 October 2018 Update).</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Xem th&ecirc;m tại:&nbsp;<a href=\"https://vnreview.vn/tin-tuc-kinh-doanh/-/view_content/content/2698057/cuu-ky-su-thuc-tap-trong-du-an-edge-google-da-pha-hoai-trinh-duyet-cua-microsoft\">https://vnreview.vn/tin-tuc-kinh-doanh/-/view_content/content/2698057/cuu-ky-su-thuc-tap-trong-du-an-edge-google-da-pha-hoai-trinh-duyet-cua-microsoft</a></span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">Lo ngại bị lạm dụng, Google tuy&ecirc;n bố kh&ocirc;ng b&aacute;n c&ocirc;ng nghệ nhận dạng khu&ocirc;n mặt</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Google kh&ocirc;ng muốn b&aacute;n c&ocirc;ng nghệ nhận dạng khu&ocirc;n mặt v&igrave; sợ n&oacute; sẽ bị lạm dụng cho những mục đ&iacute;ch xấu.&nbsp;Trong một b&agrave;i đăng blog về tr&iacute; tuệ nh&acirc;n tạo, SVP Kent Walker của Google cam kết sẽ kh&ocirc;ng b&aacute;n c&aacute;c API li&ecirc;n quan đến c&ocirc;ng nghệ nhận dạng khu&ocirc;n mặt. Vị gi&aacute;m đốc điều h&agrave;nh n&agrave;y tr&iacute;ch dẫn mối quan t&acirc;m về c&aacute;ch c&ocirc;ng nghệ c&oacute; thể bị lạm dụng cho c&aacute;c mục đ&iacute;ch xấu.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Xem th&ecirc;m tại:&nbsp;<a href=\"https://vnreview.vn/tin-tuc-kinh-doanh/-/view_content/content/2697359/lo-ngai-bi-lam-dung-google-tuyen-bo-khong-ban-cong-nghe-nhan-dang-khuon-mat\">https://vnreview.vn/tin-tuc-kinh-doanh/-/view_content/content/2697359/lo-ngai-bi-lam-dung-google-tuyen-bo-khong-ban-cong-nghe-nhan-dang-khuon-mat</a></span></p>\r\n', 'tin-tuc-phat-hanh-php-7-3-google-da-pha-hoai-trinh-duyet-cua-microsoft-3.jpg', 1, 2, 1, 37, NULL, '2020-04-15 13:34:14', '2020-06-13 16:20:17'),
(16, 'Asp.net Core những thay đổi lớn cho lập trình viên .NET', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">ASP.NET Core l&agrave; một open-source mới v&agrave; framework đa nền tảng (cross-platform) cho việc x&acirc;y dựng những ứng dụng hiện tại dựa tr&ecirc;n kết nối đ&aacute;m m&acirc;y, giống như web apps, IoT v&agrave; backend cho mobile.</span></p>\r\n', '<h1><span style=\"font-family:Arial,Helvetica,sans-serif\">1.&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core l&agrave; g&igrave;?</span></h1>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core l&agrave; một open-source mới v&agrave; framework đa nền tảng (cross-platform) cho việc x&acirc;y dựng những ứng dụng hiện tại dựa tr&ecirc;n kết nối đ&aacute;m m&acirc;y, giống như web apps, IoT v&agrave; backend cho mobile. Ứng dụng&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core c&oacute; thể chạy tr&ecirc;n .NET Core hoặc tr&ecirc;n phi&ecirc;n bản đầy đủ của .NET Framework. N&oacute; được thiết kế để cung cấp v&agrave; tối ưu development framework cho những dụng c&aacute;i m&agrave; được triển khai tr&ecirc;n đ&aacute;m m&acirc;y (clound) hoặc chạy on-promise. N&oacute; bao gồm c&aacute;c th&agrave;nh phần theo hướng module nhằm tối thiểu t&agrave;i nguy&ecirc;n v&agrave; chi ph&iacute; ph&aacute;t triển, như vậy bạn giữ lại được sự mềm giẻo trong việc x&acirc;y dựng giải ph&aacute;p của bạn. Bạn c&oacute; thể ph&aacute;t triển v&agrave; chạy những ứng dụng&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core đa nền tảng tr&ecirc;n Windows, Mac v&agrave; Linux. Đồng thời n&oacute; đ&atilde; trở th&agrave;nh một m&atilde; nguồn mở. Đ&acirc;y l&agrave; một thay đổi rất lớn v&agrave; theo m&igrave;nh l&agrave; quan trọng nhất của&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core. Điều m&agrave; trước đ&acirc;y kh&oacute; c&oacute; một lập tr&igrave;nh vi&ecirc;n n&agrave;o c&oacute; thể nghĩ đến. C&oacute; lẽ đ&oacute; cũng l&agrave; một xu thế m&agrave; c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh hiện nay đang hướng tới.</span></p>\r\n\r\n<h1><span style=\"font-family:Arial,Helvetica,sans-serif\">2.&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core ra đời trong ho&agrave;n cảnh n&agrave;o?</span></h1>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bản ph&aacute;t h&agrave;nh đầu ti&ecirc;n của&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;đ&atilde; xuất hiện c&aacute;ch đ&acirc;y 15 năm trước, n&oacute; l&agrave; một phần của .NET Framework. Từ đ&oacute;, h&agrave;ng triệu lập tr&igrave;nh vi&ecirc;n đ&atilde; sử dụng n&oacute; để x&acirc;y dựng những ứng dụng web tuyệt vời, v&agrave; tr&ecirc;n những năm đ&oacute; Microsoft đ&atilde; ph&aacute;t triển th&ecirc;m nhiều t&iacute;nh năng mới.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core c&oacute; một số thay đổi kiến tr&uacute;c lớn, đ&oacute; l&agrave; kết quả của việc học hỏi rất nhiều từ c&aacute;c framework module h&oacute;a kh&aacute;c.&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core kh&ocirc;ng c&ograve;n dựa tr&ecirc;n System.Web.dll nữa. N&oacute; được dựa tr&ecirc;n một tập hợp c&aacute;c g&oacute;i, c&aacute;c module hay cũng được gọi l&agrave; c&aacute;c Nuget packages. Điều n&agrave;y cho ph&eacute;p bạn tối ưu ứng dụng của bạn để chỉ bao gồm những packages n&agrave;o cần thiết. Lợi &iacute;ch của n&oacute; l&agrave; gi&uacute;p cho ứng dụng nhỏ hơn, bảo mật chặt chẽ hơn, giảm sự phức tạp, tối ưu hiệu suất hoạt động v&agrave; giảm chi ph&iacute;, thời gian cho việc ph&aacute;t triển.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Với&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core bạn đạt được những nền tảng cải tiến dưới đ&acirc;y:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Hợp nhất việc x&acirc;y dựng web UI v&agrave; web APIs</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">T&iacute;ch hợp những client-side frameworks hiện đại v&agrave; những luồng ph&aacute;t triển</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Hệ thống cấu h&igrave;nh dựa tr&ecirc;n m&ocirc;i trường đ&aacute;m m&acirc;y thật sự</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Dependency injection được x&acirc;y dựng sẵn</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">HTTP request được tối ưu nhẹ hơn</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">C&oacute; thể host tr&ecirc;n IIS hoặc self-host trong process của ri&ecirc;ng bạn</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Được x&acirc;y dựng tr&ecirc;n .NET Core, hỗ trợ thực sự app versioning</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Chuyển c&aacute;c thực thể, th&agrave;nh phần, module như những NuGet packages</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Những c&ocirc;ng cụ mới để đơn giản h&oacute;a qu&aacute; tr&igrave;nh ph&aacute;t triển web hiện đại</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">X&acirc;y dựng v&agrave; chạy đa nền tảng(Windows, Mac v&agrave; Linux)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">M&atilde; nguồn mở v&agrave; tập trung v&agrave;o cộng đồng</span></li>\r\n</ul>\r\n\r\n<h1><span style=\"font-family:Arial,Helvetica,sans-serif\">3. X&acirc;y dựng web UI v&agrave; web API sửng dụng&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core MVC</span></h1>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn c&oacute; thể tạo ra c&aacute;c ứng dụng web c&oacute; thể testing theo m&ocirc; h&igrave;nh MVC(Model-View-Controller)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn c&oacute; thể x&acirc;y dựng HTTP services c&aacute;i m&agrave; hỗ trợ nhiều định dạng v&agrave; c&oacute; đầy đủ hỗ trợ cho nội dung của dữ liệu trả về</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Razor cung cấp một ng&ocirc;n ngữ hiệu quả để tạo Views</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Tag Helper cho ph&eacute;p code server side tham gia v&agrave;o việc tạo v&agrave; render phần tử HTML</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Model Binding tự động &aacute;nh xạ dữ liệu từ HTTP request tới tham số của method action</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Model Validation tự động thực hiện validate client v&agrave; server</span></li>\r\n</ul>\r\n\r\n<h1><span style=\"font-family:Arial,Helvetica,sans-serif\">4. Ph&aacute;t triển client-side</span></h1>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core được thiết kế để t&iacute;ch hợp một c&aacute;ch li&ecirc;n tục với nhiều client-side frameworks, bao gồm AngularJS, KnockoutJS v&agrave; Bootstrap. Về phần n&agrave;y t&ocirc;i sẽ giới thiệu với c&aacute;c bạn ở những b&agrave;i viết sau</span></p>\r\n\r\n<h1><span style=\"font-family:Arial,Helvetica,sans-serif\">5. X&acirc;y dựng ứng dụng đầu ti&ecirc;n</span></h1>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Như vậy t&ocirc;i đ&atilde; giới thiệu một c&aacute;ch tổng quan c&aacute;c kh&aacute;i niệm của&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core. Giờ l&agrave; l&uacute;c ch&uacute;ng ta t&igrave;m hiểu cấu tr&uacute;c một project&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core MVC th&ocirc;ng qua một v&iacute; dụ đơn giản</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">5.1 C&agrave;i đặt Visual Studio and .NET Core</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&agrave;i đặt Visual Studio Community 2017. Chọn phi&ecirc;n bản download Community. Bỏ qua bước n&agrave;y nếu bạn đ&atilde; c&oacute; Visual Studio c&agrave;i đặt trong m&aacute;y</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://www.visualstudio.com/\" target=\"_blank\">Visual Studio 2017 Home page installer</a></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Chạy bản c&agrave;i đặt v&agrave; chọn những workloads b&ecirc;n dưới:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;v&agrave; web development</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">.NET Core cross-platform development</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://viblo.asia/uploads/cefe89f5-d46b-46b4-bb4a-b94dc66e42ba.png\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://viblo.asia/uploads/ab1a3b39-177f-4eeb-a642-cfee6b3ac608.png\" /></span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">5.2 Tạo một web app</span></h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Từ Visual Studio, chọn File &gt; New &gt; Project.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://viblo.asia/uploads/99113135-7c01-499d-a42a-07b00b361161.png\" /></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ho&agrave;n th&agrave;nh họp thoại New Project:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong phần b&ecirc;n tr&aacute;i, chọn .NET Core</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong phần giữa, chọn&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core Web Application(.NET Core)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">T&ecirc;n của project v&iacute; dụ l&agrave; &quot;MvcMovie&quot;</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhấn OK</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://viblo.asia/uploads/9514f966-f4e6-4ce0-b58f-e1e5aa3dc222.png\" style=\"height:392px; width:600px\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Visual Studio đ&atilde; sử dụng một template mặc định cho dự &aacute;n MVC bạn vừa tạo. Bạn c&oacute; một ứng dụng l&agrave;m việc b&acirc;y giờ bằng việc điền t&ecirc;n dự &aacute;n v&agrave; một v&agrave;i t&ugrave;y chọn. Điều n&agrave;y l&agrave; một dự &aacute;n bắt đầu đơn giản. Nhấn F5 để chạy ứng dụng trong chế độ debug hoặc Ctrl-F5 trong chế độ non-debug. C&aacute;c bạn c&oacute; thể quan s&aacute;t kết quả của trang chủ. Thật đơn giản phải kh&ocirc;ng ?<img alt=\"\" src=\"https://viblo.asia/uploads/4fc3b646-347a-42c7-8ca5-fa279b147a2e.png\" /></span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Visual studio bắt đầu với IIS Express v&agrave; chạy ứng dụng của bạn. Ch&uacute; &yacute; rằng thanh địa chỉ hiển thị dạng localhost:port# v&agrave; kh&ocirc;ng phải kiểu như&nbsp;<a href=\"http://example.com/\" target=\"_blank\">example.com</a>. Đ&oacute; l&agrave; bởi v&igrave; localhost l&agrave; hostname chuẩn cho m&aacute;y t&iacute;nh local của bạn. Khi Visual Studio tạo một dự &aacute;n web, một ramdom port được sử dụng cho web server. Trong h&igrave;nh tr&ecirc;n, số cổng l&agrave; 5000. Khi bạn chạy ứng dụng, bạn sẽ thấy một số cổng kh&aacute;c.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Việc chạy ứng dụng với Ctrl-F5(non-debug) cho ph&eacute;p bạn l&agrave;m code thay đổi, lưu file, refresh tr&igrave;nh duyệt v&agrave; thấy ngay những thay đổi của code. Nhiều lập tr&igrave;nh vi&ecirc;n th&iacute;ch sử dụng chế độ non-debug để nhanh ch&oacute;ng chạy ứng dụng v&agrave; xem những thay đổi. Điều n&agrave;y l&agrave; tương tự với ng&ocirc;n ngữ th&ocirc;ng dịch như php, python hay ruby,..</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Chạy với chế độ self-host. Một điểm mới trong&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core l&agrave; c&aacute;c bạn c&oacute; thể chạy server như một ứng dụng console thay v&igrave; host tr&ecirc;n IIS. Để d&ugrave;ng chế độ n&agrave;y, tại điểm bắt đầu với IIS ch&uacute;ng ta thay bằng bắt đầu với self-host(T&ecirc;n ch&iacute;nh l&agrave; t&ecirc;n ứng dụng)&nbsp;<img alt=\"\" src=\"https://viblo.asia/uploads/5e6ce6ea-c12c-4933-96dc-fa5167671e60.jpg\" style=\"height:337px; width:600px\" /></span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cửa sổ console ứng dụng</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://viblo.asia/uploads/31a34956-1eb8-49d9-9070-952080076748.jpg\" style=\"height:244px; width:600px\" /></span></p>\r\n\r\n<h1><span style=\"font-family:Arial,Helvetica,sans-serif\">6. Kết luận</span></h1>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Qua những giới thiệu về&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core trong b&agrave;i viết n&agrave;y, c&aacute;c bạn c&oacute; thể thất đ&acirc;y thực sự l&agrave; những thay đổi lớn cho những ai đ&atilde; quen với&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;trước kia. Giờ đ&acirc;y&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;đ&atilde; trở th&agrave;nh một open-source, đa nền tảng c&oacute; thể chạy tr&ecirc;n nhiều m&ocirc;i trường, thiết kế tối ưu cho clound, module h&oacute;a, hợp nhất&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;v&agrave;&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;MVC l&agrave;m một (chỉ c&ograve;n phi&ecirc;n bản MVC) v&agrave; rất nhiều thay đổi tuyệt vời kh&aacute;c. T&ocirc;i hy vọng sẽ chia sẻ nhiều hơn đến c&aacute;c bạn. Ch&uacute;c c&aacute;c bạn c&oacute; những kh&aacute;m ph&aacute; th&uacute; vị c&ugrave;ng&nbsp;<a href=\"http://asp.net/\" target=\"_blank\">ASP.NET</a>&nbsp;Core</span></p>\r\n', 'asp-net.png', 1, 3, 1, 458, NULL, '2020-04-15 13:41:29', '2020-07-19 13:36:55');
INSERT INTO `posts` (`id`, `title`, `description`, `contents`, `thumbnail`, `user_id`, `category_id`, `status`, `view`, `rate`, `created_at`, `updated_at`) VALUES
(17, 'Cách học Lập trình PHP hiệu quả nhất dành cho người mới bắt đầu', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhắc đến ng&ocirc;n ngữ Lập tr&igrave;nh PHP c&oacute; lẽ kh&ocirc;ng c&ograve;n xa lạ với sinh vi&ecirc;n IT. Đ&acirc;y l&agrave; một ng&ocirc;n ngữ m&atilde; nguồn mở, lại ho&agrave;n to&agrave;n miễn ph&iacute;, với việc học v&agrave; ph&aacute;t triển web nhanh ch&oacute;ng.</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"\" src=\"https://bachkhoa-aptech.edu.vn/upload/image/thu-vien-anh/1200-x-628-1-.png\" style=\"height:314px; width:600px\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>1. Chăm chỉ &#39;c&agrave;y k&eacute;o&#39; code</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Người xưa c&oacute; c&acirc;u &quot;trăm hay kh&ocirc;ng bằng quen tay&quot;, chỉ bằng việc code, code nữa, code m&atilde;i mới gi&uacute;p bạn nhanh ch&oacute;ng h&igrave;nh th&agrave;nh kỹ năng v&agrave; th&agrave;nh thạo nghề. Đừng v&igrave; những ph&uacute;t gi&acirc;y lười biếng m&agrave; copy &amp; paste những d&ograve;ng code, h&atilde;y tự viết lại để dần thuộc v&agrave; nhớ ch&uacute;ng l&acirc;u hơn.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>2. Bật IDE b&aacute;o lỗi khi code</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Chẳng c&oacute; g&igrave; l&agrave; ho&agrave;n hảo tuyệt đối cả, h&atilde;y chắc chắn bạn lu&ocirc;n bật chế độ hiển thị d&ograve;ng của IDE khi viết code v&agrave; để &yacute; b&aacute;o lỗi v&agrave; t&igrave;m c&aacute;ch khắc phục nhanh nhất. N&oacute; sẽ gi&uacute;p bạn hạn chế được việc mắc lỗi sau n&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>3. Kh&ocirc;ng ngừng hỏi, hỏi v&agrave; hỏi</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong l&uacute;c code, bạn gặp vấn đề g&igrave;, đừng ngần ngại v&agrave; giấu dốt, h&atilde;y lần lượt l&agrave;m theo c&aacute;c bước như sau: Tra Google t&igrave;m c&aacute;ch khắc phục, vẫn kh&ocirc;ng khắc phục được th&igrave; l&ecirc;n cộng đồng hội nh&oacute;m hỏi đ&aacute;p, vẫn chưa thể t&igrave;m được c&acirc;u giải đ&aacute;p th&igrave; mạnh dạn hỏi bạn b&egrave;, hỏi thầy c&ocirc; để c&oacute; được c&acirc;u trả lời ch&iacute;nh x&aacute;c nhất.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><em><img alt=\"Không ngừng học hỏi\" src=\"https://bachkhoa-aptech.edu.vn/upload/image/hoclptrinh.jpg\" style=\"height:450px; width:600px\" /></em></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Kh&ocirc;ng ngừng học hỏi v&agrave; tự học</em></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Việc tr&igrave; ho&atilde;n một vấn đề, rất c&oacute; thể sẽ k&eacute;o theo rất nhiều hệ quả ph&iacute;a sau, ch&iacute;nh v&igrave; vậy, khi gặp bất cứ một kh&oacute; khăn n&agrave;o, đừng lười t&igrave;m t&ograve;i v&agrave; &#39;đi hỏi&#39; để c&oacute; c&acirc;u giải đ&aacute;p đ&uacute;ng nhất.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>4. Thực h&agrave;nh vẫn lu&ocirc;n quan trọng hơn so với l&yacute; thuyết</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">L&yacute; thuyết gi&uacute;p bạn l&agrave;m thực h&agrave;nh đơn giản hơn, nhưng nếu bạn muốn hiểu s&acirc;u v&agrave; rộng hơn về&nbsp;<strong>ng&ocirc;n ngữ lập tr&igrave;nh PHP</strong>&nbsp;th&igrave; c&aacute;ch tốt nhất chỉ c&oacute; thể chăm chỉ thực h&agrave;nh, tự t&igrave;m hiểu th&ocirc;ng qua việc thực h&agrave;nh m&agrave; th&ocirc;i.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><em><img alt=\"\" src=\"https://bachkhoa-aptech.edu.vn/upload/image/hoc-lap-trinh(2).jpg\" style=\"height:400px; width:600px\" /></em></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Thực h&agrave;nh v&agrave; thực h&agrave;nh s&acirc;u li&ecirc;n tục</em></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>5. Tăng hiệu suất code</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y cũng l&agrave;&nbsp;<strong>c&aacute;ch học PHP hiệu quả</strong>&nbsp;d&agrave;nh cho bạn, khi việc code đối với bạn đ&atilde; dễ d&agrave;ng th&igrave; điều bạn quan t&acirc;m l&uacute;c &nbsp;n&agrave;y l&agrave; hiệu suất l&agrave;m việc. H&atilde;y đẩy nhanh hiệu suất l&ecirc;n, t&igrave;m c&aacute;ch để thu gọn code một c&aacute;ch ngắn nhất để tiết kiệm được thời gian v&agrave; c&ocirc;ng sức của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>6. Cập nhật kiến thức, xu hướng mới li&ecirc;n tục</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Để c&oacute; thể ph&aacute;t triển sự nghiệp c&ugrave;ng với ng&ocirc;n ngữ lập tr&igrave;nh PHP, th&igrave; việc theo s&aacute;t những thay đổi, thường xuy&ecirc;n cập nhật th&ocirc;ng tin về bản lỗi cũng như c&aacute;ch sửa chữa n&oacute; l&agrave; điều tất yếu bạn cần phải l&agrave;m. Cập nhật th&ecirc;m c&aacute;c h&agrave;m mới cũng l&agrave; c&aacute;ch bạn mở rộng tệp kiến thức của m&igrave;nh v&agrave; ứng dụng trong c&ocirc;ng việc.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><em><img alt=\"\" src=\"https://bachkhoa-aptech.edu.vn/upload/image/cach-tu-hoc-php.jpg\" style=\"height:400px; width:600px\" /></em></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><em>Lu&ocirc;n cập nhật kiến thức mới</em></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>7. T&igrave;m đến c&aacute;c trung t&acirc;m dạy thực h&agrave;nh chuy&ecirc;n nghiệp</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Lời khuy&ecirc;n cuối c&ugrave;ng d&agrave;nh cho bạn muốn&nbsp;<strong>học Lập tr&igrave;nh PHP hiệu quả</strong>, c&aacute;ch đơn giản v&agrave; nhanh ch&oacute;ng nhất l&agrave; đầu tư một kh&oacute;a học tại trung t&acirc;m thực h&agrave;nh chuy&ecirc;n nghiệp. Tại sao ư?</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">V&iacute; dụ đơn giản nhất, bạn phải mất v&agrave;i tiếng đến v&agrave;i ng&agrave;y mới t&igrave;m được c&acirc;u trả lời cho vấn đề m&igrave;nh gặp phải khi đăng l&ecirc;n diễn đ&agrave;n, cộng đồng. Tuy nhi&ecirc;n, bạn chỉ mất v&agrave;i ph&uacute;t khi hỏi đ&aacute;p trực tiếp giảng vi&ecirc;n tại trung t&acirc;m m&agrave; m&igrave;nh theo học.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tiếp đến, việc&nbsp;<strong>học lập tr&igrave;nh PHP tại trung t&acirc;m thực h&agrave;nh</strong>&nbsp;như Bachkhoa-Aptech, bạn được học c&ugrave;ng c&aacute;c giảng vi&ecirc;n l&agrave; những người c&oacute; gi&agrave;u kinh nghiệm thực chiến dự &aacute;n. Do đ&oacute;, bạn vừa được học kiến thức, kỹ năng chuy&ecirc;n m&ocirc;n bạn c&ograve;n được chia sẻ những b&iacute; k&iacute;p để &#39;sinh tồn&#39; trong doanh nghiệp.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Việc tự học vẫn lu&ocirc;n cần thiết đối với bất cứ ai, bất cứ ng&agrave;nh nghề n&agrave;o. Nhưng bạn vừa tự học, lại vừa c&oacute; sự định hướng từ những người c&oacute; chuy&ecirc;n m&ocirc;n vẫn hiệu quả nhanh hơn v&agrave; tốt hơn. Đặc biệt,&nbsp;<strong>học lập tr&igrave;nh PHP ở Bachkhoa-Aptech</strong>&nbsp;bạn c&ograve;n được tham gia l&agrave;m dự &aacute;n thực tế ngay từ khi nhập học, được cầm tay chỉ việc v&agrave; trải nghiệm m&ocirc;i trường học tập như một doanh nghiệp CNTT thu nhỏ, vừa trang bị kiến thức, kỹ năng chuy&ecirc;n m&ocirc;n, vừa được trang bị kỹ năng mềm để tự tin hội nhập.</span></p>\r\n', 'tintucPHP.png', 1, 2, 1, 43, NULL, '2020-04-15 13:47:45', '2020-07-15 07:47:24'),
(62, 'Lợi thế từ trí tuệ nhân tạo của người Nhật Bản', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">D&acirc;n số ng&agrave;y c&agrave;ng thu hẹp, trong khi người d&acirc;n trong nước vẫn c&ograve;n chưa cởi mở đối với người nhập cư h&oacute;a ra lại l&agrave; những yếu tố thuận lợi để đưa robot trở th&agrave;nh lực lượng lao động tại Nhật Bản.</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tỷ lệ sinh của Nhật Bản bước v&agrave;o v&ugrave;ng thấp nhất, k&eacute;o theo đ&oacute; l&agrave; sự suy giảm khoảng 10 triệu người trong độ tuổi lao động. Ước t&iacute;nh trong những thập ni&ecirc;n tới, nước n&agrave;y sẽ c&oacute; khoảng 20 triệu người rời khỏi thị trường lao động. T&igrave;nh trạng thiếu lao động kinh ni&ecirc;n của nền kinh tế lớn thứ ba thế giới, đặc biệt trong c&aacute;c lĩnh vực như điều dưỡng, sản xuất, x&acirc;y dựng v&agrave; giao h&agrave;ng bưu kiện, đang dần trở n&ecirc;n trầm trọng hơn khi tỷ lệ tuyển dụng t&iacute;nh theo đầu người l&agrave; 1:1,5. Điều đ&oacute; c&oacute; nghĩa, trung b&igrave;nh cứ một người Nhật th&igrave; sẽ c&oacute; tới 1,5 vị tr&iacute; tuyển dụng. Song, đối với một quốc gia vẫn kh&ocirc;ng mấy cởi mở với người nhập cư như Nhật Bản, th&igrave; c&oacute; lẽ lựa chọn sử dụng AI l&agrave; giải ph&aacute;p tốt nhất cho thị trường lao động.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&uacute;ng t&ocirc;i đ&atilde; thử c&aacute;c loại robot kh&aacute;c nhau để xem c&aacute;i n&agrave;o sẽ l&agrave;m việc tốt nhất. Ch&uacute;ng t&ocirc;i đ&atilde; dần tăng số lượng sử dụng v&agrave; hiện c&oacute; 20 m&ocirc; h&igrave;nh kh&aacute;c nhau đang hoạt động, bao gồm cả robot để chăm s&oacute;c điều dưỡng, phục hồi chức năng, truyền th&ocirc;ng v&agrave; giải tr&iacute;. Rất nhiều người nghĩ rằng người cao tuổi sẽ sợ h&atilde;i hoặc kh&ocirc;ng thoải m&aacute;i với robot, nhưng thực tế họ rất quan t&acirc;m cũng như tương t&aacute;c một c&aacute;ch tự nhi&ecirc;n với c&aacute;c robot. Họ th&iacute;ch n&oacute;i chuyện với c&aacute;c robot v&agrave; động lực của họ cũng tăng l&ecirc;n mỗi khi c&oacute; robot phục hồi chức năng gi&uacute;p đỡ.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong khi nhiều quốc gia đang lo lắng về t&igrave;nh trạng mất việc l&agrave;m do AI, th&igrave; Nhật Bản dường như kh&ocirc;ng mấy căng thẳng khi đối mặt với nền kinh tế tự động h&oacute;a. Theo số liệu được thống k&ecirc; hồi th&aacute;ng 4-2017, tỷ lệ thất nghiệp của nước n&agrave;y giảm xuống 2,8%, đồng thời c&oacute; tới 97,6% sinh vi&ecirc;n mới tốt nghiệp c&oacute; được việc l&agrave;m ngay sau khi ra trường.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tuy nhi&ecirc;n, d&ugrave; thị trường lao động Nhật Bản ghi nhận nhiều lợi nhuận kỷ lục, nhưng lợi &iacute;ch vẫn được ph&acirc;n phối kh&ocirc;ng đồng đều, việc tăng lương c&ograve;n chậm chạp. Số lượng lao động thủ c&ocirc;ng tại đ&acirc;y vẫn thiếu hụt, trong khi nh&acirc;n vi&ecirc;n văn ph&ograve;ng lại dư thừa, đặc biệt l&agrave; lao động nam trong độ tuổi trung ni&ecirc;n,&nbsp;</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hệ thống tăng lương theo th&acirc;m ni&ecirc;n l&agrave;m việc của&nbsp;<a href=\"http://duhoc.thanhgiang.com.vn/nhung-cam-bay-cua-du-hoc-nhat-ban\" rel=\"nofollow\" target=\"_blank\" title=\"du học Nhật Bản\">Nhật Bản</a>&nbsp;hiện kh&ocirc;ng c&ograve;n bền vững v&agrave; ph&ugrave; hợp nữa, đặc biệt khi d&acirc;n số đất nước ng&agrave;y c&agrave;ng gi&agrave; đi. &Ocirc;ng Yashiro cho biết c&aacute;c c&ocirc;ng ty Nhật Bản đang cố gắng ngăn chặn mức tăng lương tự động cho lao động trong độ tuổi 40 &ndash; 50, thay v&agrave;o đ&oacute; tăng lương cho những người trẻ hơn, nhất l&agrave; đối tượng lao động tr&igrave;nh độ trung b&igrave;nh, c&oacute; nguy cơ bị đe dọa nhiều nhất từ l&agrave;n s&oacute;ng robot.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hầu hết c&aacute;c cuộc tranh luận về chủ đề tự động h&oacute;a sẽ lấy mất việc l&agrave;m của người lao động thường kh&ocirc;ng phải l&agrave; một vấn đề ở Nhật Bản. Lao động ở đ&acirc;y cũng kh&ocirc;ng bị ảnh hưởng nặng nề như ở Anh hay Mỹ. Tuy nhi&ecirc;n, tầng lớp lao động c&oacute; tr&igrave;nh độ c&ocirc;ng nghệ cao sẽ được hưởng lợi nhiều hơn từ hệ thống mới. Do đ&oacute;, những người lao động ở tr&igrave;nh độ trung b&igrave;nh cần được bảo vệ khỏi sự tr&igrave; trệ về lương.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">B&ecirc;n cạnh những &yacute; kiến đ&aacute;nh gi&aacute; cao AI, vẫn c&oacute; một số người cho rằng tự động h&oacute;a kh&ocirc;ng phải l&agrave; giải ph&aacute;p tốt nhất cho c&aacute;c vấn đề về lao động của Nhật Bản. Sự thiếu hụt c&ocirc;ng nh&acirc;n trong c&aacute;c ng&agrave;nh c&ocirc;ng nghiệp kỹ thuật hiện nay thật khủng khiếp. Nhưng nếu ch&uacute;ng t&ocirc;i trả lương v&agrave; giảm giờ l&agrave;m theo gợi &yacute; từ ch&iacute;nh phủ, ch&uacute;ng t&ocirc;i sẽ ph&aacute; sản. Tự động h&oacute;a cũng kh&ocirc;ng phải l&agrave; c&acirc;u trả lời x&aacute;c đ&aacute;ng, m&agrave; thay v&agrave;o đ&oacute; t&ocirc;i nghĩ ch&uacute;ng t&ocirc;i cần nhiều lao động từ nước ngo&agrave;i c&oacute; mức lương thấp hơn.</span></p>\r\n', 'tri-tue-nhan-tao-cua-nguoi-nhat-ban.jpg', 1, 5, 1, 35, NULL, '2020-04-24 15:36:20', '2020-07-08 05:57:27'),
(63, 'iPhone SE 2020 loạn giá', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Mẫu iPhone mới được nhiều cửa h&agrave;ng đẩy gi&aacute; l&ecirc;n gần 13 triệu đồng, trong khi c&oacute; nơi b&aacute;n 11 triệu đồng.</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://vnexpress.net/apple-ra-mat-iphone-se-gia-re-moi-4085329.html\" rel=\"dofollow\">iPhone SE</a>&nbsp;2020 đ&atilde; c&oacute; mặt ở Việt Nam từ tối qua (23/4) dưới dạng h&agrave;ng x&aacute;ch tay.&nbsp;Tại một hệ thống ở TP HCM, gi&aacute; iPhone SE 2020 bản 64, 128 v&agrave; 256 GB lần lượt l&agrave; 12,8, 13,4 v&agrave; 16,8 triệu đồng. So với th&ocirc;ng b&aacute;o&nbsp;đặt h&agrave;ng tuần trước, gi&aacute;&nbsp;đ&atilde; bị đẩy cao hơn 1 đến 2 triệu đồng. Trong khi đ&oacute;, tại H&agrave; Nội, mẫu iPhone mới nhất của Apple được b&aacute;n lần lượt l&agrave;&nbsp;11,9, 12,9 v&agrave; 14,9 triệu đồng, tương ứng với từng mức dung lượng.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"iPhone SE 2020 đã có mặt ở Việt Nam từ tối 23/4. Ảnh: Bảo Lâm.\" src=\"https://i1-sohoa.vnecdn.net/2020/04/24/iPhone-SE-2020-3482-1587712249.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_prrvgurnDOO_J4M8mHqYw\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">iPhone SE 2020 đ&atilde; c&oacute; mặt ở Việt Nam từ tối 23/4. Ảnh:&nbsp;<em>Bảo L&acirc;m.</em></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tương tự, gi&aacute;&nbsp;<a href=\"https://vnexpress.net/iphone-se-2020-chinh-hang-co-gia-du-kien-12-trieu-dong-4085336.html\" rel=\"dofollow\">iPhone SE 2020 ch&iacute;nh h&atilde;ng</a>&nbsp;cũng bị loạn giữa c&aacute;c cửa h&agrave;ng d&ugrave; cuối th&aacute;ng 5 mới b&aacute;n ch&iacute;nh thức. Với ba bản 64, 128 v&agrave; 256 GB, gi&aacute;&nbsp;ch&iacute;nh h&atilde;ng tại c&aacute;c chuỗi cửa h&agrave;ng lớn lần lượt l&agrave; 12, 13,6 v&agrave; 16,6 triệu đồng. Nhưng c&aacute;c cửa h&agrave;ng nhỏ b&aacute;n rẻ hơn một triệu đồng, v&iacute; dụ, bản 64 GB ch&iacute;nh h&atilde;ng c&ograve;n&nbsp;11 triệu đồng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Với c&aacute;c mức tr&ecirc;n, iPhone SE 2020 h&agrave;ng x&aacute;ch tay đang cao hơn gi&aacute; ch&iacute;nh h&atilde;ng 1 đến 2 triệu đồng, tuỳ từng phi&ecirc;n bản. C&ograve;n so với gi&aacute; gốc tại thị trường Mỹ, iPhone SE 2020&nbsp;ở thị trường x&aacute;ch tay đắt hơn từ 3 đến 4 triệu đồng. Tại Apple Store Mỹ, ba phi&ecirc;n bản 64, 128 v&agrave; 256 GB lần lượt l&agrave; 399, 499 v&agrave; 549 USD, tương đương 9,2; 10,4 v&agrave;&nbsp;12,7 triệu đồng.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">T&igrave;nh trạng loạn gi&aacute; của iPhone SE tr&ecirc;n thị trường x&aacute;ch tay&nbsp;l&agrave; do sản phẩm mới c&oacute; h&agrave;ng v&agrave; số lượng hạn chế. Tuy nhi&ecirc;n, mẫu iPhone n&agrave;y kh&ocirc;ng được nhiều người Việt ưa chuộng. Theo ghi nhận tại nhiều cửa h&agrave;ng, lượng kh&aacute;ch hỏi mua, đặt h&agrave;ng k&eacute;m hơn cả&nbsp;iPad.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Với tầm 12 triệu đồng của iPhone SE, người d&ugrave;ng sẽ c&acirc;n nhắc mua iPhone 8 Plus, iPhone XR hoặc chi th&ecirc;m để l&ecirc;n iPhone 11 hay iPhone XS Max.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">iPhone SE 2020 ra mắt &acirc;m thầm v&agrave;o ng&agrave;y&nbsp;15/4. Sản phẩm c&oacute; gi&aacute; b&aacute;n rẻ nhất trong d&ograve;ng iPhone đang c&oacute; của Apple. M&aacute;y d&ugrave;ng chip A13 giống bộ ba iPhone 11, camera đơn nhưng c&oacute; t&iacute;nh năng chụp ảnh ch&acirc;n dung dựa v&agrave;o thuật to&aacute;n AI. Ngoại h&igrave;nh, k&iacute;ch thước m&agrave;n h&igrave;nh v&agrave; pin đều giống iPhone 8, nhưng kh&ocirc;ng c&oacute; m&agrave;u v&agrave;ng.&nbsp;</span></p>\r\n', 'ipse2.jpg', 1, 49, 1, 20, 3.75, '2020-04-24 16:37:48', '2020-07-04 02:44:18'),
(64, 'Xiaomi sắp ra smartphone chụp ảnh 150 megapixel', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Xiaomi c&oacute; thể tr&igrave;nh l&agrave;ng smartphone đầu ti&ecirc;n với cảm biến ảnh 150 &quot;chấm&quot; của Samsung cuối năm nay.</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Chuy&ecirc;n gia c&ocirc;ng nghệ&nbsp;<em>Ice Universe</em>&nbsp;tiết lộ tr&ecirc;n Twitter rằng&nbsp;Samsung đ&atilde; ph&aacute;t triển th&agrave;nh c&ocirc;ng cảm biến ảnh 150 megapixel d&agrave;nh cho thiết bị di động.&nbsp;Sau Xiaomi, một số c&ocirc;ng ty Trung Quốc kh&aacute;c, như Oppo v&agrave; Vivo, được cho l&agrave; cũng sẽ&nbsp;t&iacute;ch hợp cảm biến 150 megapixel tr&ecirc;n smartphone ra mắt đầu 2021.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trước đ&oacute;, Xiaomi v&agrave; Samsung đ&atilde; hợp t&aacute;c nghi&ecirc;n cứu&nbsp;v&agrave; sản xuất&nbsp;Isocell Bright HM1 108 megapixel - cảm biến ảnh được&nbsp;Xiaomi đưa v&agrave;o mẫu&nbsp;Mi CC9 Pro năm 2019.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Xiaomi CC9 ra cuối năm ngoái là mẫu smartphone đầu tiên trang bị cảm biến ảnh 108 megapixel. Ảnh: Xiaomi.\" src=\"https://i1-sohoa.vnecdn.net/2020/04/24/Mi-7475-1587711802.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=TtSwDGj_AUXUJIqVDOTNgA\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Xiaomi CC9 2019 l&agrave; smartphone đầu ti&ecirc;n d&ugrave;ng cảm biến ảnh 108 megapixel. Ảnh:&nbsp;<em>Xiaomi</em>.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Samsung đang nghi&ecirc;n cứu camera với độ ph&acirc;n giải si&ecirc;u cao v&agrave; k&iacute;ch thước nhỏ d&agrave;nh cho thiết bị di động.&nbsp;Isocell Bright HM1 sử dụng kỹ thuật gh&eacute;p điểm ảnh Nanocell, cho ph&eacute;p ghi lại h&igrave;nh ảnh sắc n&eacute;t v&agrave; chi tiết. B&ecirc;n cạnh đ&oacute;,&nbsp;c&ocirc;ng nghệ Isocell Plus với lưới ngăn c&aacute;ch giữa c&aacute;c điểm ảnh gi&uacute;p giảm hiện tượng nhiễu m&agrave;u, cũng như thu nhận &aacute;nh s&aacute;ng tốt hơn.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&aacute;c với cảm biến cũ d&ugrave;ng lưới kim loại dễ phản chiếu hoặc hấp thụ &aacute;nh s&aacute;ng ngo&agrave;i &yacute; muốn, tấm ngăn tr&ecirc;n Isocell Plus được chế tạo từ vật liệu đặc biệt của Fujifilm. Năm 2018, Naoto Yanagihara, Ph&oacute; chủ tịch Fujifilm, nhận định việc hợp t&aacute;c với Samsung&nbsp;l&agrave; bước tiến đ&aacute;ng ghi nhận&nbsp;trong qu&aacute; tr&igrave;nh thương mại h&oacute;a vật liệu mới cũng như đem lại cải tiến hữu &iacute;ch cho camera di động&quot;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Công nghệ ISOCELL Plus giúp tăng độ nhạy sáng của cảm biến, đồng thời cho phép ghi lại bức ảnh với màu sắc chân thực hơn. Ảnh: Samsung.\" src=\"https://i1-sohoa.vnecdn.net/2020/04/24/camera-5149-1587711802.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=qvPofRxNCLyqZ45MqpMfPA\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&ocirc;ng nghệ ISOCELL Plus gi&uacute;p tăng độ nhạy s&aacute;ng của cảm biến. Ảnh:&nbsp;<em>Samsung</em>.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đầu tuần n&agrave;y, Yongin Park, đứng đầu bộ phận kinh doanh cảm biến tại Samsung LSI, cho biết h&atilde;ng&nbsp;l&ecirc;n kế hoạch x&acirc;y dựng&nbsp;cảm biến ảnh l&ecirc;n tới&nbsp;600 megapixel, cao hơn cả độ ph&acirc;n giải của mắt người&nbsp;(576 megapixel), d&agrave;nh cho smartphone, xe tự h&agrave;nh, drone v&agrave; c&aacute;c thiết bị IoT</span></p>\r\n', 'Mi-7475-1587711802.jpg', 1, 49, 1, 22, NULL, '2020-04-24 16:49:22', '2020-07-15 16:50:13'),
(65, 'Chip AI mới mắt nhân tạo phân tích hình ảnh chỉ trong vài nano giây', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&ocirc;ng nghệ mới sẽ l&agrave; trợ thủ đắc lực cho machine vision.</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">V&agrave;i năm trở lại đ&acirc;y, c&ocirc;ng nghệ machine vision - m&aacute;y nh&igrave;n đạt được nhiều đột ph&aacute;, đ&atilde; trở th&agrave;nh phần kh&ocirc;ng thể thiếu của c&aacute;c hệ thống t&iacute;ch hợp tr&iacute; tuệ nh&acirc;n tạo, v&iacute; dụ như robot l&agrave;m việc hay xe tự l&aacute;i.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Th&ocirc;ng thường, th&ocirc;ng tin dưới dạng h&igrave;nh ảnh đi v&agrave;o camera sẽ được biến đổi sang dạng số, thuật to&aacute;n sẽ l&atilde;nh tr&aacute;ch nhiệm xử l&yacute; ch&uacute;ng. Lượng dữ liệu đầu v&agrave;o thường rất lớn (m&agrave; đa số trong số đ&oacute; lại l&agrave; dữ liệu thừa), khi đi qua một loạt c&aacute;c thay đổi biến dữ liệu từ dạng n&agrave;y sang dạng kh&aacute;c, kết quả cuối c&ugrave;ng sẽ vừa c&oacute; tốc độ khung h&igrave;nh thấp m&agrave; qu&aacute; tr&igrave;nh xử l&yacute; dữ liệu lại rất tốn năng lượng. Tuy nhi&ecirc;n, ta c&oacute; tin tốt rồi đ&acirc;y.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Chip AI mới cho phép mắt nhân tạo phân tích xong hình ảnh chỉ trong vài nano giây - Ảnh 1.\" id=\"img_159884090009456640\" src=\"https://genk.mediacdn.vn/thumb_w/660/139269124445442048/2020/3/5/photo-1-15833833408121765743805.jpg\" title=\"Chip AI mới cho phép mắt nhân tạo phân tích xong hình ảnh chỉ trong vài nano giây - Ảnh 1.\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c nh&agrave; nghi&ecirc;n cứu tại Viện Lượng tử &Aacute;nh s&aacute;ng Vienna, &Aacute;o tạo ra một loại mắt nh&acirc;n tạo mới, kết hợp linh kiện cảm biến &aacute;nh s&aacute;ng với một mạng neural, đặt vừa cả hai tr&ecirc;n một con chip nhỏ. Ch&uacute;ng c&oacute; khả năng xử l&yacute; h&igrave;nh ảnh chỉ trong v&agrave;i nano-gi&acirc;y, nhanh hơn bất kỳ cảm biến h&igrave;nh ảnh n&agrave;o khoa học đang sở hữu.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Thiết kế c&ocirc;ng nghệ mới vừa được đăng tải tr&ecirc;n tạp ch&iacute; khoa học Nature, cho thấy nh&oacute;m nghi&ecirc;n cứu đ&atilde; vận dụng sự kỳ diệu của tự nhi&ecirc;n v&agrave;o c&ocirc;ng nghệ: họ t&igrave;m c&aacute;ch bắt chước qu&aacute; tr&igrave;nh xử l&yacute; h&igrave;nh ảnh của mắt động vật - những con mắt c&oacute; thể xử l&yacute; xong được dữ liệu đầu v&agrave;o trước khi đưa t&iacute;n hiệu về n&atilde;o bộ.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đội ngũ c&aacute;c nh&agrave; khoa học l&agrave;m chip từ một tấm Tungsten diselenide (WSe2, một hợp chất của vonfram v&agrave; selen) với độ d&agrave;y tương đương v&agrave;i nguy&ecirc;n tử xếp s&aacute;t nhau, tr&ecirc;n tấm WSe2 l&agrave; h&agrave;ng loạt diode cảm biến &aacute;nh s&aacute;ng. Sau đ&oacute;, họ gắn con chip n&agrave;y v&agrave;o một mạng neural.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">WSe2 khiến con chip mang tr&ecirc;n m&igrave;nh thuộc t&iacute;nh điện đặc biệt, cho ph&eacute;p c&aacute;c nh&agrave; khoa học dễ d&agrave;ng điều chỉnh được độ nhạy s&aacute;ng của c&aacute;c diode. Điều n&agrave;y đồng nghĩa với việc mạng neural c&oacute; thể được học một loạt phương ph&aacute;p x&aacute;c định th&ocirc;ng tin h&igrave;nh ảnh đầu v&agrave;o chỉ bằng việc chỉnh độ nhạy s&aacute;ng của diode, chỉnh li&ecirc;n tục cho tới khi ch&uacute;ng cho ra kết quả ch&iacute;nh x&aacute;c.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bằng c&aacute;ch n&agrave;y, con chip c&oacute; thể nhanh ch&oacute;ng nhận biết được những chữ c&aacute;i n, v v&agrave; z được viết c&aacute;ch điệu.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cảm biến mới cho ph&eacute;p c&ocirc;ng nghệ machine vision &ldquo;nh&igrave;n&rdquo; được nhanh hơn, hiệu quả hơn, thế nhưng chặng đường ứng dụng vẫn c&ograve;n d&agrave;i lắm: &ldquo;con mắt&rdquo; n&agrave;y chỉ chứa được 27 cảm biến &aacute;nh s&aacute;ng v&agrave; chỉ nh&igrave;n được h&igrave;nh ảnh cỡ 3x3.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Vẫn c&oacute; những đặc điểm khiến c&ocirc;ng nghệ n&agrave;y khả quan hơn trước, đủ để c&aacute;c nh&agrave; nghi&ecirc;n cứu tin tưởng rằng việc tăng quy m&ocirc; hệ thống sẽ kh&ocirc;ng qu&aacute; phức tạp. Cụ thể, con chip c&oacute; thể tự m&igrave;nh thực hiện những t&aacute;c vụ m&aacute;y học m&agrave; kh&ocirc;ng cần sự can thiệp của c&aacute;c nh&agrave; khoa học, v&iacute; dụ như x&aacute;c định v&agrave; m&atilde; h&oacute;a k&yacute; tự.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Khi c&oacute; thể &ldquo;thả&rdquo; cho hệ thống tr&iacute; tuệ nh&acirc;n tạo tự xử l&yacute; h&igrave;nh ảnh để học dần, ta sẽ bớt được thời gian gi&aacute;m s&aacute;t ch&uacute;ng.</span></p>\r\n', 'chipAI.jpg', 1, 5, 1, 17, NULL, '2020-04-24 16:52:19', '2020-07-04 03:24:01'),
(66, 'Bộ sạc 65W của Xiaomi bị phát hiện vi phạm an ninh', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Xiaomi đ&atilde; thu hồi bộ sạc cực nhanh 65W với c&ocirc;ng nghệ gallium nitride (Xiaomi GaN Type-C 65W) m&agrave; c&ocirc;ng ty b&aacute;n đồng thời với loạt smartphone Mi 10.</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">L&yacute; do đằng sau việc loại bỏ c&aacute;c phụ kiện phổ biến khỏi kệ h&agrave;ng bắt nguồn từ lỗ hổng tiềm năng của n&oacute; đối với tin tặc. Cụ thể, bộ sạc nhanh Xiaomi c&oacute; thể bị hack từ xa v&agrave; do đ&oacute; g&acirc;y hại cho người d&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Bộ sạc 65W của Xiaomi bị phát hiện vi phạm an ninh - 1\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-22/1587565090-562-02-1587522589-width660height440.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c chuy&ecirc;n gia bảo mật nhận thấy rằng bộ sạc Xiaomi GaN Type-C 65W c&oacute; chip bộ nhớ được sử dụng để khắc phục c&aacute;c sự cố tiềm ẩn v&agrave; cập nhật giao thức sạc.&nbsp;Đồng thời, phần firmware kh&ocirc;ng m&atilde; h&oacute;a, nghĩa l&agrave; tin tặc c&oacute; cơ hội thay đổi n&oacute;. Một kịch bản đơn giản nhất l&agrave; tin tặc c&oacute; thể khai th&aacute;c lỗ hổng n&agrave;y, sau đ&oacute; &aacute;p dụng điện &aacute;p đầu ra rất cao, từ đ&oacute; l&agrave;m hỏng c&aacute;c thiết bị đang được sạc.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c nguồn tin từ c&aacute;c k&ecirc;nh cung cấp đ&atilde; x&aacute;c nhận rằng Xiaomi đ&atilde; loại bỏ bộ sạc n&agrave;y khỏi tất cả c&aacute;c nền tảng kỹ thuật số, cũng như từ c&aacute;c cửa h&agrave;ng b&aacute;n lẻ, với nguy&ecirc;n nh&acirc;n gọi l&agrave; &ldquo;l&yacute; do khẩn cấp&rdquo;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bộ sạc Xiaomi GaN Type-C 65W c&oacute; thể cung cấp c&ocirc;ng suất sạc tối đa 50W cho Xiaomi Mi 10 Pro, nơi n&oacute; cho ph&eacute;p sạc đầy pin chỉ trong 45 ph&uacute;t. Đồng thời, bộ sạc n&agrave;y hỗ trợ sạc nhanh PD 3.0 v&agrave; c&aacute;c giao thức sạc nhanh kh&aacute;c của Xiaomi. Ngo&agrave;i ra, người d&ugrave;ng c&oacute; thể sử dụng n&oacute; để sạc iPhone. C&ocirc;ng ty cho biết, sử dụng bộ sạc 65W để sạc iPhone 11, tốc độ sạc cao hơn bộ sạc 5W gốc khoảng 50%.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Bộ sạc 65W của Xiaomi bị phát hiện vi phạm an ninh - 2\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-22/1587565090-133-03-1587522596-width660height408.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhờ vật liệu b&aacute;n dẫn mới GaN (gallium nitride), k&iacute;ch thước của bộ sạc n&agrave;y nhỏ hơn khoảng 48% so với bộ sạc ti&ecirc;u chuẩn tr&ecirc;n m&aacute;y t&iacute;nh x&aacute;ch tay Xiaomi.&nbsp;Ngo&agrave;i ra, giao diện USB-C của Xiaomi GaN Type-C 65W hỗ trợ điều chỉnh th&ocirc;ng minh d&ograve;ng điện đầu ra trong nhiều cấp. N&oacute; c&oacute; thể sạc tới 65W cho c&aacute;c thiết bị c&oacute; c&ocirc;ng suất cao như MacBook Pro v&agrave; m&aacute;y t&iacute;nh x&aacute;ch tay Xiaomi mới. Cuối c&ugrave;ng, g&oacute;i sản phẩm chứa c&aacute;p dữ liệu Xiaomi USB-C-to-USB-C được trang bị chip E-Marker v&agrave; hỗ trợ d&ograve;ng điện tối đa 5A</span></p>\r\n', 'sac-xiaomi.jpg', 1, 49, 1, 9, NULL, '2020-04-24 16:56:29', '2020-06-02 16:00:27'),
(67, 'Làm sao để trẻ có thể vừa học vừa chơi ngay tại nhà', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Chỉ cần v&agrave;i thao t&aacute;c truy cập c&aacute;c ứng dụng TV, bố mẹ c&oacute; thể c&ugrave;ng c&aacute;c b&eacute; vừa học vừa chơi một c&aacute;ch v&ocirc; c&ugrave;ng trực quan, sinh động.</span></p>\r\n', '<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">&nbsp;Học to&aacute;n, học tiếng Anh, kh&aacute;m ph&aacute; thế giới khoa học&hellip; nay trở n&ecirc;n hứng th&uacute; hơn bao giờ hết, b&agrave;i to&aacute;n &ldquo;l&agrave;m sao để con vừa học vừa chơi ngay tại nh&agrave;&rdquo; nhờ vậy cũng t&igrave;m được lời giải đ&aacute;p.</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Những chiếc TV thời nay đ&atilde; chứng tỏ độ th&ocirc;ng minh của c&ocirc;ng nghệ nhờ v&agrave;o kho ứng dụng phong ph&uacute;, mang đến cho người d&ugrave;ng nhiều trải nghiệm từ giải tr&iacute;, thể thao đến tin tức, lối sống... Đơn cử như những chiếc TV Samsung Crystal UHD thế hệ mới như TU8500 được thiết kế với nhiều nội dung tiếng Anh bổ &iacute;ch, trong đ&oacute;, POPs Kids c&oacute; lẽ l&agrave; ứng vi&ecirc;n s&aacute;ng gi&aacute;. Th&ocirc;ng qua kho phim hoạt h&igrave;nh n&oacute;i tiếng Anh, c&aacute;c b&agrave;i h&aacute;t tiếng Anh tr&ecirc;n ứng dụng n&agrave;y, b&eacute; sẽ được tiếp x&uacute;c v&agrave; thẩm thấu ng&ocirc;n ngữ mới theo c&aacute;ch tự nhi&ecirc;n. Đặc biệt, nếu sử dụng smart TV từ Samsung, b&eacute; c&ograve;n c&oacute; thể c&ugrave;ng cha mẹ &ocirc;n luyện kiến thức Anh ngữ b&agrave;i bản, th&uacute; vị v&agrave; sống động th&ocirc;ng qua chương tr&igrave;nh học tiếng Anh chuẩn Cambridge t&iacute;ch hợp trong chiếc TV n&agrave;y.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Làm sao để trẻ có thể vừa học vừa chơi ngay tại nhà - 1\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-22/Lam-sao-de-tre-co-the-vua-hoc-vua-choi-ngay-tai-nha-giai-tri--1--1587529221-429-width660height431.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Pops Kids c&oacute; giao diện th&acirc;n thiện, nhiều m&agrave;u sắc để trẻ c&oacute; thể tự mở được c&aacute;c nội dung b&ecirc;n trong ứng dụng.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đặc biệt, trong năm 2020, Samsung c&ugrave;ng với POPS Kids phối hợp thực hiện chương tr&igrave;nh gi&aacute;o dục giải tr&iacute; mới cho trẻ - STEM (Science, Technology, Engineering, Mathematics &ndash; Khoa học, C&ocirc;ng nghệ, Kĩ thuật v&agrave; To&aacute;n học). Chương tr&igrave;nh sẽ được ph&aacute;t triển theo gi&aacute;o tr&igrave;nh của trung t&acirc;m Teky v&agrave; sẽ sớm được ra mắt trong năm nay. Với chương tr&igrave;nh STEM c&oacute; sẵn tr&ecirc;n chiếc Smart TV Samsung, c&aacute;c b&eacute; c&oacute; thể vừa học vừa chơi trong thế giới khoa học kỳ th&uacute; v&agrave; đầy m&agrave;u sắc.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Khi c&aacute;c &ldquo;tiết học&rdquo; tại gia tạm ngưng, b&eacute; c&oacute; thể tiếp tục giải tr&iacute; theo đ&uacute;ng sở th&iacute;ch cũng tr&ecirc;n ứng dụng n&agrave;y. Nếu ưa nhảy m&uacute;a, chương tr&igrave;nh ca nhạc Mầm Chồi L&aacute; sẽ l&agrave; ứng vi&ecirc;n s&aacute;ng gi&aacute;. Với c&aacute;c b&eacute; đam m&ecirc; hội họa, chương tr&igrave;nh Si&ecirc;u nh&acirc;n B&uacute;t Ch&igrave; sẽ gi&uacute;p b&eacute; kh&aacute;m ph&aacute; thế giới đầy m&agrave;u sắc qua lăng k&iacute;nh trẻ thơ. Ngo&agrave;i ra, POP Kids c&ograve;n c&oacute; những bộ phim hoạt h&igrave;nh được nhiều b&eacute; y&ecirc;u th&iacute;ch như Doraemon, Maruko, The Powerpuff Girls&hellip;</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Mẫu TV Samsung Crystal UHD vừa ra mắt trong năm 2020 &ndash; TU8500 hứa hẹn l&agrave; một người bạn đồng h&agrave;nh s&aacute;ng gi&aacute; để thể hiện tất cả nội dung n&oacute;i tr&ecirc;n một c&aacute;ch sống động, ch&acirc;n thực. C&oacute; gi&aacute; b&aacute;n chỉ từ 13,4 triệu đồng nhưng TU8500 lại được trang bị nhiều c&ocirc;ng nghệ mới v&agrave; thiết kế cao cấp. Với Dynamic Crystal Display, TU8500 mang đến chất lượng h&igrave;nh ảnh vượt trội với dải m&agrave;u rộng, m&agrave;u sắc sống động v&agrave; rực rỡ hơn với tỷ sắc m&agrave;u. Để n&acirc;ng cao độ ch&iacute;nh x&aacute;c của m&agrave;u sắc, TU8500 c&oacute; c&ocirc;ng nghệ Dual LED với 2 dải đ&egrave;n LED với t&ocirc;ng m&agrave;u ấm v&agrave; lạnh gi&uacute;p kiểm so&aacute;t m&agrave;u sắc đ&egrave;n nền tốt hơn, từ đ&oacute; tăng cường độ tương phản của m&agrave;u sắc l&ecirc;n 27%. Hơn nữa, d&ograve;ng TV n&agrave;y c&ograve;n được hỗ trợ bởi bộ xử l&yacute; UHD 4K gi&uacute;p xử l&yacute; h&igrave;nh ảnh v&agrave; &acirc;m thanh tối ưu, đồng thời n&acirc;ng cấp</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Làm sao để trẻ có thể vừa học vừa chơi ngay tại nhà - 2\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-22/Lam-sao-de-tre-co-the-vua-hoc-vua-choi-ngay-tai-nha-giai-tri--2--1587529221-539-width660height440.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Mhi&ecirc;n bản mới của POPS Kids sẽ mang đến những chương tr&igrave;nh dạy tiếng Anh ph&ugrave; hợp nhất với trẻ em Việt Namn thực. ưa nhảy m&uacute;a, chương tr&igrave;nh ca nhạc Mầm Chồi L&aacute; sẽ l&agrave; ứng vi&ecirc;n s&aacute;ng gi&aacute;. Với c&aacute;c b&eacute; đam m&ecirc; hội họahất lượng tốt nhất c&oacute; thể. Điều n&agrave;y sẽ giảm thiểu chứng mỏi mắt cho cả gia đ&igrave;nh so với việc sửa dụng những chiếc TV th&ocirc;ng thường.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ngo&agrave;i ra, TU8500 c&ograve;n t&iacute;ch hợp những khả năng đặc biệt m&agrave; như chức năng hỗ trợ chơi game để gi&uacute;p cả nh&agrave; thư gi&atilde;n v&agrave; chế độ Chia đ&ocirc;i m&agrave;n h&igrave;nh tiện lợi (Multi-view) cho ph&eacute;p bố mẹ vừa gi&uacute;p đỡ con &ocirc;n b&agrave;i tr&ecirc;n TV, vừa kh&ocirc;ng phải lo sợ những cuộc gọi nhỡ hay tin nhắn c&ocirc;ng việc từ điện thoại của m&igrave;nh.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">B&ecirc;n cạnh đ&oacute;, TU8500 cũng được xem l&agrave; d&ograve;ng TV UHD đ&aacute;ng tiền nhất khi sở hữu thiết kế tinh tế với kh&ocirc;ng viền 3 cạnh c&ugrave;ng độ mỏng ho&agrave;n hảo. Điều n&agrave;y l&agrave;m cho TV Crystal UHD 4K TU8500 như một bức tranh thuần khiết kh&ocirc;ng viền mang đến cho cả nh&agrave; trải nghiệm xem ho&agrave;n hảo v&agrave; đậm chất điện ảnh. T&iacute;nh năng mới cho TV UHD &ndash; Photo Wall c&oacute; thể biến TV th&agrave;nh khung ảnh gia đ&igrave;nh để người d&ugrave;ng c&oacute; thể tạm biệt m&agrave;n h&igrave;nh đen nh&agrave;m ch&aacute;n khi kh&ocirc;ng xem TV.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Làm sao để trẻ có thể vừa học vừa chơi ngay tại nhà - 3\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-22/Lam-sao-de-tre-co-the-vua-hoc-vua-choi-ngay-tai-nha-giai-tri--3--1587529221-861-width660height440.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&iacute;nh v&igrave; vậy, TU8500 xứng đ&aacute;ng l&agrave; một lựa chọn cho cả nh&agrave; bởi t&iacute;nh tiện dụng cao v&agrave; thiết kế sang trọng m&agrave; chiếc TV n&agrave;y đem lại. Với tất cả ưu điểm từ nội dung đến kỹ thuật tr&ecirc;n, TU8500 hứa hẹn l&agrave; người thầy, người bạn đồng h&agrave;nh c&ugrave;ng b&eacute; những ng&agrave;y kh&ocirc;ng đến trường. Để khi trở lại trường, kiến thức học được sẽ tạo đ&agrave; để b&eacute; nhanh ch&oacute;ng bắt nhịp học tập.</span></p>\r\n', 'Lam-sao-de-co-the-vua-hoc-vua-choi-ngay-tai-nha-giai-tri.jpg', 1, 49, 1, 59, 3.5, '2020-04-24 16:58:42', '2020-07-17 04:32:35');
INSERT INTO `posts` (`id`, `title`, `description`, `contents`, `thumbnail`, `user_id`, `category_id`, `status`, `view`, `rate`, `created_at`, `updated_at`) VALUES
(68, 'Làm thế nào để giải quyết những vấn đề chụp ảnh ban đêm', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Chụp được tấm ảnh về đ&ecirc;m đẹp chuẩn chỉnh tưởng dễ nhưng lại kh&oacute; v&ocirc; c&ugrave;ng, khi c&aacute;c vấn đề nan giải lu&ocirc;n c&ograve;n đ&oacute; l&agrave;m cho t&aacute;c phẩm nhận được thường khiến ch&uacute;ng ta tụt &quot;mood&quot;.</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Rất nhiều hoạt động th&uacute; vị, l&atilde;ng mạn thường diễn ra về đ&ecirc;m như c&aacute;c buổi tiệc, hẹn h&ograve;, kh&aacute;m ph&aacute; vui chơi c&ugrave;ng bạn b&egrave;, người y&ecirc;u,&hellip; Đặc biệt thời gian n&agrave;y ở nh&agrave;, mọi người c&ugrave;ng tận hưởng kh&ocirc;ng kh&iacute; gia đ&igrave;nh ấm c&uacute;ng qua bữa ăn tối, c&aacute;c th&agrave;nh vi&ecirc;n qu&acirc;y quần b&ecirc;n nhau coi TV hay c&ugrave;ng b&agrave;y tr&ograve; chơi tập thể. Trong kh&ocirc;ng gian trang tr&iacute; &aacute;nh đ&egrave;n lấp l&aacute;nh c&agrave;ng th&ocirc;i th&uacute;c ch&uacute;ng ta chụp h&igrave;nh, lưu lại những khoảnh khắc thật đẹp. Tuy nhi&ecirc;n &quot;đời kh&ocirc;ng như l&agrave; mơ&quot; khi d&ugrave;ng ch&iacute;nh chiếc smartphone của m&igrave;nh bởi th&agrave;nh quả bạn nhận được thường mắc một trong ba vấn đề kh&oacute; chịu sau đ&acirc;y khiến bức h&igrave;nh tr&ocirc;ng như&hellip; ảnh trong phim kinh dị.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Kh&ocirc;ng gian chụp r&otilde; đẹp, ảnh ra thấy tối thui</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y hẳn l&agrave; vấn đề kh&oacute; chịu ch&uacute;ng ta gặp phải nhiều nhất. Trong khi khung cảnh chung quanh s&aacute;ng r&otilde;, lung linh lộng lẫy nhưng qua camera điện thoại, bức ảnh bạn c&oacute; được lại chỉ c&oacute; một m&agrave;u đen chủ đạo, kh&ocirc;ng nh&igrave;n r&otilde; mặt người được chụp.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Làm thế nào để giải quyết những vấn đề chụp ảnh này ban đêm: mờ nhoè,  thiếu sáng, vỡ ảnh...?  - 1\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-17/Lam-the-nao-de-giai-quyet-nhung-van-de-chup-anh-nay-ban-dem-mo-nhoe-thieu-sang-vo-anh-anh--9--1587085873-356-width660height371.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ở nh&agrave; hai cha con kỳ c&ocirc;ng setup kh&ocirc;ng gian r&otilde; đẹp, kh&ocirc;ng chụp lại th&igrave; qu&aacute; ph&iacute;.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Làm thế nào để giải quyết những vấn đề chụp ảnh này ban đêm: mờ nhoè,  thiếu sáng, vỡ ảnh...?  - 2\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-17/Lam-the-nao-de-giai-quyet-nhung-van-de-chup-anh-nay-ban-dem-mo-nhoe-thieu-sang-vo-anh-anh--10--1587085873-734-width660height371.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhưng th&agrave;nh quả nhận được tối như thế n&agrave;y th&igrave; hẳn chẳng ai th&iacute;ch được</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">H&igrave;nh chụp l&agrave; để lưu giữ khoảnh khắc đẹp, chia sẻ ch&uacute;ng với mọi người nhưng ch&iacute;nh những tấm ảnh &quot;trường ph&aacute;i hắc &aacute;m&quot; n&agrave;y khiến bạn kh&ocirc;ng những tụt cảm x&uacute;c m&agrave; nhiều khi c&ograve;n cảm thấy tự ti kh&ocirc;ng d&aacute;m đăng l&ecirc;n mạng x&atilde; hội.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Làm thế nào để giải quyết những vấn đề chụp ảnh này ban đêm: mờ nhoè,  thiếu sáng, vỡ ảnh...?  - 3\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-17/Lam-the-nao-de-giai-quyet-nhung-van-de-chup-anh-nay-ban-dem-mo-nhoe-thieu-sang-vo-anh-anh--8--1587085873-934-width660height371.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tr&ograve; vui vẻ thế n&agrave;y khi chụp lại đăng l&ecirc;n mạng bao h&uacute;t like v&agrave; comment&hellip;</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Làm thế nào để giải quyết những vấn đề chụp ảnh này ban đêm: mờ nhoè,  thiếu sáng, vỡ ảnh...?  - 4\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-17/Lam-the-nao-de-giai-quyet-nhung-van-de-chup-anh-nay-ban-dem-mo-nhoe-thieu-sang-vo-anh-7-1587086142-869-width660height371.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">&hellip; một lần nữa giới hạn chụp thiếu s&aacute;ng của camera điện thoại khiến bạn tụt mood</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>H&igrave;nh bị nhiễu hạt (noise) qu&aacute; nhiều</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Một vấn đề cũng khiến ch&uacute;ng ta kh&oacute; chịu kh&ocirc;ng k&eacute;m, đ&oacute; l&agrave; bức ảnh chụp đ&ecirc;m bị nhiễu hạt khiến c&aacute;c chi tiết, m&agrave;u sắc bị bệt lại, kh&ocirc;ng c&ograve;n r&otilde; n&eacute;t. Ch&iacute;nh điều đ&oacute; khiến bức h&igrave;nh bạn chụp ra tr&ocirc;ng như t&aacute;c phẩm lỗi khi từ chủ thể ch&iacute;nh trong bức h&igrave;nh tới kh&ocirc;ng gian xung quanh bị phủ l&ecirc;n những chấm nhỏ li ti g&acirc;y rối mắt v&ocirc; c&ugrave;ng.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Làm thế nào để giải quyết những vấn đề chụp ảnh này ban đêm: mờ nhoè,  thiếu sáng, vỡ ảnh...?  - 5\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-17/Lam-the-nao-de-giai-quyet-nhung-van-de-chup-anh-nay-ban-dem-mo-nhoe-thieu-sang-vo-anh-anh--7--1587085873-858-width660height347.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Khoảnh khắc dễ thương của hai mẹ con nhưng nhiễu hạt l&agrave;m giảm độ đẹp của tấm h&igrave;nh gấp nhiều lần</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Làm thế nào để giải quyết những vấn đề chụp ảnh này ban đêm: mờ nhoè,  thiếu sáng, vỡ ảnh...?  - 6\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-17/Lam-the-nao-de-giai-quyet-nhung-van-de-chup-anh-nay-ban-dem-mo-nhoe-thieu-sang-vo-anh-anh--6--1587085873-167-width660height348.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ai cũng đẹp chỉ trừ bức ảnh kh&ocirc;ng đẹp v&igrave; bị nhiễu hạt nặng</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Chụp h&igrave;nh bị mờ nh&ograve;e cũng l&agrave; nỗi đau đầu kh&ocirc;ng tưởng</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Để hạn chế khuyết điểm thiếu s&aacute;ng v&agrave; nhiễu hạt trong chụp h&igrave;nh đ&ecirc;m, nhiều smartphone hiện nay &aacute;p dụng c&aacute;ch hạ tốc độ chụp xuống thấp nhằm gi&uacute;p camera thu giữ nhiều &aacute;nh s&aacute;ng hơn. Tuy vậy ch&iacute;nh điều đ&oacute; lại dẫn tới vấn đề &quot;cười ra nước mắt&quot; kh&aacute;c khi chủ thể trong h&igrave;nh kh&ocirc;ng c&ograve;n r&otilde;, thường bị mờ nh&ograve;e. Một bức h&igrave;nh d&ugrave; s&aacute;ng hay mịn m&agrave;ng cũng kh&ocirc;ng c&ograve;n &yacute; nghĩa khi ch&iacute;nh bạn hay nh&acirc;n vật được chụp chẳng hề sắc n&eacute;t. Những tấm ảnh như vậy thường chỉ c&ograve;n c&aacute;ch x&oacute;a bỏ đi chứ đ&acirc;u d&aacute;m gửi hay khoe cho ai đ&uacute;ng kh&ocirc;ng n&agrave;o?</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Làm thế nào để giải quyết những vấn đề chụp ảnh này ban đêm: mờ nhoè,  thiếu sáng, vỡ ảnh...?  - 7\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-17/Lam-the-nao-de-giai-quyet-nhung-van-de-chup-anh-nay-ban-dem-mo-nhoe-thieu-sang-vo-anh-anh--3--1587085873-541-width660height346.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn đ&atilde; cố gắng đứng y&ecirc;n khi chụp ảnh nhưng người kh&aacute;c th&igrave; kh&ocirc;ng</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Làm thế nào để giải quyết những vấn đề chụp ảnh này ban đêm: mờ nhoè,  thiếu sáng, vỡ ảnh...?  - 8\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-17/Lam-the-nao-de-giai-quyet-nhung-van-de-chup-anh-nay-ban-dem-mo-nhoe-thieu-sang-vo-anh-anh--5--1587085873-859-width660height346.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Cặp đ&ocirc;i n&agrave;o từng hẹn h&ograve; v&agrave;o buổi đ&ecirc;m hẳn đều đ&atilde; c&oacute; những tấm ảnh mờ nh&ograve;e đ&aacute;ng qu&ecirc;n như thế n&agrave;y</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>&quot;B&iacute; k&iacute;p&quot; cho những tấm h&igrave;nh chụp đ&ecirc;m chuẩn chỉnh</strong></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Điều kiện thiếu s&aacute;ng khi về đ&ecirc;m l&agrave; nguy&ecirc;n nh&acirc;n ch&iacute;nh khiến camera tr&ecirc;n điện thoại thường &quot;b&oacute; tay&quot; trong việc tạo n&ecirc;n bức ảnh s&aacute;ng r&otilde; v&agrave; sắc n&eacute;t như l&uacute;c ch&uacute;ng ta chụp ban ng&agrave;y. Giải ph&aacute;p &quot;chống ch&aacute;y&quot; một số bạn đ&atilde; thử l&agrave; d&ugrave;ng đ&egrave;n flash hoặc c&aacute;c thiết bị trợ s&aacute;ng kh&aacute;c nhưng hoặc &aacute;nh s&aacute;ng kh&ocirc;ng tự nhi&ecirc;n hoặc phải cực kỳ mất c&ocirc;ng chuẩn bị đồ đạc lỉnh kỉnh.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu đang kiếm t&igrave;m mẫu &quot;cạ cứng&quot; mới cho ra những tấm h&igrave;nh đẹp bất chấp điều kiện s&aacute;ng hay tối, th&igrave; bạn n&ecirc;n chờ đợi sự xuất hiện của chiếc vivo V19 sắp được ra mắt trong th&aacute;ng 4 n&agrave;y. Những h&igrave;nh ảnh về sản phẩm n&agrave;y chưa được tiết lộ nhiều nhưng được nh&agrave; sản xuất hứa hẹn về khả năng chụp ảnh đ&ecirc;m si&ecirc;u ấn tượng, đ&uacute;ng với thế mạnh camera trước giờ của nh&agrave; vivo.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Làm thế nào để giải quyết những vấn đề chụp ảnh này ban đêm: mờ nhoè,  thiếu sáng, vỡ ảnh...?  - 9\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-17/Lam-the-nao-de-giai-quyet-nhung-van-de-chup-anh-nay-ban-dem-mo-nhoe-thieu-sang-vo-anh-anh--2--1587085873-752-width660height344.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Làm thế nào để giải quyết những vấn đề chụp ảnh này ban đêm: mờ nhoè,  thiếu sáng, vỡ ảnh...?  - 10\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-17/Lam-the-nao-de-giai-quyet-nhung-van-de-chup-anh-nay-ban-dem-mo-nhoe-thieu-sang-vo-anh-anh--1--1587085873-338-width660height340.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Làm thế nào để giải quyết những vấn đề chụp ảnh này ban đêm: mờ nhoè,  thiếu sáng, vỡ ảnh...?  - 11\" src=\"https://cdn.24h.com.vn/upload/2-2020/images/2020-04-17/Lam-the-nao-de-giai-quyet-nhung-van-de-chup-anh-nay-ban-dem-mo-nhoe-thieu-sang-vo-anh-anh--4--1587085873-712-width660height350.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">vivo V19 c&oacute; những trang bị, c&ocirc;ng nghệ g&igrave; để thổi bay hết những kh&oacute; chịu kể tr&ecirc;n khi chụp h&igrave;nh ban đ&ecirc;m? Mọi thứ sắp được bật m&iacute; trong th&aacute;ng 4 n&agrave;y</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Th&ocirc;ng tin ban đầu, vivo V19 sẽ sở hữu thiết kế m&agrave;n h&igrave;nh nốt ruồi k&eacute;p, đ&acirc;y đang l&agrave; xu hướng mới tr&ecirc;n c&aacute;c mẫu flagship năm nay. Hai camera selfie ph&iacute;a trước c&ugrave;ng bốn camera sau sẽ mang đến sự đa dạng về g&oacute;c chụp, đồng thời khả năng chụp &ldquo;bừng s&aacute;ng m&agrave;n đ&ecirc;m&rdquo; được ch&uacute; trọng c&agrave;ng hấp dẫn th&ecirc;m đối với c&aacute;c t&iacute;n đồ y&ecirc;u th&iacute;ch nhiếp ảnh bằng điện thoại.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Với một sản phẩm sở hữu thiết kế mới thời thượng đi c&ugrave;ng khả năng chụp ảnh &quot;xịn s&ograve;&quot; như vivo V19, ắt hẳn bạn sẽ mong chờ một mức gi&aacute; cũng hấp dẫn kh&ocirc;ng k&eacute;m. Mọi th&ocirc;ng tin chi tiết của si&ecirc;u phẩm n&agrave;y sẽ được ch&iacute;nh thức v&eacute;n m&agrave;n trong v&agrave;i ng&agrave;y tới, bạn h&atilde;y đ&oacute;n chờ nh&eacute;!</span></p>\r\n', 'chup-anh-ban-dem-mo-nhoe.jpg', 1, 49, 1, 55, 4.5, '2020-04-24 17:00:52', '2020-07-16 07:41:58'),
(69, 'Java được sử dụng ở đâu trong thế giới thực?', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu bạn l&agrave; một người mới bắt đầu học lập tr&igrave;nh Java, bạn c&oacute; thể nghĩ rằng liệu Java được sử dụng thực tế ở đ&acirc;u?</span></p>\r\n', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Học lập trình Java cơ bản đến nâng cao\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/hoc-lap-trinh-java-29082016-1.jpg\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu bạn l&agrave; một người mới bắt đầu&nbsp;<a href=\"https://techmaster.vn/khoa-hoc/25467/lap-trinh-java-can-ban\" target=\"_blank\">học lập tr&igrave;nh Java,</a>&nbsp;bạn c&oacute; thể nghĩ rằng liệu Java được sử dụng thực tế ở đ&acirc;u? Bạn kh&ocirc;ng thấy nhiều tr&ograve; game được viết bằng Java, ngoại trừ game Minecraft, c&aacute;c c&ocirc;ng cụ desktop như Adobe Acrobat, Microsoft Office kh&ocirc;ng được viết bằng Java, c&aacute;c hệ điều h&agrave;nh như Linux hay Windows cũng vậy, thế th&igrave; Java ch&iacute;nh x&aacute;c được sử dụng ở nơi n&agrave;o? Liệu n&oacute; c&oacute; bất kỳ ứng dụng n&agrave;o trong thế giới thực hay kh&ocirc;ng? V&acirc;ng, bạn kh&ocirc;ng phải l&agrave; người duy nhất thắc mắc điều đ&oacute;, rất nhiều lập tr&igrave;nh vi&ecirc;n đ&atilde; đặt ra c&acirc;u hỏi n&agrave;y trước khi bắt đầu với Java, hoặc sau khi chọn Java l&agrave; một trong những ng&ocirc;n ngữ lập tr&igrave;nh ở trường đại học.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">D&ugrave; sao th&igrave; bạn cũng đ&atilde; c&oacute; một manh mối về nơi Java được sử dụng khi c&agrave;i đặt Java v&agrave;o m&aacute;y t&iacute;nh để b&agrave;n của bạn, Oracle cho biết c&oacute; hơn 3 tỷ thiết bị chạy Java, đ&oacute; l&agrave; một con số rất lớn phải kh&ocirc;ng n&agrave;o? Hầu hết c&aacute;c c&ocirc;ng ty lớn đều sử dụng Java theo c&aacute;ch n&agrave;y hay c&aacute;ch kh&aacute;c. Nhiều ứng dụng ph&iacute;a m&aacute;y chủ được viết bằng Java để xử l&yacute; h&agrave;ng chục triệu request mỗi ng&agrave;y, c&aacute;c ứng dụng giao dịch chứng kho&aacute;n cũng được viết bằng Java, v&iacute; dụ ứng dụng LMAX được x&acirc;y dựng tr&ecirc;n thư viện&nbsp;<a href=\"https://lmax-exchange.github.io/disruptor/\" target=\"_blank\">Disruptor</a>. Trong b&agrave;i viết n&agrave;y, ch&uacute;ng ta sẽ thấy ch&iacute;nh x&aacute;c hơn, những loại dự &aacute;n n&agrave;o được viết bằng Java, Java thống trị ở trong ng&agrave;nh hoặc lĩnh vực n&agrave;o v&agrave; ch&iacute;nh x&aacute;c nơi n&agrave;o Java được sử dụng hữu &iacute;ch trong thực tế?</span></p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\"><u>C&aacute;c ứng dụng Java trong thế giới thực</u></span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Java được sử dụng ở rất nhiều nơi trong thế giới thực, từ c&aacute;c trang web thương mại điện tử đến c&aacute;c ứng dụng Android, từ ứng dụng khoa học đến ứng dụng t&agrave;i ch&iacute;nh như c&aacute;c hệ thống giao dịch điện tử, từ c&aacute;c tr&ograve; game như Minecraft cho đến c&aacute;c ứng dụng desktop như Eclipse, Netbeans v&agrave; IntelliJ, từ thư viện m&atilde; nguồn mở tới c&aacute;c ứng dụng J2ME v.v... Ch&uacute;ng ta h&atilde;y xem x&eacute;t một c&aacute;ch chi tiết hơn nh&eacute;!</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">1. C&aacute;c ứng dụng Android</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu bạn muốn nh&igrave;n thấy nơi Java được sử dụng, th&igrave; bạn chẳng phải đi đ&acirc;u xa x&ocirc;i cả. H&atilde;y mở điện thoại Android của bạn l&ecirc;n với bất kỳ ứng dụng n&agrave;o, ch&uacute;ng đều được viết bằng ng&ocirc;n ngữ lập tr&igrave;nh Java, với Android API của Google, c&aacute;i tương tự như JDK. V&agrave;i năm gần đ&acirc;y Android đ&atilde; ph&aacute;t triển rất nhanh v&agrave; nhiều lập tr&igrave;nh vi&ecirc;n Java đ&atilde; trở th&agrave;nh&nbsp;<a href=\"https://vinacode.net/2016/06/30/hay-tham-gia-cong-dong-va-hoc-hoi-tu-nhung-nguoi-di-truoc/\" target=\"_blank\">c&aacute;c nh&agrave; ph&aacute;t triển ứng dụng Android.</a>&nbsp;Android sử dụng m&aacute;y ảo JVM v&agrave; c&aacute;c package kh&aacute;c, nhưng phần code th&igrave; vẫn được viết bằng Java.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">2. Ứng dụng m&aacute;y chủ d&ugrave;ng cho dịch vụ t&agrave;i ch&iacute;nh</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Java đ&oacute;ng vai tr&ograve; rất lớn trong c&aacute;c dịch vụ t&agrave;i ch&iacute;nh. Rất nhiều ng&acirc;n h&agrave;ng đầu tư to&agrave;n cầu như Goldman Sachs, Citigroup, Barclays, Standard Charted v&agrave; c&aacute;c ng&acirc;n h&agrave;ng kh&aacute;c sử dụng Java để viết c&aacute;c hệ thống giao dịch điện tử, c&aacute;c hệ thống x&aacute;c nhận v&agrave; kiểm to&aacute;n, c&aacute;c dự &aacute;n xử l&yacute; dữ liệu v&agrave; một số c&ocirc;ng việc quan trọng kh&aacute;c. Java chủ yếu được sử dụng để viết c&aacute;c ứng dụng ph&iacute;a m&aacute;y chủ, hầu như kh&ocirc;ng c&oacute; bất kỳ phần front-end n&agrave;o, n&oacute; nhận dữ liệu từ một m&aacute;y chủ (upstream), xử l&yacute; n&oacute; v&agrave; gửi dữ liệu đến một quy tr&igrave;nh kh&aacute;c (downstream). Java Swing cũng được d&ugrave;ng phổ biến để tạo ra c&aacute;c phần giao diện người d&ugrave;ng.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">3. C&aacute;c ứng dụng web Java</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Java cũng được sử dụng nhiều trong c&aacute;c ứng dụng web v&agrave; thương mại điện tử. Bạn c&oacute; rất nhiều RESTfull services được tạo ra sử dụng Spring MVC, Struts 2.0 v&agrave; c&aacute;c framework tương tự. Ngay cả c&aacute;c ứng dụng web đơn giản dựa tr&ecirc;n Servlet, JSP v&agrave; Struts cũng kh&aacute; phổ biến trong c&aacute;c dự &aacute;n ch&iacute;nh phủ. Nhiều tổ chức ch&iacute;nh phủ, y tế, bảo hiểm, gi&aacute;o dục, quốc ph&ograve;ng v&agrave; một số bộ phận kh&aacute;c c&oacute; ứng dụng web được x&acirc;y dựng bằng Java.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Học lập trình Java cơ bản đến nâng cao\" src=\"https://techmaster.vn/media/fileman/Uploads/ImageBlog/hoc-lap-trinh-java-29082016-2.jpg\" title=\"Java được sử dụng ở đâu trong thế giới thực?\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Tham khảo kh&oacute;a học&nbsp;<a href=\"https://techmaster.vn/khoa-hoc/25556/thanh-thao-java-qua-tung-buoc\" target=\"_blank\">&quot;Java căn bản thực h&agrave;nh&quot;</a>&nbsp;v&agrave;&nbsp;<a href=\"https://techmaster.vn/khoa-hoc/25535/java-spring-co-ban\" target=\"_blank\">&quot;Lập tr&igrave;nh Java Spring&quot;</a>&nbsp;tại TechMaster</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">4. C&aacute;c c&ocirc;ng cụ phần mềm</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhiều c&ocirc;ng cụ ph&aacute;t triển v&agrave; phần mềm hữu &iacute;ch được viết bằng Java, v&iacute; dụ: Eclipse, InetelliJ Idea v&agrave; Netbans IDE. T&ocirc;i nghĩ rằng ch&uacute;ng cũng l&agrave; những ứng dụng desktop viết bằng Java được sử dụng nhiều nhất. Mặc d&ugrave; c&oacute; những thời điểm Swing đ&atilde; rất phổ biến, chủ yếu l&agrave; trong lĩnh vực ng&acirc;n h&agrave;ng v&agrave; dịch vụ đầu tư t&agrave;i ch&iacute;nh. Nhưng hiện nay, Java FX l&agrave; kh&aacute; phổ biến nhưng vẫn chưa thể thay thế được Swing.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">5. C&aacute;c ứng dụng thương mại</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">C&aacute;c ứng dụng thương mại của b&ecirc;n thứ ba (third party), chiếm một phần lớn trong ng&agrave;nh c&ocirc;ng nghiệp dịch vụ t&agrave;i ch&iacute;nh, cũng sử dụng Java. C&aacute;c ứng dụng thương mại nổi tiếng như Murex, được sử dụng tại nhiều ng&acirc;n h&agrave;ng để kết nối từ trong ra ngo&agrave;i, cũng được viết bằng Java.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">6. C&aacute;c ứng dụng J2ME</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Mặc d&ugrave; sự ra đời của&nbsp;<a href=\"https://techmaster.vn/khoa-hoc/8217/lap-trinh-ios-swift\" target=\"_blank\">iOS</a>&nbsp;v&agrave;&nbsp;<a href=\"https://techmaster.vn/khoa-hoc/25513/lap-trinhandroid\" target=\"_blank\">Android</a>&nbsp;gần như giết chết thị trường J2ME, nhưng vẫn c&ograve;n l&agrave; thị trường lớn đối với c&aacute;c d&ograve;ng điện thoại cấu h&igrave;nh thấp của Nokia v&agrave; Samsung sử dụng J2ME. C&oacute; thời điểm khi gần như tất cả c&aacute;c tr&ograve; game, ứng dụng tr&ecirc;n Android được viết bằng MIDP v&agrave; CLDC, một phần của nền tảng J2ME. J2ME vẫn c&ograve;n phổ biến tr&ecirc;n c&aacute;c sản phẩm như đĩa Blu-ray, Cards, Set top boxes v.v... Một trong những l&yacute; do m&agrave; WhatsApp trở n&ecirc;n phổ biến l&agrave; bởi v&igrave; n&oacute; cũng c&oacute; sẵn tr&ecirc;n J2ME cho tất cả những thiết bị cầm tay của Nokia m&agrave; hiện vẫn c&ograve;n kh&aacute; lớn.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">7. Kh&ocirc;ng gian nh&uacute;ng</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Java cũng được sử dụng nhiều trong lĩnh vực embedded (nh&uacute;ng). N&oacute; cho thấy khả năng của nền tảng n&agrave;y, bạn chỉ cần 130 KB để c&oacute; thể sử dụng c&ocirc;ng nghệ Java (tr&ecirc;n một smart card hoặc cảm biến). Ban đầu Java được thiết kế d&agrave;nh cho c&aacute;c thiết bị nh&uacute;ng. Trong thực tế, đ&acirc;y l&agrave; một phần trong chiến dịch ban đầu của Java &quot;viết một lần, chạy mọi nơi&quot; v&agrave; c&oacute; vẻ như n&oacute; đang mang lại nhiều lợi &iacute;ch to lớn.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">8. C&aacute;c c&ocirc;ng nghệ Big Data</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hadoop v&agrave; c&aacute;c c&ocirc;ng nghệ big data kh&aacute;c cũng đang sử dụng Java theo c&aacute;ch n&agrave;y hay c&aacute;ch kh&aacute;c, v&iacute; dụ: HBase, Accumulo (m&atilde; nguồn mở) v&agrave; ElasticSearch của Apache dựa tr&ecirc;n Java. Mặc d&ugrave; Java kh&ocirc;ng phải l&agrave; thống trị trong lĩnh vực n&agrave;y, v&igrave; c&oacute; những c&ocirc;ng nghệ như MongoDB được viết bằng C++. Nhưng Java c&oacute; tiềm năng chiếm được thị phần lớn hơn trong lĩnh vực n&agrave;y nếu Hadoop hoặc ElasticSearch trở n&ecirc;n lớn hơn.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">9. Trong lĩnh vực giao dịch chứng kho&aacute;n</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nền tảng Java đ&atilde; được cải thiện hiệu năng rất nhiều với c&aacute;c JIT hiện đại, n&oacute; c&oacute; khả năng đạt được hiệu suất ngang với mức C++. V&igrave; l&yacute; do n&agrave;y, Java cũng được sử dụng rất phổ biến để viết c&aacute;c hệ thống hiệu suất cao, bởi v&igrave; mặc d&ugrave; hiệu suất c&oacute; thua ch&uacute;t &iacute;t so với ng&ocirc;n ngữ native, nhưng bạn lại c&oacute; được sự an to&agrave;n, linh động v&agrave; bảo tr&igrave; với tốc độ nhanh hơn.</span></p>\r\n\r\n<h3><span style=\"font-family:Arial,Helvetica,sans-serif\">10. C&aacute;c ứng dụng khoa học</span></h3>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiện nay Java thường l&agrave; một lựa chọn mặc định cho c&aacute;c ứng dụng khoa học, bao gồm cả xử l&yacute; ng&ocirc;n ngữ tự nhi&ecirc;n. L&yacute; do ch&iacute;nh của việc n&agrave;y l&agrave; bởi v&igrave; Java an to&agrave;n hơn, portable, dễ bảo tr&igrave; v&agrave; đi k&egrave;m với c&aacute;c c&ocirc;ng cụ tốt hơn so với C ++ hoặc bất kỳ ng&ocirc;n ngữ n&agrave;o kh&aacute;c.</span></p>\r\n\r\n<p><br />\r\n<span style=\"font-family:Arial,Helvetica,sans-serif\">Trong những năm 1990 Java đ&oacute;ng vai tr&ograve; kh&aacute; lớn tr&ecirc;n Internet nhờ Applet, nhưng trong những năm qua, Applet mất dần sự phổ biến, chủ yếu l&agrave; do c&aacute;c vấn đề an ninh kh&aacute;c nhau tr&ecirc;n m&ocirc; h&igrave;nh sand box của Applet. Java l&agrave; ng&ocirc;n ngữ mặc định trong ng&agrave;nh ph&aacute;t triển phần mềm, v&agrave; được sử dụng nhiều trong ng&agrave;nh c&ocirc;ng nghiệp dịch vụ t&agrave;i ch&iacute;nh, ng&acirc;n h&agrave;ng v&agrave; thương mại điện tử. Java 8 đ&atilde; củng cố th&ecirc;m niềm tin rằng Java sẽ tiếp tục thống trị lĩnh vực ph&aacute;t triển phần mềm trong nhiều năm tới.</span></p>\r\n', 'hoc-lap-trinh-java-29082016-1.jpg', 1, 1, 1, 16, NULL, '2020-04-24 17:02:59', '2020-07-19 13:36:45'),
(70, 'Các tính năng của java', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Java c&oacute; rất nhiều t&iacute;nh năng nổi bật. Ch&uacute;ng được biết đến như những thuật ngữ quen thuộc. C&aacute;c t&iacute;nh năng n&agrave;y đơn giản v&agrave; dễ d&agrave;ng để t&igrave;m hiểu. Dưới đ&acirc;y l&agrave; danh s&aacute;ch c&aacute;c t&iacute;nh năng của java:</span></p>', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"các tính năng trong java\" src=\"https://viettuts.vn/images/java/cac-tinh-nang-cua-java.png\" /></span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Đơn giản (Simple)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Hướng đối tượng (Object Oriented)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Độc lập nền tảng (Platform Independent)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Bảo mật (Secured)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Robust (Mạnh mẽ)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Kiến tr&uacute;c - tập trung (Architecture-neutral)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Khả chuyển (Portable)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Năng động (Dynamic)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Th&ocirc;ng dịch (Interpreted)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiệu suất cao (High Performance)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Đa luồng (Multi-thread)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Ph&acirc;n t&aacute;n (Distributed)</span></li>\r\n</ol>\r\n\r\n<hr />\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Nội dung ch&iacute;nh</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://viettuts.vn/java/cac-tinh-nang-cua-java#goto-h2-1\">Đơn giản</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://viettuts.vn/java/cac-tinh-nang-cua-java#goto-h2-2\">Hướng đối tượng</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://viettuts.vn/java/cac-tinh-nang-cua-java#goto-h2-3\">Độc lập nền tảng</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://viettuts.vn/java/cac-tinh-nang-cua-java#goto-h2-4\">Bảo mật</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://viettuts.vn/java/cac-tinh-nang-cua-java#goto-h2-5\">Robust (Mạnh mẽ)</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://viettuts.vn/java/cac-tinh-nang-cua-java#goto-h2-6\">Kiến tr&uacute;c - tập trung</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://viettuts.vn/java/cac-tinh-nang-cua-java#goto-h2-7\">Portable</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://viettuts.vn/java/cac-tinh-nang-cua-java#goto-h2-8\">Hiệu suất cao</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://viettuts.vn/java/cac-tinh-nang-cua-java#goto-h2-9\">Đa luồng (Multi-thread)</a></span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://viettuts.vn/java/cac-tinh-nang-cua-java#goto-h2-10\">Ph&acirc;n t&aacute;n (Distributed)</a></span></li>\r\n</ul>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Đơn giản</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ng&ocirc;n ngữ Java c&oacute; đặc điểm đơn giản l&agrave; v&igrave;:</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">C&uacute; ph&aacute;p dựa tr&ecirc;n C++ (v&igrave; vậy việc học Java sẽ rất dễ d&agrave;ng sau khi lập tr&igrave;nh vi&ecirc;n học C++)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Gỡ bỏ nhiều đặc điểm g&acirc;y bối rối v&agrave; hiếm khi được sử dụng chẳng hạn như c&aacute;c con trỏ tường minh, nạp chồng to&aacute;n tử, ...</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn kh&ocirc;ng cần x&oacute;a c&aacute;c đối tượng m&agrave; kh&ocirc;ng được tham chiếu, bởi v&igrave; Bộ dọn r&aacute;c tự động (Garbage Collection) trong Java sẽ l&agrave;m việc đ&oacute; thay bạn.</span></li>\r\n</ol>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Hướng đối tượng</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hướng đối tượng nghĩa l&agrave; ch&uacute;ng ta tổ chức phần mềm dưới dạng kết hợp của nhiều loại đối tượng kh&aacute;c nhau, trong đ&oacute; c&oacute; sự kết hợp chặt chẽ cả về dữ liệu v&agrave; h&agrave;nh vi của ch&uacute;ng.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Lập tr&igrave;nh hướng đối tượng (OOP) l&agrave; một phương ph&aacute;p l&agrave;m đơn giản h&oacute;a việc ph&aacute;t triển v&agrave; bảo tr&igrave; phần mềm bằng việc cung cấp một số qui tắc.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Một số kh&aacute;i niệm cơ bản của hướng đối tượng (OOP) l&agrave;:</span></p>\r\n\r\n<ol>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Đối tượng (Object)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Lớp (Class)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">T&iacute;nh kế thừa</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">T&iacute;nh đa h&igrave;nh</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">T&iacute;nh trừu tượng</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">T&iacute;nh đ&oacute;ng g&oacute;i</span></li>\r\n</ol>\r\n\r\n<hr />\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Độc lập nền tảng</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Một Platform l&agrave; m&ocirc;i trường phần cứng hoặc phần mềm m&agrave; một hoặc nhiều chương tr&igrave;nh chạy trong đ&oacute;. C&oacute; hai loại Platform, một loại dựa tr&ecirc;n phần mềm (software-based) v&agrave; một loại dựa tr&ecirc;n phần cứng (hardware-based). Java cung cấp software-based platform. Java Platform kh&aacute;c với nhiều nền tảng kh&aacute;c ở chỗ n&oacute; chạy tr&ecirc;n c&aacute;c nền tảng hardware-based kh&aacute;c nhau. N&oacute; c&oacute; hai th&agrave;nh phần:</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ol>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">JRE (Java Runtime Environment)</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">API (Application Programming Interface)</span></li>\r\n</ol>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Java code c&oacute; thể chạy tr&ecirc;n nhiều nền tảng như Windows, Linux, Sun Solaris, Mac/OS, ... Java code được bi&ecirc;n dịch bởi Tr&igrave;nh bi&ecirc;n dịch (Compiler) v&agrave; được chuyển đổi th&agrave;nh Bytecode. Bytecode n&agrave;y l&agrave; một code độc lập nền tảng bởi v&igrave; n&oacute; c&oacute; thể chạy tr&ecirc;n nhiều nền tảng kh&aacute;c nhau. Đ&oacute; l&agrave; l&yacute; do v&igrave; sao java c&oacute; khẩu hiệu &quot;Viết một lần, Chạy khắp nơi (Write Once and Run Anywhere)&quot;.</span></p>\r\n\r\n<hr />\r\n<p>&nbsp;</p>\r\n\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Bảo mật</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Java bảo mật bởi v&igrave;:</span></p>\r\n\r\n<ul>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&ocirc;ng c&oacute; con trỏ tường minh.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\">Chương tr&igrave;nh chạy b&ecirc;n trong m&aacute;y ảo.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Classloader:</strong>&nbsp;Th&ecirc;m sự bảo mật bằng việc ph&acirc;n chia package cho c&aacute;c class của hệ thống file tr&ecirc;n local m&agrave; từ đ&oacute; ch&uacute;ng được import với c&aacute;c file từ nguồn mạng.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Bytecode Vertifier:</strong>&nbsp;Kiểm tra c&aacute;c đoạn code để t&igrave;m ra c&aacute;c phần code kh&ocirc;ng hợp lệ m&agrave; c&oacute; thể truy cập tr&aacute;i ph&eacute;p tới c&aacute;c đối tượng.</span></li>\r\n	<li><span style=\"font-family:Arial,Helvetica,sans-serif\"><strong>Security Manager:</strong>&nbsp;Quyết định xem nguồn resource n&agrave;o m&agrave; một lớp c&oacute; thể truy cập chẳng hạn như đọc v&agrave; ghi tới local disk.</span></li>\r\n</ul>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Những t&iacute;nh năng bảo mật n&agrave;y được cung cấp bởi Ng&ocirc;n ngữ Java. Ngo&agrave;i ra, một v&agrave;i t&iacute;nh năng bảo mật kh&aacute;c được cung cấp th&ocirc;ng qua nh&agrave; ph&aacute;t triển như SSL,JAAS,cryptography, ...</span></p>\r\n\r\n<hr />\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Robust (Mạnh mẽ)</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Bạn c&oacute; thể hiểu đơn giản Robust nghĩa l&agrave; mạnh mẽ. Java sử dụng tr&igrave;nh quản l&yacute; bộ nhớ mạnh mẽ. Đ&oacute; l&agrave;, Java sử dụng &iacute;t con trỏ hơn để tr&aacute;nh c&aacute;c vấn đề li&ecirc;n quan tới bảo mật. B&ecirc;n cạnh đ&oacute; Java c&ograve;n c&oacute; Tr&igrave;nh dọn r&aacute;c tự động (Garbage Collection). C&oacute; xử l&yacute; ngoại lệ (Exception Handling) v&agrave; cơ chế kiểm tra kiểu ngoại lệ xảy ra. Đ&oacute; l&agrave; những điểm nổi bật khiếm cho Java mạnh mẽ!</span></p>\r\n\r\n<hr />\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Kiến tr&uacute;c - tập trung</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Một ứng dụng được bi&ecirc;n dịch tr&ecirc;n kiến tr&uacute;c phần cứng n&agrave;y v&agrave; ứng dụng đ&oacute; chạy được tr&ecirc;n tất cả c&aacute;c kiến tr&uacute;c phần cứng kh&aacute;c. vd: Một ứng dụng được bi&ecirc;n dịch với vi xử l&yacute; 32bit v&agrave; n&oacute; sẽ chạy tốt tr&ecirc;n vi xử l&yacute; 64bit.</span></p>\r\n\r\n<hr />\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Portable</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Java l&agrave; ng&ocirc;n ngữ lập tr&igrave;nh c&oacute; t&iacute;nh Portable bởi v&igrave; java c&oacute; thể thực thi ứng dụng của n&oacute; tr&ecirc;n tất cả c&aacute;c hệ điều h&agrave;nh v&agrave; phần cứng kh&aacute;c nhau.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<hr />\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiệu suất cao</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiệu suất Java nhanh hơn kể từ khi được th&ocirc;ng dịch th&agrave;nh ByteCode, m&atilde; nguồn gốc th&igrave; chậm hơn so với một số ng&ocirc;n ngữ bi&ecirc;n dịch (v&iacute; dụ như C++).</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<hr />\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Đa luồng (Multi-thread)</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&uacute;ng ta c&oacute; thể tạo c&aacute;c ứng dụng ph&acirc;n t&aacute;n trong Java. RMI v&agrave; EJB được sử dụng để tạo c&aacute;c ứng dụng n&agrave;y. Ch&uacute;ng ta c&oacute; thể truy cập c&aacute;c file bằng việc gọi c&aacute;c phương thức từ bất cứ thiết bị n&agrave;o tr&ecirc;n internet.</span></p>\r\n\r\n<hr />\r\n<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Ph&acirc;n t&aacute;n (Distributed)</span></h2>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Một Thread l&agrave; giống như một chương tr&igrave;nh ri&ecirc;ng rẽ, thực thi một c&aacute;ch đồng thời. Ch&uacute;ng ta c&oacute; thể viết c&aacute;c chương tr&igrave;nh Java m&agrave; xử l&yacute; nhiều t&aacute;c vụ c&ugrave;ng một l&uacute;c bằng việc định nghĩa nhiều Thread. Lợi thế ch&iacute;nh của Multi-thread l&agrave; n&oacute; chia sẻ c&ugrave;ng bộ nhớ. C&aacute;c Thread l&agrave; quan trọng cho Multi-media, Web App, ...</span></p>', 'cac-tinh-nang-cua-java.png', 1, 1, 1, 6, NULL, '2020-04-24 17:05:55', '2020-07-19 13:36:50'),
(75, 'Giữa tin đồn iPhone 12 bỏ củ sạc, Apple thăm dò người dùng về củ sạc iPhone cũ', '<h2><span style=\"font-family:Arial,Helvetica,sans-serif\">Giữa những tin đồn về việc iPhone 12 mới sẽ kh&ocirc;ng đi k&egrave;m củ sạc, một số người d&ugrave;ng iPhone mới đ&acirc;y đ&atilde; được Apple thực hiện khảo s&aacute;t về th&oacute;i quen sử dụng củ sạc USB cũ.</span></h2>', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Apple mới đ&acirc;y đ&atilde; bắt đầu thực hiện việc khảo s&aacute;t người d&ugrave;ng iPhone về th&oacute;i quen m&agrave; họ sử dụng phụ kiện củ sạc đi k&egrave;m sẵn với m&aacute;y. Khảo s&aacute;t n&agrave;y được đưa ra ngay trong thời điểm c&aacute;c tin đồn về việc Apple c&oacute; thể sẽ loại bỏ củ sạc theo m&aacute;y tr&ecirc;n c&aacute;c mẫu iPhone 12 v&agrave; người d&ugrave;ng buộc phải mua ngo&agrave;i nếu muốn sử dụng. Kh&ocirc;ng chỉ củ sạc m&agrave; tai nghe EarPods cũng sẽ chịu chung số phận.</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Giữa tin đồn iPhone 12 bỏ củ sạc, Apple thăm dò người dùng về củ sạc iPhone cũ - Ảnh 1.\" id=\"img_203677045381902336\" src=\"https://genk.mediacdn.vn/thumb_w/660/139269124445442048/2020/7/4/photo-1-15938243910271955447257.jpg\" title=\"Giữa tin đồn iPhone 12 bỏ củ sạc, Apple thăm dò người dùng về củ sạc iPhone cũ - Ảnh 1.\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Theo khảo s&aacute;t, Apple hỏi người d&ugrave;ng xem họ đ&atilde; l&agrave;m g&igrave; với củ sạc iPhone cũ sau khi n&acirc;ng cấp l&ecirc;n một model mới hơn. Khảo s&aacute;t n&agrave;y được c&ocirc;ng ty hướng tới những người d&ugrave;ng đ&atilde; từng sử dụng iPhone v&agrave; vừa mới n&acirc;ng cấp l&ecirc;n iPhone mới trong thời gian gần đ&acirc;y.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Apple cũng giải th&iacute;ch r&otilde; l&agrave; khảo s&aacute;t lần n&agrave;y chỉ li&ecirc;n quan tới củ sạc USB k&egrave;m theo m&aacute;y chứ kh&ocirc;ng li&ecirc;n quan tới d&acirc;y sạc Lightning.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong b&agrave;i khảo s&aacute;t, Apple đưa ra một loạt t&ugrave;y chọn để người d&ugrave;ng c&oacute; thể lựa chọn xem họ đ&atilde; l&agrave;m g&igrave; với củ sạc USB cũ bao gồm:</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">- T&ocirc;i đ&atilde; b&aacute;n hoặc trao đổi n&oacute; c&ugrave;ng với chiếc iPhone cũ<br />\r\n- T&ocirc;i l&agrave;m mất n&oacute;<br />\r\n- T&ocirc;i cho một th&agrave;nh vi&ecirc;n kh&aacute;c trong gia đ&igrave;nh hoặc một người bạn<br />\r\n- T&ocirc;i vẫn đang d&ugrave;ng n&oacute; ở nh&agrave;<br />\r\n- T&ocirc;i vẫn đang d&ugrave;ng n&oacute; ở chỗ kh&aacute;c (chỗ l&agrave;m, trường học,...)<br />\r\n- T&ocirc;i vẫn giữ n&oacute;, nhưng kh&ocirc;ng d&ugrave;ng</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><img alt=\"Giữa tin đồn iPhone 12 bỏ củ sạc, Apple thăm dò người dùng về củ sạc iPhone cũ - Ảnh 2.\" id=\"img_203677424585183232\" src=\"https://genk.mediacdn.vn/thumb_w/660/139269124445442048/2020/7/4/-15938244819001563346185.jpg\" title=\"Giữa tin đồn iPhone 12 bỏ củ sạc, Apple thăm dò người dùng về củ sạc iPhone cũ - Ảnh 2.\" /></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y kh&ocirc;ng phải l&agrave; lần đầu ti&ecirc;n Apple thực hiện việc hỏi thăm &yacute; kiến người d&ugrave;ng về những g&igrave; m&agrave; họ cảm nhận đối với c&aacute;c sản phẩm của c&ocirc;ng ty. Trước đ&oacute; Apple cũng đ&atilde; từng thực hiện khảo s&aacute;t về việc trang bị jack cắm tai nghe tr&ecirc;n c&aacute;c thiết bị MacBook Pro, tuy nhi&ecirc;n tới b&acirc;y giờ th&igrave; Apple vẫn chưa khai tử kết nối n&agrave;y tr&ecirc;n d&ograve;ng laptop cao cấp của m&igrave;nh. Sẽ l&agrave; một c&acirc;u hỏi th&uacute; vị để xem xem liệu Apple c&oacute; loại bỏ củ sạc USB tr&ecirc;n iPhone 12 mới hay kh&ocirc;ng.</span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\">Dự kiến iPhone 12 mới sẽ c&oacute; tới 4 phi&ecirc;n bản v&agrave; ra mắt v&agrave;o m&ugrave;a thu năm nay.</span></p>', 'I5Hs_iphone12-bo-sac.jpg', 1, 49, 1, 11, 4, '2020-07-04 09:04:51', '2020-07-15 16:35:05');

